/**
 * Chart series management component
 * Extracted from LightweightCharts.tsx for better separation of concerns
 */

import React, { useEffect, useRef, useCallback } from 'react'
import { IChartApi, ISeriesApi, createSeriesMarkers } from 'lightweight-charts'
import { SeriesConfig, TradeConfig } from '../types'
import { ExtendedSeriesApi, TradeData } from '../types/ChartInterfaces'
import { createSeries } from '../utils/seriesFactory'
import { cleanLineStyleOptions } from '../utils/lineStyle'

interface ChartSeriesManagerProps {
  chart: IChartApi | null
  chartId: string
  seriesConfigs: SeriesConfig[]
  trades?: TradeConfig[]
  onSeriesCreated?: (series: ExtendedSeriesApi, config: SeriesConfig) => void
  onError?: (error: Error, context: string) => void
}

/**
 * Manages chart series creation, updates, and lifecycle
 */
export const ChartSeriesManager: React.FC<ChartSeriesManagerProps> = ({
  chart,
  chartId,
  seriesConfigs,
  trades = [],
  onSeriesCreated,
  onError
}) => {
  const seriesRefsRef = useRef<Map<string, ExtendedSeriesApi>>(new Map())
  const signalSeriesRefsRef = useRef<Map<string, ExtendedSeriesApi>>(new Map())

  /**
   * Create a single series with error handling
   */
  const createSeriesWithErrorHandling = useCallback((
    seriesConfig: SeriesConfig,
    index: number
  ): ExtendedSeriesApi | null => {
    if (!chart) return null

    try {
      // Clean line style options before creating series
      const cleanedConfig = {
        ...seriesConfig,
        ...(('line_style' in seriesConfig && seriesConfig.line_style) ? cleanLineStyleOptions(seriesConfig.line_style as Record<string, unknown>) : {})
      }

      const series = createSeries(chart, cleanedConfig, index)

      if (series) {
        const seriesKey = `series-${chartId}-${index}`
        seriesRefsRef.current.set(seriesKey, series)
        onSeriesCreated?.(series, seriesConfig)
        return series
      }
    } catch (error) {
      onError?.(error as Error, `createSeries-${index}`)
    }

    return null
  }, [chart, chartId, onSeriesCreated, onError])

  /**
   * Setup trade markers for a series
   */
  const setupTradeMarkers = useCallback((
    series: ExtendedSeriesApi,
    seriesConfig: SeriesConfig,
    index: number
  ) => {
    if (!trades || trades.length === 0) return

    try {
      const seriesData = seriesConfig.data || []
      const markers = trades
        .filter(trade => {
          // Filter trades relevant to this series
          return trade.series_id === seriesConfig.name ||
                 trade.series_index === index ||
                 !trade.series_id // Apply to all series if no specific ID
        })
        .map(trade => {
          const nearestTime = findNearestTimeInData(trade.entry_time, seriesData)
          return {
            time: nearestTime || trade.entry_time,
            position: trade.side === 'long' ? 'belowBar' : 'aboveBar',
            color: trade.side === 'long' ? '#4CAF50' : '#f44336',
            shape: trade.side === 'long' ? 'arrowUp' : 'arrowDown',
            text: `${trade.side.toUpperCase()} ${trade.quantity || ''}`
          }
        })

      if (markers.length > 0) {
        const seriesMarkers = createSeriesMarkers(markers)
        series.setMarkers(seriesMarkers)
      }
    } catch (error) {
      onError?.(error as Error, `tradeMarkers-${index}`)
    }
  }, [trades, onError])

  /**
   * Create all series from configurations
   */
  const createAllSeries = useCallback(() => {
    if (!chart || !seriesConfigs || seriesConfigs.length === 0) return

    // Clear existing series
    seriesRefsRef.current.clear()
    signalSeriesRefsRef.current.clear()

    // Create each series
    seriesConfigs.forEach((seriesConfig, index) => {
      const series = createSeriesWithErrorHandling(seriesConfig, index)

      if (series) {
        // Setup trade markers if available
        setupTradeMarkers(series, seriesConfig, index)

        // Handle special series types
        if (seriesConfig.type === 'signal') {
          const seriesKey = `signal-${chartId}-${index}`
          signalSeriesRefsRef.current.set(seriesKey, series)
        }
      }
    })
  }, [chart, seriesConfigs, chartId, createSeriesWithErrorHandling, setupTradeMarkers])

  /**
   * Update existing series data
   */
  const updateSeriesData = useCallback(() => {
    seriesConfigs.forEach((seriesConfig, index) => {
      const seriesKey = `series-${chartId}-${index}`
      const series = seriesRefsRef.current.get(seriesKey)

      if (series && seriesConfig.data) {
        try {
          series.setData(seriesConfig.data)
        } catch (error) {
          onError?.(error as Error, `updateSeriesData-${index}`)
        }
      }
    })
  }, [seriesConfigs, chartId, onError])

  /**
   * Find nearest time in series data
   */
  const findNearestTimeInData = useCallback((
    targetTime: number,
    data: TradeData[]
  ): number | null => {
    if (!data || data.length === 0) return null

    let nearestTime: number | null = null
    let minDiff = Infinity

    for (const item of data) {
      if (!item.time) continue

      let itemTime: number | null = null
      if (typeof item.time === 'number') {
        itemTime = item.time > 1000000000000 ? Math.floor(item.time / 1000) : item.time
      } else if (typeof item.time === 'string') {
        itemTime = Math.floor(new Date(item.time).getTime() / 1000)
      }

      if (itemTime !== null) {
        const diff = Math.abs(itemTime - targetTime)
        if (diff < minDiff) {
          minDiff = diff
          nearestTime = itemTime
        }
      }
    }

    return nearestTime
  }, [])

  /**
   * Cleanup series on unmount
   */
  const cleanup = useCallback(() => {
    try {
      // Remove all series from chart
      for (const series of seriesRefsRef.current.values()) {
        if (chart && series) {
          chart.removeSeries(series)
        }
      }

      seriesRefsRef.current.clear()
      signalSeriesRefsRef.current.clear()
    } catch (error) {
      onError?.(error as Error, 'seriesCleanup')
    }
  }, [chart, onError])

  // Create series when chart or configs change
  useEffect(() => {
    createAllSeries()
    return cleanup
  }, [createAllSeries, cleanup])

  // Update series data when configs change (without recreating series)
  useEffect(() => {
    updateSeriesData()
  }, [updateSeriesData])

  // Expose series refs for parent component access
  React.useImperativeHandle(React.forwardRef(() => null), () => ({
    getSeriesRefs: () => seriesRefsRef.current,
    getSignalSeriesRefs: () => signalSeriesRefsRef.current,
    updateSeries: updateSeriesData,
    recreateSeries: createAllSeries
  }))

  // This component doesn't render anything - it only manages series
  return null
}

/**
 * Chart synchronization manager component
 * Extracted from LightweightCharts.tsx for better separation of concerns
 */

import React, { useEffect, useRef, useCallback } from 'react'
import { IChartApi } from 'lightweight-charts'
import { SyncConfig } from '../types'

interface ChartSyncManagerProps {
  chart: IChartApi | null
  chartId: string
  syncConfig?: SyncConfig
  onSyncEvent?: (eventType: string, data: any) => void
}

/**
 * Manages chart synchronization across multiple chart instances
 */
export const ChartSyncManager: React.FC<ChartSyncManagerProps> = ({
  chart,
  chartId,
  syncConfig,
  onSyncEvent
}) => {
  const syncStateRef = useRef({
    isExternalSync: false,
    isExternalTimeRangeSync: false,
    lastSyncTimestamp: 0,
    lastTimeRangeSyncTimestamp: 0
  })

  /**
   * Setup crosshair synchronization
   */
  const setupCrosshairSync = useCallback(() => {
    if (!chart || !syncConfig?.crosshair) return

    const SYNC_DEBOUNCE_MS = 50

    // Add storage listener for incoming sync events
    if (!(chart as any)._storageListenerAdded) {
      const handleStorageSync = (event: StorageEvent) => {
        if (event.key === 'chart-crosshair-sync' && event.newValue) {
          try {
            const syncData = JSON.parse(event.newValue)
            if (syncData.chartId !== chartId) {
              const now = Date.now()
              if (now - syncStateRef.current.lastSyncTimestamp < SYNC_DEBOUNCE_MS) {
                return
              }
              syncStateRef.current.lastSyncTimestamp = now

              if (syncData.param) {
                ;(chart as any)._isExternalSync = true
                chart.timeScale().scrollToPosition(syncData.param.point?.x || 0, false)
                setTimeout(() => {
                  ;(chart as any)._isExternalSync = false
                }, 50)
              }
            }
          } catch (error) {
            // Ignore invalid sync data
          }
        }
      }

      window.addEventListener('storage', handleStorageSync)
      ;(chart as any)._storageListenerAdded = true
    }

    // Subscribe to crosshair move events
    const handleCrosshairMove = (param: any) => {
      if ((chart as any)._isExternalSync) {
        return
      }

      // Broadcast sync event
      const syncData = {
        chartId,
        param,
        timestamp: Date.now()
      }

      try {
        localStorage.setItem('chart-crosshair-sync', JSON.stringify(syncData))
        onSyncEvent?.('crosshairMove', syncData)
      } catch (error) {
        // Ignore localStorage errors
      }
    }

    chart.subscribeCrosshairMove(handleCrosshairMove)

    return () => {
      chart.unsubscribeCrosshairMove(handleCrosshairMove)
    }
  }, [chart, chartId, syncConfig?.crosshair, onSyncEvent])

  /**
   * Setup time range synchronization
   */
  const setupTimeRangeSync = useCallback(() => {
    if (!chart || !syncConfig?.timeRange) return

    const TIME_RANGE_SYNC_DEBOUNCE_MS = 100

    // Add storage listener for time range sync
    if (!(chart as any)._timeRangeStorageListenerAdded) {
      const handleTimeRangeSync = (event: StorageEvent) => {
        if (event.key === 'chart-timerange-sync' && event.newValue) {
          try {
            const syncData = JSON.parse(event.newValue)
            if (syncData.chartId !== chartId) {
              const now = Date.now()
              if (now - syncStateRef.current.lastTimeRangeSyncTimestamp < TIME_RANGE_SYNC_DEBOUNCE_MS) {
                return
              }
              syncStateRef.current.lastTimeRangeSyncTimestamp = now

              if (syncData.timeRange) {
                ;(chart as any)._isExternalTimeRangeSync = true
                chart.timeScale().setVisibleRange(syncData.timeRange)
                setTimeout(() => {
                  ;(chart as any)._isExternalTimeRangeSync = false
                }, 100)
              }
            }
          } catch (error) {
            // Ignore invalid sync data
          }
        }
      }

      window.addEventListener('storage', handleTimeRangeSync)
      ;(chart as any)._timeRangeStorageListenerAdded = true
    }

    // Subscribe to visible time range change
    const handleTimeRangeChange = () => {
      if ((chart as any)._isExternalTimeRangeSync) {
        return
      }

      try {
        const visibleRange = chart.timeScale().getVisibleRange()
        const syncData = {
          chartId,
          timeRange: visibleRange,
          timestamp: Date.now()
        }

        localStorage.setItem('chart-timerange-sync', JSON.stringify(syncData))
        onSyncEvent?.('timeRangeChange', syncData)
      } catch (error) {
        // Ignore localStorage errors
      }
    }

    chart.timeScale().subscribeVisibleTimeRangeChange(handleTimeRangeChange)

    return () => {
      chart.timeScale().unsubscribeVisibleTimeRangeChange(handleTimeRangeChange)
    }
  }, [chart, chartId, syncConfig?.timeRange, onSyncEvent])

  /**
   * Setup click synchronization
   */
  const setupClickSync = useCallback(() => {
    if (!chart || !syncConfig?.click) return

    const handleClick = () => {
      const syncData = {
        chartId,
        event: 'click',
        timestamp: Date.now()
      }

      try {
        localStorage.setItem('chart-click-sync', JSON.stringify(syncData))
        onSyncEvent?.('click', syncData)
      } catch (error) {
        // Ignore localStorage errors
      }
    }

    chart.subscribeClick(handleClick)

    return () => {
      chart.unsubscribeClick(handleClick)
    }
  }, [chart, chartId, syncConfig?.click, onSyncEvent])

  // Setup all synchronization types
  useEffect(() => {
    if (!chart || !syncConfig) return

    const cleanupFunctions: (() => void)[] = []

    if (syncConfig.crosshair) {
      const cleanup = setupCrosshairSync()
      if (cleanup) cleanupFunctions.push(cleanup)
    }

    if (syncConfig.timeRange) {
      const cleanup = setupTimeRangeSync()
      if (cleanup) cleanupFunctions.push(cleanup)
    }

    if (syncConfig.click) {
      const cleanup = setupClickSync()
      if (cleanup) cleanupFunctions.push(cleanup)
    }

    return () => {
      cleanupFunctions.forEach(cleanup => cleanup())
    }
  }, [chart, syncConfig, setupCrosshairSync, setupTimeRangeSync, setupClickSync])

  // This component doesn't render anything - it only manages synchronization
  return null
}

import React, {Component, ErrorInfo, ReactNode} from 'react'

interface Props {
  children: ReactNode
  fallback?: ReactNode
  onError?: (error: Error, errorInfo: ErrorInfo) => void
}

interface State {
  hasError: boolean
  error?: Error
}

export class ErrorBoundary extends Component<Props, State> {
  constructor(props: Props) {
    super(props)
    this.state = {hasError: false}
  }

  static getDerivedStateFromError(error: Error): State {
    return {hasError: true, error}
  }

  componentDidCatch(error: Error, errorInfo: ErrorInfo) {


    if (this.props.onError) {
      this.props.onError(error, errorInfo)
    }
  }

  render() {
    if (this.state.hasError) {
      if (this.props.fallback) {
        return this.props.fallback
      }

      return (
        <div
          style={{
            padding: '20px',
            border: '1px solid #ff6b6b',
            borderRadius: '8px',
            backgroundColor: '#fff5f5',
            color: '#d63031',
            textAlign: 'center',
            margin: '10px 0'
          }}
        >
          <h3>Chart Error</h3>
          <p>Something went wrong while rendering the chart.</p>
          <button
            onClick={() => this.setState({hasError: false, error: undefined})}
            style={{
              padding: '8px 16px',
              backgroundColor: '#ff6b6b',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer',
              marginTop: '10px'
            }}
          >
            Try Again
          </button>
        </div>
      )
    }

    return this.props.children
  }
}

// Hook for functional components
export function useErrorHandler() {
  const [error, setError] = React.useState<Error | null>(null)

  const handleError = React.useCallback((error: Error) => {

    setError(error)
  }, [])

  const clearError = React.useCallback(() => {
    setError(null)
  }, [])

  return {error, handleError, clearError}
}

