        throw new TypeError('Type error occurred')
      }

      render(
        <ErrorBoundary>
          <TypeErrorComponent />
        </ErrorBoundary>
      )

      expect(screen.getByText(/Something went wrong/i)).toBeInTheDocument()
    })

    it('should handle reference errors', () => {
