/**
 * Signal Series Plugin for Lightweight Charts
 *
 * This plugin renders background bands based on signal data, creating
 * vertical colored bands that span the entire chart height for specific
 * time periods.
 */

import {
  IChartApi,
  ISeriesApi,
  ISeriesPrimitive,
  SeriesAttachedParameter,
  IPrimitivePaneView,
  IPrimitivePaneRenderer,
  Coordinate,
  Time,
  UTCTimestamp,
  LineSeries,
  PrimitivePaneViewZOrder
} from 'lightweight-charts'

export interface SignalData {
  time: string | number
  value: number
  color?: string
}

export interface SignalOptions {
  neutralColor?: string
  signalColor?: string
  alertColor?: string
  visible: boolean
}

export interface SignalSeriesConfig {
  type: 'signal'
  data: SignalData[]
  options: SignalOptions
  paneId?: number
}

// Signal renderer data interface
interface SignalRendererData {
  x: Coordinate | number
  y1: Coordinate | number
  y2: Coordinate | number
  color: string
}

// Signal view data interface
interface SignalViewData {
  data: SignalRendererData[]
  options: SignalOptions
}

/**
 * Check if a color is transparent or effectively invisible
 */
function isTransparent(color: string): boolean {
  if (!color) return true

  // Check for fully transparent colors
  if (color === 'transparent') return true

  // Check for rgba with alpha = 0
  if (color.startsWith('rgba(')) {
    const match = color.match(/rgba\([^)]+,\s*([^)]+)\)/)
    if (match && parseFloat(match[1]) === 0) return true
  }

  // Check for hex with alpha = 00 (8-digit hex)
  if (color.startsWith('#') && color.length === 9) {
    const alpha = color.substring(7, 9)
    if (alpha === '00') return true
  }

  // Check for hex with alpha = 00 (4-digit hex)
  if (color.startsWith('#') && color.length === 5) {
    const alpha = color.substring(4, 5)
    if (alpha === '0') return true
  }

  return false
}

// Signal primitive pane renderer
class SignalPrimitivePaneRenderer implements IPrimitivePaneRenderer {
  _viewData: SignalViewData

  constructor(data: SignalViewData) {
    this._viewData = data
  }

  draw(target: any) {
    this.drawBackground(target)
  }

  drawBackground(target: any) {
    const points: SignalRendererData[] = this._viewData.data

    if (points.length === 0) {
      return
    }

    target.useBitmapCoordinateSpace((scope: any) => {
      const ctx = scope.context
      ctx.scale(scope.horizontalPixelRatio, scope.verticalPixelRatio)

      // Draw background bands - following TradingView's approach
      for (let i = 0; i < points.length; i += 2) {
        if (i + 1 < points.length) {
          const startPoint = points[i]
          const endPoint = points[i + 1]

          // Skip rendering if color is transparent or effectively invisible
          if (isTransparent(startPoint.color)) {
            continue
          }

          // Use the color exactly as provided by the backend
          const fillStyle = startPoint.color

          ctx.fillStyle = fillStyle

          // FIX: Ensure bands don't overlap by using exact coordinates
          // Draw the background rectangle with precise boundaries to prevent color blending
          const width = Math.max(1, endPoint.x - startPoint.x) // Use exact coordinate difference
          const height = startPoint.y2 - startPoint.y1

          ctx.fillRect(startPoint.x, startPoint.y1, width, height)
        }
      }
    })
  }
}

// Signal primitive pane view
class SignalPrimitivePaneView implements IPrimitivePaneView {
  _source: SignalSeries
  _data: SignalViewData

  constructor(source: SignalSeries) {
    this._source = source
    this._data = {
      data: [],
      options: this._source.getOptions()
    }
  }

  update() {
    const timeScale = this._source.getChart().timeScale()
    const priceScale = this._source.getChart().priceScale('left')

    if (!timeScale || !priceScale) {
      return
    }

    const bands = this._source.getBackgroundBands()

    const renderData: SignalRendererData[] = []

    // Get bar spacing to properly align with bars
    const barSpacing = timeScale.options().barSpacing || 6
    const halfBarSpacing = barSpacing / 2

    bands.forEach((band, index) => {
      const startX = timeScale.timeToCoordinate(band.startTime)
      const endX = timeScale.timeToCoordinate(band.endTime)

      // Handle cases where coordinates are null (outside visible range)
      if (startX !== null && endX !== null) {
        // Both coordinates are valid - adjust for bar alignment
        const chartHeight = this._source.getChart().chartElement()?.clientHeight || 400

        // FIX: Use proper full bar width calculation following TradingView guidelines
        // Start: x - halfBarSpacing (bar start boundary)
        // End: x + halfBarSpacing (bar end boundary)
        // This ensures each band fills its complete space without gaps
        const adjustedStartX = Math.floor(startX - halfBarSpacing)
        const adjustedEndX = Math.floor(endX + halfBarSpacing)

        renderData.push({
          x: adjustedStartX,
          y1: 0,
          y2: chartHeight,
          color: band.color
        })

        renderData.push({
          x: adjustedEndX,
          y1: 0,
          y2: chartHeight,
          color: band.color
        })
      } else if (startX !== null && endX === null) {
        // Start is visible but end is outside - extend to chart edge
        const chartHeight = this._source.getChart().chartElement()?.clientHeight || 400
        const chartWidth = this._source.getChart().chartElement()?.clientWidth || 800

        // Use proper full bar width calculation for start boundary
        const adjustedStartX = Math.floor(startX - halfBarSpacing)

        renderData.push({
          x: adjustedStartX,
          y1: 0,
          y2: chartHeight,
          color: band.color
        })

        renderData.push({
          x: chartWidth,
          y1: 0,
          y2: chartHeight,
          color: band.color
        })
      } else if (startX === null && endX !== null) {
        // End is visible but start is outside - extend from chart edge
        const chartHeight = this._source.getChart().chartElement()?.clientHeight || 400

        // Use proper full bar width calculation for end boundary
        const adjustedEndX = Math.floor(endX + halfBarSpacing)

        renderData.push({
          x: 0,
          y1: 0,
          y2: chartHeight,
          color: band.color
        })

        renderData.push({
          x: adjustedEndX,
          y1: 0,
          y2: chartHeight,
          color: band.color
        })
      } else {
        // Both coordinates are null - band is completely outside visible range
      }
    })

    this._data.data = renderData
  }

  renderer() {
    return new SignalPrimitivePaneRenderer(this._data)
  }

  // Z-order support: Signal series renders at normal layer for visibility
  zOrder(): PrimitivePaneViewZOrder {
    return 'bottom' as PrimitivePaneViewZOrder
  }
}

// Background band interface
interface BackgroundBand {
  startTime: UTCTimestamp
  endTime: UTCTimestamp
  value: number | boolean
  color: string
}

// Signal series class
export class SignalSeries implements ISeriesPrimitive<Time> {
  private chart: IChartApi
  private dummySeries: ISeriesApi<'Line'>
  private options: SignalOptions
  private signalData: SignalData[] = []
  private backgroundBands: BackgroundBand[] = []
  private _paneViews: SignalPrimitivePaneView[]
  private paneId: number

  constructor(chart: IChartApi, config: SignalSeriesConfig) {
    this.chart = chart
    this.options = config.options || {visible: true}
    this.signalData = config.data || []
    this.paneId = config.paneId || 0
    this._paneViews = [new SignalPrimitivePaneView(this)]

    // Create a dummy line series to attach the primitive to
    this.dummySeries = chart.addSeries(
      LineSeries,
      {
        color: 'transparent',
        lineWidth: 0 as any,
        visible: false, // Hide the dummy series completely
        priceScaleId: 'right',
        lastValueVisible: false, // Hide last value
        priceLineVisible: false, // Hide price line
        crosshairMarkerVisible: false, // Disable crosshair for dummy series
        lineVisible: false // Hide the line itself
      },
      this.paneId
    )

    // Add some dummy data to ensure the time scale is properly initialized
    if (this.signalData.length > 0) {
      const dummyData = this.signalData.map(signal => ({
        time: this.parseTime(signal.time),
        value: 0
      }))
      this.dummySeries.setData(dummyData)
    }

    // Process signal data to create background bands
    this.processSignalData()

    // Attach the primitive to the dummy series for rendering
    this.dummySeries.attachPrimitive(this)
  }

  /**
   * Public method for setting signal data
   */
  public setSignals(signals: SignalData[]): void {
    this.signalData = signals
    this.processSignalData()
  }

  /**
   * Public method for adding to chart (for testing compatibility)
   */
  public addToChart(chart: IChartApi): void {
    // Already initialized in constructor, this is just for test compatibility
  }

  /**
   * Process signal data to create background bands
   */
  private processSignalData(): void {
    this.backgroundBands = []

    if (this.signalData.length === 0) {
      return
    }

    // Sort signals by time to ensure proper ordering
    const sortedSignals = [...this.signalData].sort((a, b) => {
      const timeA = this.parseTime(a.time)
      const timeB = this.parseTime(b.time)
      return timeA - timeB
    })

    // Process each signal separately - create one band per signal
    for (let i = 0; i < sortedSignals.length; i++) {
      const signal = sortedSignals[i]
      const signalTime = this.parseTime(signal.time)

      // Create a band for this signal
      // Each signal gets its own band covering its individual time period
      const band = {
        value: signal.value,
        startTime: signalTime,
        endTime: signalTime, // Each band covers just its own signal time
        individualColor: signal.color || this.getColorForValue(signal.value) || undefined
      }

      this.addBackgroundBand(band)
    }
  }

  /**
   * Add a background band
   */
  private addBackgroundBand(band: {
    value: number | boolean
    startTime: UTCTimestamp
    endTime: UTCTimestamp
    individualColor?: string
  }): void {
    // Use individual color if available, otherwise fall back to series-level colors
    let color = band.individualColor
    if (!color) {
      const seriesColor = this.getColorForValue(band.value)
      if (seriesColor) {
        color = seriesColor
      }
    }

    // Skip adding bands with no color or transparent colors
    if (!color || isTransparent(color)) {
      return
    }

    const backgroundBand = {
      startTime: band.startTime,
      endTime: band.endTime,
      value: typeof band.value === 'boolean' ? (band.value ? 1 : 0) : band.value,
      color: color
    }

    this.backgroundBands.push(backgroundBand)
  }

  /**
   * Get color for a signal value
   */
  private getColorForValue(value: number | boolean): string | null {
    // Handle boolean values
    if (typeof value === 'boolean') {
      if (value === true) {
        return this.options.signalColor || null
      } else {
        // For false values, always return neutral color if available
        return this.options.neutralColor || null
      }
    }

    // Handle numeric values with range-based comparisons
    if (value === 0) {
      // For value 0, always return neutral color if available
      return this.options.neutralColor || null
    } else if (value > 0) {
      // For positive values, use signal color
      return this.options.signalColor || null
    } else if (value < 0) {
      // For negative values, try alertColor first, then fall back to signalColor if alertColor is not set
      return this.options.alertColor || this.options.signalColor || null
    } else {
      return null
    }
  }

  /**
   * Parse time value to timestamp
   * Handles both string dates and numeric timestamps
   */
  private parseTime(time: string | number): UTCTimestamp {
    try {
      // If it's already a number (Unix timestamp), convert to seconds if needed
      if (typeof time === 'number') {
        // If timestamp is in milliseconds, convert to seconds
        if (time > 1000000000000) {
          return Math.floor(time / 1000) as UTCTimestamp
        }
        return Math.floor(time) as UTCTimestamp
      }

      // If it's a string, try to parse as date
      if (typeof time === 'string') {
        // First try to parse as Unix timestamp string
        const timestamp = parseInt(time, 10)
        if (!isNaN(timestamp)) {
          // It's a numeric string (Unix timestamp)
          if (timestamp > 1000000000000) {
            return Math.floor(timestamp / 1000) as UTCTimestamp
          }
          return Math.floor(timestamp) as UTCTimestamp
        }

        // Try to parse as date string
        const date = new Date(time)
        if (isNaN(date.getTime())) {
          return 0 as UTCTimestamp
        }
        return Math.floor(date.getTime() / 1000) as UTCTimestamp
      }

      return 0 as UTCTimestamp
    } catch (error) {
      return 0 as UTCTimestamp
    }
  }

  // Getter methods
  getOptions(): SignalOptions {
    return this.options
  }

  getChart(): IChartApi {
    return this.chart
  }

  getBackgroundBands(): BackgroundBand[] {
    return this.backgroundBands
  }

  // ISeriesPrimitive implementation
  attached(param: SeriesAttachedParameter<Time>): void {
    // Primitive is attached to the series
  }

  detached(): void {
    // Primitive is detached from the series
  }

  updateAllViews(): void {
    this._paneViews.forEach(pv => pv.update())
  }

  paneViews(): IPrimitivePaneView[] {
    return this._paneViews
  }

  /**
   * Update signal data and re-render
   */
  updateData(newData: SignalData[]): void {
    this.signalData = newData
    this.processSignalData()
    this.updateAllViews()
  }

  /**
   * Update options and re-render
   */
  updateOptions(newOptions: SignalOptions): void {
    this.options = newOptions
    this.processSignalData()
    this.updateAllViews()
  }

  /**
   * Set data (for compatibility with ISeriesApi interface)
   */
  setData(data: SignalData[]): void {
    this.updateData(data)
  }

  /**
   * Update single data point (for compatibility with ISeriesApi interface)
   */
  update(data: SignalData): void {
    // For signal series, we need to update the entire dataset
    const newData = [...this.signalData]
    const existingIndex = newData.findIndex(item => item.time === data.time)

    if (existingIndex >= 0) {
      newData[existingIndex] = data
    } else {
      newData.push(data)
    }

    this.updateData(newData)
  }

  /**
   * Apply options (for compatibility with ISeriesApi interface)
   */
  applyOptions(options: Partial<SignalOptions>): void {
    this.updateOptions({...this.options, ...options})
  }

  /**
   * Get price scale (for compatibility with ISeriesApi interface)
   */
  priceScale(): any {
    return this.chart.priceScale('left')
  }

  /**
   * Remove the series (for compatibility with ISeriesApi interface)
   */
  remove(): void {
    this.destroy()
  }

  /**
   * Destroy the plugin and clean up resources
   */
  destroy(): void {
    try {
      this.chart.removeSeries(this.dummySeries)
    } catch (error) {
      // Series already removed
    }
  }
}

/**
 * Factory function to create signal series plugin
 */
export function createSignalSeriesPlugin(
  chart: IChartApi,
  config: SignalSeriesConfig
): SignalSeries {
  return new SignalSeries(chart, config)
}

/**
 * Trend Fill Series Plugin for Lightweight Charts
 *
 * This plugin renders trend lines with fill areas between trend line and base line,
 * creating a visual representation of trend direction and strength.
 *
 * Features:
 * - Dynamic baseline filling (similar to BaseLineSeries but with variable baseline)
 * - Band filling between trend line and base line
 * - Dynamic color changes based on trend direction
 * - Base line support for reference
 * - Optimized rendering using BaseLineSeries patterns
 */

import {
  IChartApi,
  ISeriesApi,
  ISeriesPrimitive,
  SeriesAttachedParameter,
  IPrimitivePaneView,
  IPrimitivePaneRenderer,
  Time,
  UTCTimestamp,
  LineSeries
} from 'lightweight-charts'

// Data structure for trend fill series (matching Python snake_case fields)
export interface TrendFillData {
  time: number | string
  // Support both snake_case (Python) and camelCase (JavaScript) field names
  base_line?: number | null
  trend_line?: number | null
  trend_direction?: number | null
  baseLine?: number | null  // camelCase fallback
  trendLine?: number | null  // camelCase fallback
  trendDirection?: number | null  // camelCase fallback
}

// Options for trend fill series
export interface TrendFillOptions {
  zIndex?: number
  uptrendFillColor: string
  downtrendFillColor: string
  trendLine: {
    color: string
    lineWidth: 1 | 2 | 3 | 4
    lineStyle: 0 | 1 | 2
    visible: boolean
  }
  baseLine: {
    color: string
    lineWidth: 1 | 2 | 3 | 4
    lineStyle: 0 | 1 | 2
    visible: boolean
  }
  visible: boolean
  priceScaleId?: string // Added for price scale ID
}

// Internal data structures for rendering (following BaseLineSeries pattern)
interface TrendFillItem {
  time: UTCTimestamp
  baseLine: number
  trendLine: number
  trendDirection: number
  fillColor: string
  lineColor: string
  lineWidth: number
  lineStyle: number
}

// Pre-converted coordinates for rendering (like BaseLineSeries)
interface TrendFillRenderData {
  x: number | null
  baseLineY: number | null
  trendLineY: number | null
  fillColor: string
  lineColor: string
  lineWidth: number
  lineStyle: number
  trendDirection: number
}

// Renderer data interface (following BaseLineSeries pattern)
interface TrendFillRendererData {
  items: TrendFillRenderData[]
  timeScale: any
  priceScale: any
  chartWidth: number
  // BaseLineSeries-style data
  lineWidth: number
  lineStyle: number
  visibleRange: {from: number; to: number} | null
  barWidth: number
}

// View data interface
interface TrendFillViewData {
  data: TrendFillRendererData
  options: TrendFillOptions
}

/**
 * Parse time value to timestamp
 * Handles both string dates and numeric timestamps
 */
function parseTime(time: string | number): UTCTimestamp {
  try {
    // If it's already a number (Unix timestamp), convert to seconds if needed
    if (typeof time === 'number') {
      // If timestamp is in milliseconds, convert to seconds
      if (time > 1000000000000) {
        return Math.floor(time / 1000) as UTCTimestamp
      }
      return Math.floor(time) as UTCTimestamp
    }

    // If it's a string, try to parse as date
    if (typeof time === 'string') {
      // First try to parse as Unix timestamp string
      const timestamp = parseInt(time, 10)
      if (!isNaN(timestamp)) {
        // It's a numeric string (Unix timestamp)
        if (timestamp > 1000000000000) {
          return Math.floor(timestamp / 1000) as UTCTimestamp
        }
        return Math.floor(timestamp) as UTCTimestamp
      }

      // Try to parse as date string
      const date = new Date(time)
      if (isNaN(date.getTime())) {
        return 0 as UTCTimestamp
      }
      return Math.floor(date.getTime() / 1000) as UTCTimestamp
    }

    return 0 as UTCTimestamp
  } catch (error) {
    return 0 as UTCTimestamp
  }
}

// Optimized Trend Fill Pane Renderer (following BaseLineSeries pattern)
class TrendFillPrimitivePaneRenderer implements IPrimitivePaneRenderer {
  _viewData: TrendFillViewData

  constructor(data: TrendFillViewData) {
    this._viewData = data
  }

  draw() {}

  drawBackground(target: any) {
    // Batch all rendering operations (following band series pattern)
    target.useBitmapCoordinateSpace((scope: any) => {
      const ctx = scope.context
      ctx.scale(scope.horizontalPixelRatio, scope.verticalPixelRatio)

      // Save context state once
      ctx.save()

      // Draw all elements efficiently - fills in background, lines in foreground
      this._drawTrendFills(ctx, scope)
      this._drawTrendLines(ctx, scope) // Re-enabled with proper color handling

      // Restore context state once
      ctx.restore()
    })
  }

  private _drawTrendFills(ctx: CanvasRenderingContext2D, scope: any): void {
    const {items, visibleRange} = this._viewData.data

    if (items.length === 0 || visibleRange === null) {
      return
    }

    // Group consecutive items by trend direction and fill color to draw continuous fills
    this._drawContinuousFills(ctx, items, visibleRange, scope.horizontalPixelRatio, scope.verticalPixelRatio)
  }

  private _drawTrendLines(ctx: CanvasRenderingContext2D, scope: any): void {
    const {items, visibleRange, lineWidth, lineStyle} = this._viewData.data

    if (items.length === 0 || visibleRange === null) return

    // Set line style once (like BaseLineSeries)
    ctx.lineCap = 'butt'
    ctx.lineJoin = 'round'
    ctx.lineWidth = lineWidth
    this._setLineStyle(ctx, lineStyle)

    // Group consecutive items by trend direction to draw continuous lines
    this._drawContinuousTrendLines(ctx, items, visibleRange, scope.horizontalPixelRatio, scope.verticalPixelRatio)
  }

  private _drawContinuousFills(
    ctx: CanvasRenderingContext2D,
    items: TrendFillRenderData[],
    visibleRange: {from: number; to: number},
    hRatio: number,
    vRatio: number
  ): void {
    if (visibleRange.to <= visibleRange.from) {
      return
    }

    // Draw individual fills between consecutive points only
    for (let i = visibleRange.from; i < visibleRange.to - 1; i++) {
      const currentItem = items[i]
      const nextItem = items[i + 1]

      // Skip if either point has invalid coordinates
      if (!this._isValidCoordinates(currentItem) || !this._isValidCoordinates(nextItem)) {
        continue
      }

      // Only draw fill if both points have the same trend direction and fill color
      if (currentItem.trendDirection === nextItem.trendDirection &&
          currentItem.fillColor === nextItem.fillColor &&
          currentItem.trendDirection !== 0) {

        // Check if points are reasonably close (no huge gaps)
        const xDistance = Math.abs(nextItem.x! - currentItem.x!)
        const maxGap = 100 // Maximum pixel gap to consider "consecutive"

        if (xDistance <= maxGap) {
          this._drawFillBetweenTwoPoints(ctx, currentItem, nextItem, hRatio, vRatio)
        }
      }
    }
  }

  private _drawFillBetweenTwoPoints(
    ctx: CanvasRenderingContext2D,
    point1: TrendFillRenderData,
    point2: TrendFillRenderData,
    hRatio: number,
    vRatio: number
  ): void {
    ctx.fillStyle = point1.fillColor

    // Create a trapezoid fill between the two points
    ctx.beginPath()

    // Start from point1's baseline
    ctx.moveTo(point1.x!, point1.baseLineY!)

    // Go to point2's baseline
    ctx.lineTo(point2.x!, point2.baseLineY!)

    // Go to point2's trend line
    ctx.lineTo(point2.x!, point2.trendLineY!)

    // Go to point1's trend line
    ctx.lineTo(point1.x!, point1.trendLineY!)

    // Close back to point1's baseline
    ctx.closePath()
    ctx.fill()
  }

  private _drawFillGroup(
    ctx: CanvasRenderingContext2D,
    group: TrendFillRenderData[],
    hRatio: number,
    vRatio: number,
    fillColor: string
  ): void {
    if (group.length === 0) {
      return
    }

    ctx.fillStyle = fillColor

    // NOW THAT WE KNOW COORDINATES WORK: Create smooth continuous fills
    if (group.length === 1) {
      // Single point - draw a small rectangle fill
      const item = group[0]
      const x = item.x!
      const baseLineY = item.baseLineY!
      const trendLineY = item.trendLineY!

      const rectX = Math.max(0, x - 1)
      const rectY = Math.min(baseLineY, trendLineY)
      const rectWidth = 2
      const rectHeight = Math.abs(baseLineY - trendLineY)

      ctx.fillRect(rectX, rectY, rectWidth, rectHeight)
    } else {
      // Multiple points - draw continuous fill area using path
      ctx.beginPath()

      // Start from the first point's baseline
      const firstItem = group[0]
      ctx.moveTo(firstItem.x!, firstItem.baseLineY!)

      // Draw along the baseline to all points
      for (let i = 1; i < group.length; i++) {
        const item = group[i]
        ctx.lineTo(item.x!, item.baseLineY!)
      }

      // Connect to the last point's trend line
      const lastItem = group[group.length - 1]
      ctx.lineTo(lastItem.x!, lastItem.trendLineY!)

      // Draw along the trend line back to start (reverse order)
      for (let i = group.length - 2; i >= 0; i--) {
        const item = group[i]
        ctx.lineTo(item.x!, item.trendLineY!)
      }

      // Connect back to the first point's trend line and then to baseline
      ctx.lineTo(firstItem.x!, firstItem.trendLineY!)
      ctx.lineTo(firstItem.x!, firstItem.baseLineY!)

      ctx.fill()
    }

    // Smooth fills drawn
  }

  private _drawContinuousTrendLines(
    ctx: CanvasRenderingContext2D,
    items: TrendFillRenderData[],
    visibleRange: {from: number; to: number},
    hRatio: number,
    vRatio: number
  ): void {
    if (visibleRange.to <= visibleRange.from) return

    // Group consecutive points by trend direction and color
    let currentGroup: TrendFillRenderData[] = []
    let currentTrendDirection: number | null = null
    let currentColor: string | null = null

    for (let i = visibleRange.from; i < visibleRange.to; i++) {
      const item = items[i]
      if (!this._isValidCoordinates(item)) continue

      const itemColor = item.lineColor
      const itemTrendDirection = item.trendDirection

      // Start new group if trend direction or color changes
      if (currentTrendDirection !== itemTrendDirection || currentColor !== itemColor) {
        // Draw the current group before starting new one
        if (currentGroup.length > 0) {
          this._drawLineGroup(ctx, currentGroup, hRatio, vRatio, currentColor!)
        }

        // Start new group
        currentGroup = [item]
        currentTrendDirection = itemTrendDirection
        currentColor = itemColor
      } else {
        // Add to current group
        currentGroup.push(item)
      }
    }

    // Draw the final group
    if (currentGroup.length > 0 && currentColor) {
      this._drawLineGroup(ctx, currentGroup, hRatio, vRatio, currentColor)
    }
  }

  private _drawLineGroup(
    ctx: CanvasRenderingContext2D,
    group: TrendFillRenderData[],
    hRatio: number,
    vRatio: number,
    color: string
  ): void {
    if (group.length === 0) return


    ctx.strokeStyle = color
    ctx.lineWidth = group[0].lineWidth || 2
    this._setLineStyle(ctx, group[0].lineStyle || 0)

    if (group.length === 1) {
      // Single point - draw a small circle
      const item = group[0]
      const trendLineY = item.trendLineY!
      ctx.fillStyle = color
      ctx.beginPath()
      ctx.arc(item.x!, trendLineY, 1.5, 0, 2 * Math.PI)
      ctx.fill()
    } else {
      // Multiple points - draw continuous line
      ctx.beginPath()
      const firstItem = group[0]
      ctx.moveTo(firstItem.x!, firstItem.trendLineY!)

      for (let i = 1; i < group.length; i++) {
        const item = group[i]
        ctx.lineTo(item.x!, item.trendLineY!)
      }

      ctx.stroke()
    }
  }

  private _setLineStyle(ctx: CanvasRenderingContext2D, lineStyle: number): void {
    switch (lineStyle) {
      case 0:
        ctx.setLineDash([]) // Solid
        break
      case 1:
        ctx.setLineDash([5, 5]) // Dotted
        break
      case 2:
        ctx.setLineDash([10, 5]) // Dashed
        break
      default:
        ctx.setLineDash([])
    }
  }

  private _isValidCoordinates(item: TrendFillRenderData): boolean {
    // Strict coordinate validation (like BaseLineSeries)
    if (item.x === null || item.baseLineY === null || item.trendLineY === null) {
      return false
    }

    // Check bounds with tolerance
    const chartWidth = this._viewData.data.chartWidth || 800
    const tolerance = 100

    if (item.x < -tolerance || item.x > chartWidth + tolerance) {
      return false
    }

    // Check for extreme Y values
    if (Math.abs(item.baseLineY) > 10000 || Math.abs(item.trendLineY) > 10000) {
      return false
    }

    return true
  }
}

// Optimized Trend Fill Pane View (following BaseLineSeries pattern)
class TrendFillPrimitivePaneView implements IPrimitivePaneView {
  _source: TrendFillSeries
  _data: TrendFillViewData

  constructor(source: TrendFillSeries) {
    this._source = source
    this._data = {
      data: {
        items: [],
        timeScale: null,
        priceScale: null,
        chartWidth: 0,
        lineWidth: 1,
        lineStyle: 0,
        visibleRange: null,
        barWidth: 1
      },
      options: this._source.getOptions()
    }
  }

  update() {
    const chart = this._source.getChart()
    const timeScale = chart.timeScale()
    const chartElement = chart.chartElement()

    if (!timeScale || !chartElement) {
      return
    }

    // Get the price scales using the real line series (following band series pattern)
    const baseLineSeries = this._source.getBaseLineSeries()
    const trendLineSeries = this._source.getTrendLineSeries()
    if (!baseLineSeries || !trendLineSeries) {
      return
    }

    // Update view data with the real line series which have the correct price scale
    this._data.data.timeScale = timeScale
    this._data.data.priceScale = baseLineSeries

    // Get chart dimensions
    this._data.data.chartWidth = chartElement?.clientWidth || 800

    // Get bar spacing (like BaseLineSeries)
    try {
      // Try to get bar spacing from chart model
      const chartModel = (chart as any)._model
      if (chartModel && chartModel.timeScale && chartModel.timeScale.barSpacing) {
        this._data.data.barWidth = chartModel.timeScale.barSpacing()
      } else {
        this._data.data.barWidth = 1 // Default fallback
      }
    } catch (error) {
      this._data.data.barWidth = 1 // Default fallback
    }

    // Batch coordinate conversion (like BaseLineSeries)
    const items = this._source.getProcessedData()
    const convertedItems = this._batchConvertCoordinates(items, timeScale, baseLineSeries, trendLineSeries)

    // Set visible range (like BaseLineSeries)
    this._data.data.visibleRange = this._calculateVisibleRange(convertedItems)

    // Update renderer data efficiently
    this._data.data.items = convertedItems
    this._data.data.lineWidth = this._source.getOptions().trendLine.lineWidth
    this._data.data.lineStyle = this._source.getOptions().trendLine.lineStyle

  }

  // Batch coordinate conversion (following band series pattern)
  private _batchConvertCoordinates(
    items: TrendFillItem[],
    timeScale: any,
    baseLineSeries: any,
    trendLineSeries: any
  ): TrendFillRenderData[] {

    if (!timeScale || !baseLineSeries || !trendLineSeries) {
      return []
    }

    return items
      .map((item, index) => {
        try {
          // Convert coordinates using the real line series (following band series pattern)
          const x = timeScale.timeToCoordinate(item.time)


          // Use the real line series for coordinate conversion (following band series approach)
          const baseLineY = baseLineSeries.priceToCoordinate(item.baseLine)
          const trendLineY = trendLineSeries.priceToCoordinate(item.trendLine)


          // Validate coordinates
          if (x === null || baseLineY === null || trendLineY === null) {
            return null
          }

          return {
            x,
            baseLineY,
            trendLineY,
            fillColor: item.fillColor,
            lineColor: item.lineColor,
            lineWidth: item.lineWidth,
            lineStyle: item.lineStyle,
            trendDirection: item.trendDirection
          }
        } catch (error) {
          return null
        }
      })
      .filter(item => item !== null) as TrendFillRenderData[]
  }

  // Calculate visible range (like BaseLineSeries)
  private _calculateVisibleRange(items: TrendFillRenderData[]): {from: number; to: number} | null {
    if (items.length === 0) return null

    // Simple visible range calculation
    // In a real implementation, this would consider chart viewport
    return {from: 0, to: items.length}
  }

  renderer() {
    return new TrendFillPrimitivePaneRenderer(this._data)
  }

  // Z-index support: Return the Z-index for proper layering
  zIndex(): number {
    const zIndex = this._source.getOptions().zIndex
    // Validate Z-index is a positive number
    if (typeof zIndex === 'number' && zIndex >= 0) {
      return zIndex
    }
    // Return default Z-index for trend fill series
    return 100
  }
}

// Trend Fill Series Class (following BandSeries pattern)
export class TrendFillSeries implements ISeriesPrimitive<Time> {
  private chart: IChartApi
  private baseLineSeries: ISeriesApi<'Line'>
  private trendLineSeries: ISeriesApi<'Line'>
  private options: TrendFillOptions
  private data: TrendFillData[] = []
  private _paneViews: TrendFillPrimitivePaneView[]
  private paneId: number

  // Processed data for rendering
  private trendFillItems: TrendFillItem[] = []

  constructor(
    chart: IChartApi,
    options: TrendFillOptions = {
      uptrendFillColor: 'rgba(76, 175, 80, 0.3)', // Green with transparency for uptrend fill
      downtrendFillColor: 'rgba(244, 67, 54, 0.3)', // Red with transparency for downtrend fill
      trendLine: {
        color: '#4CAF50', // Default green (will be overridden by trend direction)
        lineWidth: 2,
        lineStyle: 0,
        visible: true
      },
      baseLine: {
        color: '#666666',
        lineWidth: 1,
        lineStyle: 1,
        visible: false
      },
      visible: true,
      priceScaleId: 'right' // Default priceScaleId
    },
    paneId: number = 0
  ) {
    this.chart = chart
    this.options = {...options}
    this.paneId = paneId
    this._paneViews = [new TrendFillPrimitivePaneView(this)]

    // Create the two line series (following band series pattern) - make them transparent for coordinate conversion only
    this.baseLineSeries = chart.addSeries(LineSeries, {
      color: 'rgba(0,0,0,0)', // Fully transparent
      lineStyle: 0,
      lineWidth: 1,
      visible: true, // Must be visible for primitive to render and coordinate conversion
      priceScaleId: this.options.priceScaleId,
      lastValueVisible: false,
      priceLineVisible: false
    })

    this.trendLineSeries = chart.addSeries(LineSeries, {
      color: 'rgba(0,0,0,0)', // Fully transparent
      lineStyle: 0,
      lineWidth: 1,
      visible: true, // Must be visible for coordinate conversion
      priceScaleId: this.options.priceScaleId,
      lastValueVisible: false,
      priceLineVisible: false
    })

    // Attach the primitive to the base line series for rendering
    this.baseLineSeries.attachPrimitive(this)
  }

  public setDummySeries(series: any): void {
    // No longer needed - we have real line series
  }

  public setData(data: TrendFillData[]): void {
    this.data = data
    this.processData()

    // Extract baseline and trendline data for the real line series
    if (this.data.length > 0) {
      const baseLineData = this.data.map(item => ({
        time: parseTime(item.time),
        value: (item.base_line ?? item.baseLine) || 0
      })).filter(item => item.time > 0)

      const trendLineData = this.data.map(item => ({
        time: parseTime(item.time),
        value: (item.trend_line ?? item.trendLine) || 0
      })).filter(item => item.time > 0 && item.value !== 0)

      // Set data for the real line series
      if (baseLineData.length > 0) {
        this.baseLineSeries.setData(baseLineData)
      }
      if (trendLineData.length > 0) {
        this.trendLineSeries.setData(trendLineData)
      }
    }

    this.updateAllViews()
  }

  public updateData(data: TrendFillData[]): void {
    this.setData(data)
  }

  private processData(): void {
    this.trendFillItems = []

    if (!this.data || this.data.length === 0) {
      return
    }


    // Sort data by time
    const sortedData = [...this.data].sort((a, b) => {
      const timeA = parseTime(a.time)
      const timeB = parseTime(b.time)
      return timeA - timeB
    })

    // Count trend directions in raw data
    const trendDirectionCounts = { '-1': 0, '0': 0, '1': 0, 'null': 0, 'undefined': 0 }
    sortedData.forEach((item, i) => {
      const trendDirection = item.trend_direction ?? item.trendDirection
      const key = trendDirection === null ? 'null' : trendDirection === undefined ? 'undefined' : String(trendDirection)
      trendDirectionCounts[key] = (trendDirectionCounts[key] || 0) + 1
    })

    // Process each data point
    for (let i = 0; i < sortedData.length; i++) {
      const item = sortedData[i]
      const time = parseTime(item.time)
      // Handle both camelCase and snake_case field names for backwards compatibility
      const baseLine = item.base_line ?? item.baseLine
      const trendLine = item.trend_line ?? item.trendLine
      const trendDirection = item.trend_direction ?? item.trendDirection


      if (
        baseLine === null ||
        baseLine === undefined ||
        trendDirection === null ||
        trendDirection === undefined
      ) {
        continue
      }

      // Skip neutral trends (they don't have trend lines to draw)
      if (trendDirection === 0 || trendLine === null || trendLine === undefined) {
        continue
      }

      // Determine colors and styles based on trend direction
      const isUptrend = trendDirection > 0

      // Use different colors for uptrend and downtrend
      const fillColor = isUptrend ? this.options.uptrendFillColor : this.options.downtrendFillColor

      // Use different line colors for uptrend (green) and downtrend (red) - matching the image
      const lineColor = isUptrend
        ? this._getSolidColorFromFill(this.options.uptrendFillColor) // Green line for uptrend
        : this._getSolidColorFromFill(this.options.downtrendFillColor) // Red line for downtrend


      const lineWidth = this.options.trendLine.lineWidth
      const lineStyle = this.options.trendLine.lineStyle

      // Create trend fill item (like BaseLineSeries data structure)
      this.trendFillItems.push({
        time,
        baseLine,
        trendLine,
        trendDirection,
        fillColor,
        lineColor,
        lineWidth,
        lineStyle
      })
    }

  }

  public applyOptions(options: Partial<TrendFillOptions>): void {
    this.options = {...this.options, ...options}
    this.processData()
    this.updateAllViews()
  }

  private _getSolidColorFromFill(fillColor: string): string {
    // Convert fill colors (which may have transparency) to solid line colors
    if (fillColor.includes('rgba')) {
      // Convert rgba to solid color by setting alpha to 1
      return fillColor.replace(/,\s*[0-9.]+\s*\)/, ', 1)')
    } else if (fillColor.includes('rgb')) {
      // Already solid rgb
      return fillColor
    } else if (fillColor.startsWith('#')) {
      // Hex color - already solid
      return fillColor
    } else {
      // Named color or other format - return as is
      return fillColor
    }
  }

  public setVisible(visible: boolean): void {
    this.options.visible = visible
    this.processData()
    this.updateAllViews()
  }

  public destroy(): void {
    try {
      this.chart.removeSeries(this.baseLineSeries)
      this.chart.removeSeries(this.trendLineSeries)
    } catch (error) {
      // Ignore errors during cleanup
    }
  }

  // Getter methods
  getOptions(): TrendFillOptions {
    return this.options
  }

  getChart(): IChartApi {
    return this.chart
  }

  getProcessedData(): TrendFillItem[] {
    return this.trendFillItems
  }

  getBaseLineSeries(): ISeriesApi<'Line'> {
    return this.baseLineSeries
  }

  getTrendLineSeries(): ISeriesApi<'Line'> {
    return this.trendLineSeries
  }

  // ISeriesPrimitive implementation
  attached(param: SeriesAttachedParameter<Time>): void {
    // Primitive is attached to the series
  }

  detached(): void {
    // Primitive is detached from the series
  }

  updateAllViews(): void {
    this._paneViews.forEach(pv => pv.update())
  }

  paneViews(): IPrimitivePaneView[] {
    return this._paneViews
  }
}

// Custom Series View for compatibility with series factory
class TrendFillSeriesView {
  private _data: TrendFillData[] = []
  private _options: TrendFillOptions
  private _trendFillSeries: TrendFillSeries | null = null

  constructor(data: TrendFillData[], options: TrendFillOptions) {
    this._data = data
    this._options = options
  }

  processData() {
    return this._data
  }

  setData(newData: TrendFillData[]) {
    this._data = newData
    if (this._trendFillSeries) {
      this._trendFillSeries.setData(newData)
    }
  }

  getData() {
    return this._data
  }

  getOptions() {
    return this._options
  }

  // This method will be called by addCustomSeries
  createSeries(chart: IChartApi, paneId?: number) {
    this._trendFillSeries = new TrendFillSeries(chart, this._options, paneId || 0)
    this._trendFillSeries.setData(this._data)
    return this._trendFillSeries
  }
}

// Factory function to create trend fill series (matching the series factory call pattern)
export function createTrendFillSeriesPlugin(
  data: TrendFillData[],
  options: TrendFillOptions
): TrendFillSeriesView {
  return new TrendFillSeriesView(data, options)
}

import {
  IPrimitivePaneRenderer,
  IPrimitivePaneView,
  ISeriesPrimitive,
  IChartApi,
  ISeriesApi,
  Coordinate,
  UTCTimestamp
} from 'lightweight-charts'
import {ChartCoordinateService} from '../../services/ChartCoordinateService'
import {createBoundingBox} from '../../utils/coordinateValidation'

interface TradeRectangleData {
  time1: UTCTimestamp
  time2: UTCTimestamp
  price1: number
  price2: number
  fillColor: string
  borderColor: string
  borderWidth: number
  opacity: number
  label?: string
  textPosition?: 'inside' | 'above' | 'below'
  textFontSize?: number
  textColor?: string
  textBackground?: string
}

/**
 * Trade Rectangle Renderer following official TradingView patterns
 */
class TradeRectangleRenderer implements IPrimitivePaneRenderer {
  private _x1: Coordinate
  private _y1: Coordinate
  private _x2: Coordinate
  private _y2: Coordinate
  private _fillColor: string
  private _borderColor: string
  private _borderWidth: number
  private _opacity: number
  private _label: string
  private _textPosition: 'inside' | 'above' | 'below'
  private _textFontSize: number
  private _textColor: string
  private _textBackground: string

  constructor(
    x1: Coordinate,
    y1: Coordinate,
    x2: Coordinate,
    y2: Coordinate,
    fillColor: string,
    borderColor: string,
    borderWidth: number,
    opacity: number,
    label: string = '',
    textPosition: 'inside' | 'above' | 'below' = 'inside',
    textFontSize: number = 10,
    textColor: string = '#FFFFFF',
    textBackground: string = 'rgba(0, 0, 0, 0.7)'
  ) {
    this._x1 = x1
    this._y1 = y1
    this._x2 = x2
    this._y2 = y2
    this._fillColor = fillColor
    this._borderColor = borderColor
    this._borderWidth = borderWidth
    this._opacity = opacity
    this._label = label
    this._textPosition = textPosition
    this._textFontSize = textFontSize
    this._textColor = textColor
    this._textBackground = textBackground
  }

  draw(target: any) {
    // We use drawBackground for rectangles
  }

  drawBackground(target: any) {
    // Early return if coordinates are invalid
    if (
      this._x1 === null ||
      this._y1 === null ||
      this._x2 === null ||
      this._y2 === null ||
      this._x1 === undefined ||
      this._y1 === undefined ||
      this._x2 === undefined ||
      this._y2 === undefined ||
      this._x1 === 0 ||
      this._y1 === 0 ||
      this._x2 === 0 ||
      this._y2 === 0
    ) {
      return
    }

    // Use bitmap coordinate space for pixel-perfect rendering
    target.useBitmapCoordinateSpace((scope: any) => {
      const ctx = scope.context

      // Convert to bitmap coordinates
      const x1 = this._x1 * scope.horizontalPixelRatio
      const y1 = this._y1 * scope.verticalPixelRatio
      const x2 = this._x2 * scope.horizontalPixelRatio
      const y2 = this._y2 * scope.verticalPixelRatio

      // Calculate rectangle bounds
      const left = Math.min(x1, x2)
      const top = Math.min(y1, y2)
      const width = Math.abs(x2 - x1)
      const height = Math.abs(y2 - y1)

      if (width < 1 || height < 1) {
        return
      }

      try {
        // Draw filled rectangle
        ctx.globalAlpha = this._opacity
        ctx.fillStyle = this._fillColor
        ctx.fillRect(left, top, width, height)

        // Draw border
        if (this._borderWidth > 0) {
          ctx.globalAlpha = 1.0
          ctx.strokeStyle = this._borderColor
          ctx.lineWidth = this._borderWidth * scope.horizontalPixelRatio
          ctx.strokeRect(left, top, width, height)
        }

        // Draw label with configurable position and styling
        if (this._label) {
          ctx.globalAlpha = 1.0
          ctx.font = `${this._textFontSize * scope.verticalPixelRatio}px Arial`
          ctx.textAlign = 'center'

          // Calculate text position based on textPosition setting
          let textX = left + width / 2
          let textY: number
          let showBackground = true

          switch (this._textPosition) {
            case 'above':
              textY = top - 5 * scope.verticalPixelRatio // 5px above rectangle
              ctx.textBaseline = 'bottom'
              break
            case 'below':
              textY = top + height + 5 * scope.verticalPixelRatio // 5px below rectangle
              ctx.textBaseline = 'top'
              break
            case 'inside':
            default:
              textY = top + height / 2
              ctx.textBaseline = 'middle'
              break
          }

          // Measure text dimensions for background
          const textMetrics = ctx.measureText(this._label)
          const textWidth = textMetrics.width
          const textHeight = this._textFontSize * scope.verticalPixelRatio

          // Draw text background if configured
          if (showBackground && this._textBackground && this._textBackground !== 'transparent') {
            ctx.fillStyle = this._textBackground
            const bgPadding = 2 * scope.horizontalPixelRatio
            ctx.fillRect(
              textX - textWidth / 2 - bgPadding,
              textY - textHeight / 2 - bgPadding,
              textWidth + 2 * bgPadding,
              textHeight + 2 * bgPadding
            )
          }

          // Draw text
          ctx.fillStyle = this._textColor
          ctx.fillText(this._label, textX, textY)
        }
      } finally {
        ctx.globalAlpha = 1.0
      }
    })
  }
}

/**
 * Trade Rectangle View following official TradingView patterns
 */
class TradeRectangleView implements IPrimitivePaneView {
  private _source: TradeRectanglePrimitive
  private _x1: Coordinate = 0 as Coordinate
  private _y1: Coordinate = 0 as Coordinate
  private _x2: Coordinate = 0 as Coordinate
  private _y2: Coordinate = 0 as Coordinate

  constructor(source: TradeRectanglePrimitive) {
    this._source = source
  }

  update() {
    const data = this._source.data()
    const chart = this._source.chart()
    const series = this._source.series()

    if (!chart || !series || !data) {
      return
    }

    try {
      // Use our coordinate services and utilities for consistency across the codebase:
      // - Chart registration is handled via ChartCoordinateService
      // - Coordinate validation uses our centralized validation utilities
      // - Direct coordinate conversion follows official TradingView patterns
      const timeScale = chart.timeScale()

      // Get time scale state for coordinate conversion

      const x1 = timeScale.timeToCoordinate(data.time1)
      const x2 = timeScale.timeToCoordinate(data.time2)

      // Convert price coordinates using series coordinate conversion
      const y1 = series.priceToCoordinate(data.price1)
      const y2 = series.priceToCoordinate(data.price2)

      // CRITICAL FIX: Graceful failure handling identical to the old working canvas overlay approach
      // If coordinate conversion fails, silently return and let automatic retry mechanism handle it
      if (x1 === null || x2 === null || y1 === null || y2 === null) {
        return // Silent failure - just like old canvas overlay approach
      }

      // Validate coordinates are finite
      if (
        !isFinite(x1) ||
        isNaN(x1) ||
        !isFinite(x2) ||
        isNaN(x2) ||
        !isFinite(y1) ||
        isNaN(y1) ||
        !isFinite(y2) ||
        isNaN(y2)
      ) {
        return // Silent failure - automatic retry will occur
      }

      // Create bounding box for validation
      const boundingBox = createBoundingBox(
        Math.min(x1, x2),
        Math.min(y1, y2),
        Math.abs(x2 - x1),
        Math.abs(y2 - y1)
      )

      // Ensure non-zero dimensions
      if (boundingBox.width <= 0 || boundingBox.height <= 0) {
        return // Silent failure - automatic retry will occur
      }

      // SUCCESS: Set coordinates (same as old approach when coordinates are valid)
      this._x1 = x1
      this._y1 = y1
      this._x2 = x2
      this._y2 = y2
    } catch (error) {
      // CRITICAL FIX: Graceful error handling like old canvas overlay approach
      // Don't log errors prominently - just let automatic retry handle it

      return // Silent failure with automatic retry via event listeners
    }
  }

  renderer() {
    const data = this._source.data()
    return new TradeRectangleRenderer(
      this._x1,
      this._y1,
      this._x2,
      this._y2,
      data.fillColor,
      data.borderColor,
      data.borderWidth,
      data.opacity,
      data.label || '',
      data.textPosition || 'inside',
      data.textFontSize || 10,
      data.textColor || '#FFFFFF',
      data.textBackground || 'rgba(0, 0, 0, 0.7)'
    )
  }
}

/**
 * Trade Rectangle Primitive following official TradingView patterns
 *
 * This primitive provides:
 * - Event-driven automatic retry logic for coordinate conversion
 * - Graceful failure handling when chart isn't ready
 * - Integration with ChartCoordinateService for consistency
 * - Proper cleanup of event listeners to prevent memory leaks
 * - Support for findNearestTime timestamp adjustment
 */
export class TradeRectanglePrimitive implements ISeriesPrimitive {
  private _data: TradeRectangleData
  private _chart: IChartApi | null = null
  private _series: ISeriesApi<any> | null = null
  private _paneView: TradeRectangleView
  private _requestUpdate?: () => void
  private _timeScaleCallback?: (() => void) | null
  private _crosshairCallback?: (() => void) | null
  private _updateThrottled: boolean = false

  constructor(data: TradeRectangleData) {
    this._data = data
    this._paneView = new TradeRectangleView(this)
  }

  // Required by ISeriesPrimitive interface
  updateAllViews() {
    this._paneView.update()
  }

  paneViews() {
    return [this._paneView]
  }

  // Lifecycle methods following official patterns
  attached({
    chart,
    series,
    requestUpdate
  }: {
    chart: IChartApi
    series: ISeriesApi<any>
    requestUpdate: () => void
  }) {
    this._chart = chart
    this._series = series
    this._requestUpdate = requestUpdate

    // Ensure chart is registered with our coordinate service for consistency
    const coordinateService = ChartCoordinateService.getInstance()
    const chartId = chart.chartElement()?.id || 'default'
    coordinateService.registerChart(chartId, chart)

    // CRITICAL FIX: Add event-driven retry logic identical to the old working canvas overlay approach
    // This ensures coordinate conversion is automatically retried when the chart becomes ready
    try {
      // Create callback functions and store them for cleanup
      this._timeScaleCallback = () => {
        this._requestUpdate()
      }

      this._crosshairCallback = () => {
        // Throttle crosshair updates to avoid performance issues
        if (!this._updateThrottled) {
          this._updateThrottled = true
          setTimeout(() => {
            this._updateThrottled = false
            this._requestUpdate()
          }, 100) // Throttle to 10fps for crosshair updates
        }
      }

      // Subscribe to events (Lightweight Charts pattern: returns void, store callbacks for cleanup)
      chart.timeScale().subscribeVisibleTimeRangeChange(this._timeScaleCallback)
      chart.subscribeCrosshairMove(this._crosshairCallback)
    } catch (error) {

    }

    // Request initial update
    this._requestUpdate()
  }

  detached() {
    // Clean up event subscriptions to prevent memory leaks
    if (this._chart && this._timeScaleCallback) {
      try {
        this._chart.timeScale().unsubscribeVisibleTimeRangeChange(this._timeScaleCallback)
        this._timeScaleCallback = null
      } catch (error) {

      }
    }

    if (this._chart && this._crosshairCallback) {
      try {
        this._chart.unsubscribeCrosshairMove(this._crosshairCallback)
        this._crosshairCallback = null
      } catch (error) {

      }
    }

    this._chart = null
    this._series = null
    this._requestUpdate = undefined
    this._updateThrottled = false
  }

  // Getter methods
  data(): TradeRectangleData {
    return this._data
  }

  chart(): IChartApi | null {
    return this._chart
  }

  series(): ISeriesApi<any> | null {
    return this._series
  }

  // Update rectangle data and request redraw
  updateData(newData: Partial<TradeRectangleData>) {
    this._data = {...this._data, ...newData}
    if (this._requestUpdate) {
      this._requestUpdate()
    }
  }
}

// Factory function for creating trade rectangle primitives
export function createTradeRectanglePrimitives(
  trades: Array<{
    entryTime: string | UTCTimestamp
    exitTime?: string | UTCTimestamp
    entryPrice: number
    exitPrice: number
    fillColor?: string
    borderColor?: string
    borderWidth?: number
    opacity?: number
    label?: string
  }>,
  chartData?: any[]
): TradeRectanglePrimitive[] {
  const primitives: TradeRectanglePrimitive[] = []

  trades.forEach(trade => {
    // Parse times
    let time1: UTCTimestamp
    let time2: UTCTimestamp

    if (typeof trade.entryTime === 'string') {
      time1 = Math.floor(new Date(trade.entryTime).getTime() / 1000) as UTCTimestamp
    } else {
      time1 = trade.entryTime
    }

    if (trade.exitTime) {
      if (typeof trade.exitTime === 'string') {
        time2 = Math.floor(new Date(trade.exitTime).getTime() / 1000) as UTCTimestamp
      } else {
        time2 = trade.exitTime
      }
    } else if (chartData && chartData.length > 0) {
      // Use last available time for open trades
      const lastTime = chartData[chartData.length - 1]?.time
      if (lastTime) {
        time2 =
          typeof lastTime === 'string'
            ? (Math.floor(new Date(lastTime).getTime() / 1000) as UTCTimestamp)
            : lastTime
      } else {
        return // Skip if no exit time available
      }
    } else {
      return // Skip if no exit time available
    }

    const rectangleData: TradeRectangleData = {
      time1,
      time2,
      price1: trade.entryPrice,
      price2: trade.exitPrice,
      fillColor: trade.fillColor || 'rgba(0, 150, 136, 0.2)',
      borderColor: trade.borderColor || 'rgb(0, 150, 136)',
      borderWidth: trade.borderWidth || 1,
      opacity: trade.opacity || 0.2,
      label: trade.label
    }

    primitives.push(new TradeRectanglePrimitive(rectangleData))
  })

  return primitives
}

import { IChartApi, ISeriesApi, IPanePrimitive, Time } from 'lightweight-charts'
import { CornerLayoutManager } from '../services/CornerLayoutManager'
import { ChartCoordinateService } from '../services/ChartCoordinateService'
import { TemplateEngine, TemplateContext, TemplateResult } from '../services/TemplateEngine'
import { PrimitiveEventManager, EventSubscription } from '../services/PrimitiveEventManager'
import { Corner, Position, IPositionableWidget, WidgetDimensions } from '../types/layout'

/**
 * Base configuration for all pane primitives
 */
export interface BasePrimitiveConfig {
  /**
   * Position in chart corner
   */
  corner: Corner

  /**
   * Priority for stacking order (lower = higher priority)
   */
  priority: number


  /**
   * Whether the primitive is visible
   */
  visible?: boolean

  /**
   * Styling configuration
   */
  style?: {
    backgroundColor?: string
    color?: string
    fontSize?: number
    fontFamily?: string
    borderRadius?: number
    padding?: number
    margin?: number
    zIndex?: number
  }
}

/**
 * Interface for template data used in primitives
 */
export interface TemplateData {
  [key: string]: any
}

/**
 * Abstract base class for all pane primitives
 *
 * This class provides:
 * - Layout Management System - built-in positioning and stacking
 * - React Integration Layer - hybrid primitives that can render React components
 * - Event System Integration - built-in event handling
 * - Template processing system for dynamic content
 *
 * Following DRY principles with single source of truth architecture
 */
export abstract class BasePanePrimitive<TConfig extends BasePrimitiveConfig = BasePrimitiveConfig>
  implements IPanePrimitive<Time>, IPositionableWidget {

  // IPanePrimitive implementation
  public readonly id: string

  // IPositionableWidget implementation
  public readonly corner: Corner
  public readonly priority: number
  public visible: boolean = true

  // Core services
  protected config: TConfig
  protected chart: IChartApi | null = null
  protected series: ISeriesApi<any> | null = null
  protected requestUpdate: (() => void) | null = null
  protected layoutManager: CornerLayoutManager | null = null
  protected coordinateService: ChartCoordinateService
  protected templateEngine: TemplateEngine
  protected eventManager: PrimitiveEventManager | null = null

  // Layout state
  protected currentPosition: Position | null = null
  protected containerElement: HTMLElement | null = null
  protected mounted: boolean = false

  // Event handling
  protected eventSubscriptions: EventSubscription[] = []

  // Template processing
  protected templateData: TemplateData = {}
  protected templateContext: TemplateContext = {}
  protected lastTemplateResult: TemplateResult | null = null

  constructor(id: string, config: TConfig) {
    this.id = id
    this.config = { ...config }
    this.corner = config.corner
    this.priority = config.priority
    this.visible = config.visible !== false
    this.coordinateService = ChartCoordinateService.getInstance()
    this.templateEngine = TemplateEngine.getInstance()
  }

  // ===== IPanePrimitive Interface =====

  /**
   * Called when primitive is attached to a pane
   */
  public attached(params: {
    chart: IChartApi
    series: ISeriesApi<any>
    requestUpdate: () => void
  }): void {
    this.chart = params.chart
    this.series = params.series
    this.requestUpdate = params.requestUpdate

    // Initialize layout management
    this.initializeLayoutManagement()

    // Initialize event management
    this.initializeEventManagement()

    // Setup default event subscriptions
    this.setupDefaultEventSubscriptions()

    // Call lifecycle hook
    this.onAttached(params)

    // Mark as mounted
    this.mounted = true

    // Register with layout manager
    if (this.layoutManager) {
      this.layoutManager.registerWidget(this)
    }

    // Trigger initial render
    this.updateAllViews()
  }

  /**
   * Called when primitive is detached from a pane
   */
  public detached(): void {
    // Unregister from layout manager
    if (this.layoutManager) {
      this.layoutManager.unregisterWidget(this.id)
    }

    // Cleanup event subscriptions
    this.cleanupEventSubscriptions()

    // Cleanup container
    this.destroyContainer()

    // Call lifecycle hook
    this.onDetached()

    // Clear references
    this.chart = null
    this.series = null
    this.layoutManager = null
    this.eventManager = null
    this.mounted = false
  }

  /**
   * Required IPanePrimitive method - provides pane views for rendering
   * This enables immediate response to pane resize/redraw events
   */
  public paneViews(): any[] {
    return [
      {
        renderer: () => ({
          draw: () => {
            // Empty draw method - layout updates should only happen on actual resize events
            // not on every frame to avoid performance issues during pan/zoom operations
          }
        })
      }
    ]
  }

  /**
   * Main primitive update method - handles rendering
   */
  public updateAllViews(): void {
    if (!this.mounted || !this.visible) {
      return
    }

    // Process template data
    this.processTemplate()

    // Ensure container exists
    this.ensureContainer()

    // Update content
    this.renderContent()

    // Call lifecycle hook
    this.onUpdate()
  }

  // ===== IPositionableWidget Interface =====

  /**
   * Get current dimensions of the primitive's container
   */
  public getDimensions(): WidgetDimensions {
    if (!this.containerElement) {
      return { width: 0, height: 0 }
    }

    // Get actual dimensions
    let width = this.containerElement.offsetWidth || 0
    let height = this.containerElement.offsetHeight || 0

    // Fallback for cases where element hasn't been rendered yet
    if ((width === 0 || height === 0) && this.containerElement) {
      // Force measurement by temporarily making element visible
      const originalDisplay = this.containerElement.style.display
      const originalVisibility = this.containerElement.style.visibility
      const originalPosition = this.containerElement.style.position

      this.containerElement.style.display = 'block'
      this.containerElement.style.visibility = 'hidden'
      this.containerElement.style.position = 'absolute'

      width = this.containerElement.offsetWidth || width
      height = this.containerElement.offsetHeight || height

      // Restore original styles
      this.containerElement.style.display = originalDisplay
      this.containerElement.style.visibility = originalVisibility
      this.containerElement.style.position = originalPosition

      // If still zero, provide reasonable defaults based on content
      if (width === 0) {
        const textLength = this.containerElement.textContent?.length || 50
        width = Math.max(100, textLength * 8) // Approximate character width
      }
      if (height === 0) {
        height = 24 // Default height for one line of text with padding
      }
    }

    return { width, height }
  }

  /**
   * Called by layout manager to update position
   * Now properly integrates with lightweight-charts coordinate updates
   */
  public updatePosition(position: Position): void {
    this.currentPosition = position

    if (this.containerElement) {
      // Apply position immediately for instant response
      this.applyPositionToContainer(position)
    }

    // Call lifecycle hook
    this.onPositionUpdate(position)
  }

  // ===== Layout Management System =====

  /**
   * Initialize layout management integration
   */
  private initializeLayoutManagement(): void {
    if (!this.chart) return

    // Get chart-level layout manager (for chart-level widgets like range switcher)
    // or pane-specific layout manager (for pane-level widgets like legends)
    const paneId = this.getPaneId()
    const chartId = this.getChartId()

    this.layoutManager = CornerLayoutManager.getInstance(chartId, paneId)
    this.layoutManager.setChartApi(this.chart)

    // Setup coordinate service integration
    this.coordinateService.setupLayoutManagerIntegration(this.chart, this.layoutManager)
  }

  /**
   * Get the pane ID for this primitive
   * Defaults to 0 (main pane) - can be overridden by subclasses if needed
   */
  protected getPaneId(): number {
    return 0
  }

  /**
   * Get the chart ID for this primitive
   */
  protected getChartId(): string {
    return this.chart?.chartElement()?.id || 'default'
  }

  // ===== Container Management =====

  /**
   * Ensure container element exists
   */
  private ensureContainer(): void {
    if (this.containerElement) {
      return
    }

    if (!this.chart) {
      return
    }

    const chartElement = this.chart.chartElement()
    if (!chartElement) {
      return
    }

    // Create container
    this.containerElement = document.createElement('div')
    this.containerElement.id = `${this.id}-container`
    this.containerElement.className = `primitive-container ${this.getContainerClassName()}`

    // Apply base styling
    this.applyBaseContainerStyling()

    // Apply position if available
    if (this.currentPosition) {
      this.applyPositionToContainer(this.currentPosition)
    }

    // Attach to chart
    chartElement.appendChild(this.containerElement)

    // Call lifecycle hook
    this.onContainerCreated(this.containerElement)
  }

  /**
   * Apply position to container element using lightweight-charts coordinate system
   */
  private applyPositionToContainer(position: Position): void {
    if (!this.containerElement) return

    const style = this.containerElement.style

    // Use lightweight-charts coordinate system for positioning
    style.position = 'absolute'
    style.top = ''
    style.right = ''
    style.bottom = ''
    style.left = ''

    // Apply coordinates that will be managed by lightweight-charts coordinate transforms
    if (position.top !== undefined) style.top = `${position.top}px`
    if (position.right !== undefined) style.right = `${position.right}px`
    if (position.bottom !== undefined) style.bottom = `${position.bottom}px`
    if (position.left !== undefined) style.left = `${position.left}px`
    if (position.zIndex !== undefined) style.zIndex = position.zIndex.toString()

    // Request chart update to synchronize with lightweight-charts coordinate system
    if (this.requestUpdate) {
      this.requestUpdate()
    }
  }

  /**
   * Apply base container styling
   */
  private applyBaseContainerStyling(): void {
    if (!this.containerElement) return

    const style = this.containerElement.style
    style.position = 'absolute'
    style.pointerEvents = 'auto'
    style.userSelect = 'none'

    // Apply config styling
    if (this.config.style) {
      const configStyle = this.config.style
      const isLegend = this.id.includes('legend')

      // For legends, don't apply background to container - let content handle it
      if (configStyle.backgroundColor && !isLegend) {
        style.backgroundColor = configStyle.backgroundColor
      }
      if (configStyle.color && !isLegend) style.color = configStyle.color
      if (configStyle.fontSize) style.fontSize = `${configStyle.fontSize}px`
      if (configStyle.fontFamily) style.fontFamily = configStyle.fontFamily
      if (configStyle.borderRadius && !isLegend) style.borderRadius = `${configStyle.borderRadius}px`
      if (configStyle.padding && !isLegend) style.padding = `${configStyle.padding}px`

      // For legends, force margin to 0 since spacing is handled by layout manager
      if (isLegend) {
        style.margin = '0'
      } else if (configStyle.margin) {
        style.margin = `${configStyle.margin}px`
      }
      if (configStyle.zIndex) style.zIndex = configStyle.zIndex.toString()

      // Ensure container is transparent for legends
      if (isLegend) {
        style.backgroundColor = 'transparent'
        style.color = 'inherit'
      }
    }
  }

  /**
   * Destroy container element
   */
  private destroyContainer(): void {
    if (this.containerElement && this.containerElement.parentNode) {
      this.containerElement.parentNode.removeChild(this.containerElement)
    }
    this.containerElement = null
  }

  // ===== Template Processing System =====

  /**
   * Set template data for processing
   */
  public setTemplateData(data: TemplateData): void {
    this.templateData = { ...this.templateData, ...data }

    if (this.mounted) {
      this.processTemplate()
      this.renderContent()
    }
  }

  /**
   * Update template context for processing
   */
  public updateTemplateContext(context: Partial<TemplateContext>): void {
    this.templateContext = { ...this.templateContext, ...context }

    if (this.mounted) {
      this.processTemplate()
      this.renderContent()
    }
  }

  /**
   * Process template with current data using TemplateEngine
   */
  protected processTemplate(): void {
    const template = this.getTemplate()
    const context: TemplateContext = {
      ...this.templateContext,
      customData: this.templateData
    }

    this.lastTemplateResult = this.templateEngine.processTemplate(template, context)

    // Log any errors for debugging
    if (this.lastTemplateResult.hasErrors) {
      console.warn(`Template processing errors in ${this.id}:`, this.lastTemplateResult.errors)
    }
  }

  /**
   * Get processed template content
   */
  protected getProcessedContent(): string {
    return this.lastTemplateResult?.content || this.getTemplate()
  }

  /**
   * Get template processing result for debugging
   */
  public getTemplateResult(): TemplateResult | null {
    return this.lastTemplateResult
  }

  // ===== Event System Integration =====

  /**
   * Initialize event management
   */
  private initializeEventManagement(): void {
    if (!this.chart) return

    const chartId = this.getChartId()
    this.eventManager = PrimitiveEventManager.getInstance(chartId)
    this.eventManager.initialize(this.chart)
  }

  /**
   * Setup default event subscriptions
   */
  private setupDefaultEventSubscriptions(): void {
    if (!this.eventManager) return

    // Subscribe to crosshair moves for template data updates
    const crosshairSub = this.eventManager.subscribe('crosshairMove', (event) => {
      this.handleCrosshairMove(event)
    })
    this.eventSubscriptions.push(crosshairSub)

    // Subscribe to chart resize for layout updates
    const resizeSub = this.eventManager.subscribe('resize', (event) => {
      this.handleChartResize(event)
    })
    this.eventSubscriptions.push(resizeSub)

    // Call subclass hook for additional subscriptions
    this.setupCustomEventSubscriptions()
  }

  /**
   * Cleanup event subscriptions
   */
  private cleanupEventSubscriptions(): void {
    this.eventSubscriptions.forEach(sub => sub.unsubscribe())
    this.eventSubscriptions = []
  }

  /**
   * Handle crosshair move events
   */
  protected handleCrosshairMove(event: { time: any; point: { x: number; y: number } | null; seriesData: Map<any, any> }): void {
    // Update template context with series data
    if (event.seriesData.size > 0 && this.series) {
      const seriesValue = event.seriesData.get(this.series)
      if (seriesValue) {
        this.updateTemplateContext({
          seriesData: seriesValue,
          formatting: this.config.style ? {
            valueFormat: '.2f', // Default format, can be overridden
            timeFormat: 'YYYY-MM-DD HH:mm:ss'
          } : undefined
        })
      }
    }

    // Call subclass hook
    this.onCrosshairMove(event)
  }

  /**
   * Handle chart resize events
   */
  protected handleChartResize(event: { width: number; height: number }): void {
    // Trigger layout recalculation
    if (this.layoutManager) {
      this.layoutManager.updateChartDimensions(event)
    }

    // Call subclass hook
    this.onChartResize(event)
  }

  /**
   * Get event manager instance
   */
  public getEventManager(): PrimitiveEventManager | null {
    return this.eventManager
  }

  // ===== Visibility Management =====

  /**
   * Set primitive visibility
   */
  public setVisible(visible: boolean): void {
    if (this.visible !== visible) {
      this.visible = visible

      // Update container visibility
      if (this.containerElement) {
        this.containerElement.style.display = visible ? 'block' : 'none'
      }

      // Update layout manager
      if (this.layoutManager) {
        this.layoutManager.updateWidgetVisibility(this.id, visible)
      }

      // Call lifecycle hook
      this.onVisibilityChanged(visible)
    }
  }

  /**
   * Toggle primitive visibility
   */
  public toggle(): void {
    this.setVisible(!this.visible)
  }

  // ===== Configuration Management =====

  /**
   * Update primitive configuration
   */
  public updateConfig(newConfig: Partial<TConfig>): void {
    this.config = { ...this.config, ...newConfig }

    // Re-apply styling if container exists
    if (this.containerElement) {
      this.applyBaseContainerStyling()
    }

    // Trigger update
    if (this.mounted) {
      this.updateAllViews()
    }

    // Call lifecycle hook
    this.onConfigUpdate(newConfig)
  }

  /**
   * Get current configuration
   */
  public getConfig(): TConfig {
    return { ...this.config }
  }

  // ===== Abstract Methods (to be implemented by subclasses) =====

  /**
   * Get the template string for this primitive
   */
  protected abstract getTemplate(): string

  /**
   * Render content to the container
   */
  protected abstract renderContent(): void

  /**
   * Get CSS class name for the container
   */
  protected abstract getContainerClassName(): string

  // ===== Lifecycle Hooks (optional overrides) =====

  /**
   * Called when primitive is attached to chart
   */
  protected onAttached(params: { chart: IChartApi; series: ISeriesApi<any>; requestUpdate: () => void }): void {
    // Override in subclasses
  }

  /**
   * Called when primitive is detached from chart
   */
  protected onDetached(): void {
    // Override in subclasses
  }

  /**
   * Called during each update cycle
   */
  protected onUpdate(): void {
    // Override in subclasses
  }

  /**
   * Called when position is updated by layout manager
   */
  protected onPositionUpdate(position: Position): void {
    // Override in subclasses
  }

  /**
   * Called when container element is created
   */
  protected onContainerCreated(container: HTMLElement): void {
    // Override in subclasses
  }

  /**
   * Called when visibility changes
   */
  protected onVisibilityChanged(visible: boolean): void {
    // Override in subclasses
  }

  /**
   * Called when configuration is updated
   */
  protected onConfigUpdate(newConfig: Partial<TConfig>): void {
    // Override in subclasses
  }

  /**
   * Called when crosshair moves over the chart
   */
  protected onCrosshairMove(event: { time: any; point: { x: number; y: number } | null; seriesData: Map<any, any> }): void {
    // Override in subclasses
  }

  /**
   * Called when chart is resized
   */
  protected onChartResize(event: { width: number; height: number }): void {
    // Override in subclasses
  }

  /**
   * Setup custom event subscriptions - override in subclasses
   */
  protected setupCustomEventSubscriptions(): void {
    // Override in subclasses to add custom event subscriptions
  }

  // ===== Utility Methods =====

  /**
   * Get current position
   */
  public getPosition(): Position | null {
    return this.currentPosition
  }

  /**
   * Get container element
   */
  public getContainer(): HTMLElement | null {
    return this.containerElement
  }

  /**
   * Check if primitive is mounted
   */
  public isMounted(): boolean {
    return this.mounted
  }

  /**
   * Get chart API reference
   */
  public getChart(): IChartApi | null {
    return this.chart
  }

  /**
   * Get series API reference
   */
  public getSeries(): ISeriesApi<any> | null {
    return this.series
  }
}

/**
 * Priority levels for common primitives
 */
export const PrimitivePriority = {
  RANGE_SWITCHER: 1,     // Highest priority - navigation aid
  MINIMIZE_BUTTON: 2,    // High priority - always visible after range switcher
  LEGEND: 3,             // Medium priority - important for data understanding
  CUSTOM: 10,            // Default for custom primitives
  DEBUG: 999             // Lowest priority - debug/development primitives
} as const

/**
 * Primitive type identifiers
 */
export const PrimitiveType = {
  LEGEND: 'legend',
  RANGE_SWITCHER: 'range-switcher',
  BUTTON: 'button',
  CUSTOM: 'custom'
} as const

import { BasePanePrimitive, BasePrimitiveConfig, PrimitivePriority } from './BasePanePrimitive'
import {
  ButtonColors,
  ButtonDimensions,
  ButtonEffects,
  ButtonSpacing
} from './PrimitiveDefaults'
import { PrimitiveStylingUtils, BaseStyleConfig } from './PrimitiveStylingUtils'

/**
 * Button types for different behaviors
 */
export type ButtonType = 'collapse' | 'toggle' | 'action' | 'custom'

/**
 * Button states
 */
export interface ButtonState {
  /**
   * Whether button is pressed/active
   */
  pressed?: boolean

  /**
   * Whether button is disabled
   */
  disabled?: boolean

  /**
   * Custom state data
   */
  customData?: any
}

/**
 * Configuration for ButtonPrimitive
 */
export interface ButtonPrimitiveConfig extends BasePrimitiveConfig {
  /**
   * Button type
   */
  buttonType: ButtonType

  /**
   * Button text or HTML content
   */
  content: string

  /**
   * Initial button state
   */
  initialState?: ButtonState

  /**
   * Whether this is a pane-specific button (vs chart-level)
   */
  isPanePrimitive?: boolean

  /**
   * Pane ID for pane-specific buttons
   */
  paneId?: number

  /**
   * Click handler
   */
  onClick?: (state: ButtonState, primitive: ButtonPrimitive) => void

  /**
   * State change handler
   */
  onStateChange?: (newState: ButtonState, oldState: ButtonState, primitive: ButtonPrimitive) => void

  /**
   * Button styling
   */
  style?: BasePrimitiveConfig['style'] & {
    /**
     * Button-specific styling
     */
    button?: {
      width?: number
      height?: number
      backgroundColor?: string
      color?: string
      hoverBackgroundColor?: string
      hoverColor?: string
      pressedBackgroundColor?: string
      pressedColor?: string
      disabledBackgroundColor?: string
      disabledColor?: string
      border?: string
      borderRadius?: number
      fontSize?: number
      fontWeight?: string | number
      cursor?: string
      transition?: string
      boxShadow?: string
      hoverBoxShadow?: string
      pressedBoxShadow?: string
    }

    /**
     * Icon styling (if using icons)
     */
    icon?: {
      size?: number
      color?: string
      hoverColor?: string
      pressedColor?: string
      disabledColor?: string
    }
  }
}

/**
 * ButtonPrimitive - A lightweight-charts pane primitive for interactive buttons
 *
 * This primitive provides:
 * - Interactive buttons with state management
 * - Collapse/expand functionality for panes
 * - Toggle buttons for features
 * - Action buttons for custom operations
 * - Full styling control and state feedback
 * - Pane-specific or chart-level positioning
 *
 * Example usage:
 * ```typescript
 * // Collapse button for a pane
 * const collapseButton = new ButtonPrimitive('collapse-btn', {
 *   corner: 'top-right',
 *   priority: PrimitivePriority.MINIMIZE_BUTTON,
 *   buttonType: 'collapse',
 *   content: '',
 *   isPanePrimitive: true,
 *   paneId: 1,
 *   onClick: (state, primitive) => {
 *     // Toggle pane visibility
 *     const newState = { pressed: !state.pressed }
 *     primitive.setState(newState)
 *     // Handle pane collapse logic
 *   }
 * })
 *
 * // Toggle button for a feature
 * const toggleButton = new ButtonPrimitive('toggle-btn', {
 *   corner: 'top-left',
 *   buttonType: 'toggle',
 *   content: '',
 *   onClick: (state, primitive) => {
 *     primitive.setState({ pressed: !state.pressed })
 *   }
 * })
 * ```
 */
export class ButtonPrimitive extends BasePanePrimitive<ButtonPrimitiveConfig> {

  private currentState: ButtonState = {}
  private buttonElement: HTMLElement | null = null

  constructor(id: string, config: ButtonPrimitiveConfig) {
    // Set default priority and configuration for buttons
    const configWithDefaults: ButtonPrimitiveConfig = {
      priority: config.buttonType === 'collapse' ? PrimitivePriority.MINIMIZE_BUTTON : PrimitivePriority.CUSTOM,
      visible: true,
      isPanePrimitive: config.buttonType === 'collapse',
      paneId: 0,
      initialState: { pressed: false, disabled: false },
      style: {
        backgroundColor: 'transparent',
        padding: ButtonSpacing.CONTAINER_PADDING,
        button: {
          width: ButtonDimensions.DEFAULT_WIDTH,
          height: ButtonDimensions.DEFAULT_HEIGHT,
          backgroundColor: ButtonColors.DEFAULT_BACKGROUND,
          color: ButtonColors.DEFAULT_COLOR,
          hoverBackgroundColor: ButtonColors.HOVER_BACKGROUND,
          hoverColor: ButtonColors.HOVER_COLOR,
          pressedBackgroundColor: ButtonColors.PRESSED_BACKGROUND,
          pressedColor: ButtonColors.PRESSED_COLOR,
          disabledBackgroundColor: ButtonColors.DISABLED_BACKGROUND,
          disabledColor: ButtonColors.DISABLED_COLOR,
          border: ButtonEffects.DEFAULT_BORDER,
          borderRadius: ButtonDimensions.BORDER_RADIUS,
          fontSize: ButtonDimensions.FONT_SIZE,
          fontWeight: 'bold',
          cursor: 'pointer',
          transition: ButtonEffects.DEFAULT_TRANSITION,
          boxShadow: 'none',
          hoverBoxShadow: ButtonEffects.HOVER_BOX_SHADOW,
          pressedBoxShadow: ButtonEffects.PRESSED_BOX_SHADOW
        },
        ...config.style
      },
      ...config
    }

    super(id, configWithDefaults)
    this.currentState = { ...configWithDefaults.initialState }
  }

  // ===== BasePanePrimitive Implementation =====

  /**
   * Get the template string (not used for interactive elements)
   */
  protected getTemplate(): string {
    return '' // Button is fully interactive, no template needed
  }

  /**
   * Render the button element
   */
  protected renderContent(): void {
    if (!this.containerElement) return

    // Clear existing content
    this.containerElement.innerHTML = ''

    // Create button element
    this.buttonElement = document.createElement('button')
    this.buttonElement.className = 'primitive-button'
    this.buttonElement.innerHTML = this.config.content
    this.buttonElement.setAttribute('aria-label', this.getAriaLabel())

    // Apply styling
    this.updateButtonStyling()

    // Update content based on state (for collapse buttons)
    this.updateContentBasedOnState()

    // Update CSS classes based on state (for collapse buttons)
    this.updateCSSClassesBasedOnState()

    // Add event handlers
    this.setupButtonEventHandlers()

    this.containerElement.appendChild(this.buttonElement)
  }

  /**
   * Get aria label for accessibility
   */
  private getAriaLabel(): string {
    switch (this.config.buttonType) {
      case 'collapse':
        return this.currentState.pressed ? 'Expand pane' : 'Collapse pane'
      case 'toggle':
        return this.currentState.pressed ? 'Disable feature' : 'Enable feature'
      case 'action':
        return 'Execute action'
      default:
        return 'Button'
    }
  }

  /**
   * Setup button event handlers
   */
  private setupButtonEventHandlers(): void {
    if (!this.buttonElement) return

    this.attachClickHandlers()
    this.attachMouseInteractionHandlers()
    this.attachKeyboardHandlers()
    this.attachFocusHandlers()
  }

  /**
   * Attach click event handlers
   */
  private attachClickHandlers(): void {
    if (!this.buttonElement) return

    this.buttonElement.addEventListener('click', (e) => {
      e.preventDefault()
      e.stopPropagation()
      this.handleClick()
    })
  }

  /**
   * Attach mouse interaction handlers (hover, press)
   */
  private attachMouseInteractionHandlers(): void {
    if (!this.buttonElement) return

    // Hover handlers
    this.buttonElement.addEventListener('mouseenter', () => {
      this.updateButtonStyling(true, false)
    })

    this.buttonElement.addEventListener('mouseleave', () => {
      this.updateButtonStyling(false, false)
    })

    // Mouse down/up for pressed effect
    this.buttonElement.addEventListener('mousedown', () => {
      this.updateButtonStyling(false, true)
    })

    this.buttonElement.addEventListener('mouseup', () => {
      this.updateButtonStyling(false, false)
    })
  }

  /**
   * Attach keyboard interaction handlers
   */
  private attachKeyboardHandlers(): void {
    if (!this.buttonElement) return

    this.buttonElement.addEventListener('keydown', (e) => {
      if (e.key === 'Enter' || e.key === ' ') {
        e.preventDefault()
        this.handleClick()
      }
    })
  }

  /**
   * Attach focus/blur handlers for accessibility
   */
  private attachFocusHandlers(): void {
    if (!this.buttonElement) return

    this.buttonElement.addEventListener('focus', () => {
      this.buttonElement!.style.outline = ButtonEffects.FOCUS_OUTLINE
    })

    this.buttonElement.addEventListener('blur', () => {
      this.buttonElement!.style.outline = 'none'
    })
  }

  /**
   * Handle button click
   */
  private handleClick(): void {
    if (this.currentState.disabled) return

    // For toggle and collapse buttons, automatically toggle pressed state
    if (this.config.buttonType === 'toggle' || this.config.buttonType === 'collapse') {
      this.setState({ pressed: !this.currentState.pressed })
    }

    // Call onClick handler
    if (this.config.onClick) {
      this.config.onClick(this.currentState, this)
    }

    // Emit custom event
    if (this.eventManager) {
      this.eventManager.emitCustomEvent('buttonClick', {
        buttonId: this.id,
        buttonType: this.config.buttonType,
        state: this.currentState
      })
    }
  }

  /**
   * Update button styling based on state
   */
  private updateButtonStyling(isHover: boolean = false, isMouseDown: boolean = false): void {
    if (!this.buttonElement) return

    const buttonConfig = this.config.style?.button
    if (!buttonConfig) return

    this.applyBaseButtonStyling(buttonConfig)
    this.applyButtonStateStyles(buttonConfig, isHover, isMouseDown)
  }

  /**
   * Apply base button styling (dimensions, layout, transitions)
   */
  private applyBaseButtonStyling(buttonConfig: NonNullable<ButtonPrimitiveConfig['style']>['button']): void {
    if (!this.buttonElement || !buttonConfig) return

    // Create standardized style configuration
    const baseStyles: BaseStyleConfig = {
      fontSize: buttonConfig.fontSize || ButtonDimensions.FONT_SIZE,
      fontWeight: buttonConfig.fontWeight || 'bold',
      border: buttonConfig.border || 'none',
      borderRadius: buttonConfig.borderRadius || ButtonDimensions.BORDER_RADIUS,
      transition: buttonConfig.transition || ButtonEffects.DEFAULT_TRANSITION
    }

    // Apply standardized base styles
    PrimitiveStylingUtils.applyBaseStyles(this.buttonElement, baseStyles)

    // Apply layout using standardized utilities
    PrimitiveStylingUtils.applyLayout(this.buttonElement, {
      width: buttonConfig.width || ButtonDimensions.DEFAULT_WIDTH,
      height: buttonConfig.height || ButtonDimensions.DEFAULT_HEIGHT
    })

    // Create flex container with standardized utilities
    PrimitiveStylingUtils.createFlexContainer(this.buttonElement, 'row', 'center', 'center')

    // Apply specific padding for collapse buttons (pane action buttons need zero padding)
    if (this.config.buttonType === 'collapse') {
      this.buttonElement.style.padding = ButtonSpacing.PANE_ACTION_PADDING
    }
  }

  /**
   * Apply state-specific button styling (colors, shadows, cursor)
   */
  private applyButtonStateStyles(
    buttonConfig: NonNullable<ButtonPrimitiveConfig['style']>['button'],
    isHover: boolean,
    isMouseDown: boolean
  ): void {
    if (!this.buttonElement || !buttonConfig) return

    const style = this.buttonElement.style

    if (this.currentState.disabled) {
      this.applyDisabledButtonStyle(style, buttonConfig)
    } else if (this.currentState.pressed || isMouseDown) {
      this.applyPressedButtonStyle(style, buttonConfig)
    } else if (isHover) {
      this.applyHoverButtonStyle(style, buttonConfig)
    } else {
      this.applyDefaultButtonStyle(style, buttonConfig)
    }
  }

  /**
   * Apply disabled button styling
   */
  private applyDisabledButtonStyle(
    style: CSSStyleDeclaration,
    buttonConfig: NonNullable<ButtonPrimitiveConfig['style']>['button']
  ): void {
    if (!buttonConfig || !this.buttonElement) return

    const baseStyles: BaseStyleConfig = {
      backgroundColor: buttonConfig.backgroundColor || ButtonColors.DEFAULT_BACKGROUND,
      color: buttonConfig.color || ButtonColors.DEFAULT_COLOR
    }

    const disabledStyles: BaseStyleConfig = {
      backgroundColor: buttonConfig.disabledBackgroundColor || ButtonColors.DISABLED_BACKGROUND,
      color: buttonConfig.disabledColor || ButtonColors.DISABLED_COLOR,
      boxShadow: 'none'
    }

    PrimitiveStylingUtils.applyInteractionState(this.buttonElement, baseStyles, disabledStyles, 'disabled')
  }

  /**
   * Apply pressed/active button styling
   */
  private applyPressedButtonStyle(
    style: CSSStyleDeclaration,
    buttonConfig: NonNullable<ButtonPrimitiveConfig['style']>['button']
  ): void {
    if (!buttonConfig || !this.buttonElement) return

    const baseStyles: BaseStyleConfig = {
      backgroundColor: buttonConfig.backgroundColor || ButtonColors.DEFAULT_BACKGROUND,
      color: buttonConfig.color || ButtonColors.DEFAULT_COLOR,
      cursor: buttonConfig.cursor || 'pointer'
    }

    const pressedStyles: BaseStyleConfig = {
      backgroundColor: buttonConfig.pressedBackgroundColor || ButtonColors.PRESSED_BACKGROUND,
      color: buttonConfig.pressedColor || ButtonColors.PRESSED_COLOR
    }

    PrimitiveStylingUtils.applyInteractionState(this.buttonElement, baseStyles, pressedStyles, 'active')
    PrimitiveStylingUtils.applyShadow(this.buttonElement, {
      boxShadow: buttonConfig.pressedBoxShadow || ButtonEffects.PRESSED_BOX_SHADOW
    })
  }

  /**
   * Apply hover button styling
   */
  private applyHoverButtonStyle(
    style: CSSStyleDeclaration,
    buttonConfig: NonNullable<ButtonPrimitiveConfig['style']>['button']
  ): void {
    if (!buttonConfig || !this.buttonElement) return

    const baseStyles: BaseStyleConfig = {
      backgroundColor: buttonConfig.backgroundColor || ButtonColors.DEFAULT_BACKGROUND,
      color: buttonConfig.color || ButtonColors.DEFAULT_COLOR,
      cursor: buttonConfig.cursor || 'pointer'
    }

    const hoverStyles: BaseStyleConfig = {
      backgroundColor: buttonConfig.hoverBackgroundColor || ButtonColors.HOVER_BACKGROUND,
      color: buttonConfig.hoverColor || ButtonColors.HOVER_COLOR
    }

    PrimitiveStylingUtils.applyInteractionState(this.buttonElement, baseStyles, hoverStyles, 'hover')
    PrimitiveStylingUtils.applyShadow(this.buttonElement, {
      boxShadow: buttonConfig.hoverBoxShadow || ButtonEffects.HOVER_BOX_SHADOW
    })
  }

  /**
   * Apply default button styling
   */
  private applyDefaultButtonStyle(
    style: CSSStyleDeclaration,
    buttonConfig: NonNullable<ButtonPrimitiveConfig['style']>['button']
  ): void {
    if (!buttonConfig || !this.buttonElement) return

    const baseStyles: BaseStyleConfig = {
      backgroundColor: buttonConfig.backgroundColor || ButtonColors.DEFAULT_BACKGROUND,
      color: buttonConfig.color || ButtonColors.DEFAULT_COLOR,
      cursor: buttonConfig.cursor || 'pointer'
    }

    PrimitiveStylingUtils.applyInteractionState(this.buttonElement, baseStyles, {}, 'default')
    PrimitiveStylingUtils.applyShadow(this.buttonElement, {
      boxShadow: buttonConfig.boxShadow || 'none'
    })
  }

  /**
   * Get CSS class name for the container
   */
  protected getContainerClassName(): string {
    return `button-primitive button-${this.config.buttonType}`
  }

  /**
   * Override pane ID for pane-specific buttons
   */
  protected getPaneId(): number {
    if (this.config.isPanePrimitive && this.config.paneId !== undefined) {
      return this.config.paneId
    }
    return 0 // Default to chart-level
  }

  // ===== Lifecycle Hooks =====

  /**
   * Called when container is created
   */
  protected onContainerCreated(container: HTMLElement): void {
    // Ensure container allows pointer events for button
    container.style.pointerEvents = 'auto'
  }

  // ===== Public API =====

  /**
   * Set button state
   */
  public setState(newState: Partial<ButtonState>): void {
    const oldState = { ...this.currentState }
    this.currentState = { ...this.currentState, ...newState }

    // Update styling
    this.updateButtonStyling()

    // Update content based on state (for collapse buttons)
    this.updateContentBasedOnState()

    // Update CSS classes based on state (for collapse buttons)
    this.updateCSSClassesBasedOnState()

    // Update aria label
    if (this.buttonElement) {
      this.buttonElement.setAttribute('aria-label', this.getAriaLabel())
    }

    // Call state change handler
    if (this.config.onStateChange) {
      this.config.onStateChange(this.currentState, oldState, this)
    }

    // Emit state change event
    if (this.eventManager) {
      this.eventManager.emitCustomEvent('buttonStateChange', {
        buttonId: this.id,
        buttonType: this.config.buttonType,
        newState: this.currentState,
        oldState: oldState
      })
    }
  }

  /**
   * Get current button state
   */
  public getState(): ButtonState {
    return { ...this.currentState }
  }

  /**
   * Set button content
   */
  public setContent(content: string): void {
    this.config.content = content
    if (this.buttonElement) {
      this.buttonElement.innerHTML = content
    }
  }

  /**
   * Update button content based on state (for collapse buttons)
   */
  private updateContentBasedOnState(): void {
    if (this.config.buttonType === 'collapse' && this.buttonElement) {
      // SVG for uncollapsed state (showing both brackets)
      const uncollapseIcon = '<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 15 15" width="15" height="15" fill="none"><path stroke="currentColor" d="m4 5 3.5-3L11 5" class="bracket-up"></path><path stroke="currentColor" d="M11 10l-3.5 3L4 10" class="bracket-down"></path></svg>'

      // SVG for collapsed state (showing single bracket pointing down)
      const collapseIcon = '<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 15 15" width="15" height="15" fill="none"><path stroke="currentColor" d="M11 10l-3.5 3L4 10" class="bracket-down"></path></svg>'

      // Update content based on pressed state
      this.buttonElement.innerHTML = this.currentState.pressed ? collapseIcon : uncollapseIcon
    }
  }

  /**
   * Update CSS classes based on button state (for collapse buttons)
   */
  private updateCSSClassesBasedOnState(): void {
    if (this.config.buttonType === 'collapse' && this.buttonElement) {
      // Add or remove 'collapsed' class based on pressed state
      if (this.currentState.pressed) {
        this.buttonElement.classList.add('collapsed')
      } else {
        this.buttonElement.classList.remove('collapsed')
      }
    }
  }

  /**
   * Enable/disable button
   */
  public setEnabled(enabled: boolean): void {
    this.setState({ disabled: !enabled })
  }

  /**
   * Check if button is pressed
   */
  public isPressed(): boolean {
    return this.currentState.pressed || false
  }

  /**
   * Check if button is disabled
   */
  public isDisabled(): boolean {
    return this.currentState.disabled || false
  }

  /**
   * Programmatically trigger click
   */
  public click(): void {
    this.handleClick()
  }

  /**
   * Set pressed state (for toggle/collapse buttons)
   */
  public setPressed(pressed: boolean): void {
    this.setState({ pressed })
  }
}

/**
 * Factory function to create button primitives
 */
export function createButtonPrimitive(
  id: string,
  config: Partial<ButtonPrimitiveConfig> & { buttonType: ButtonType; content: string; corner: any }
): ButtonPrimitive {
  return new ButtonPrimitive(id, config as ButtonPrimitiveConfig)
