/**
 * Architecture Validation Framework for Primitives
 *
 * Validates architectural constraints and patterns at runtime
 * to ensure compliance with design principles.
 */

export enum ValidationSeverity {
  WARNING = 'warning',
  ERROR = 'error',
  CRITICAL = 'critical'
}

export interface ValidationResult {
  valid: boolean
  violations: ValidationViolation[]
  score: number // 0-100
}

export interface ValidationViolation {
  rule: string
  severity: ValidationSeverity
  message: string
  context: Record<string, any>
  suggestion?: string
}

export interface ArchitecturalRule {
  name: string
  description: string
  severity: ValidationSeverity
  validate(primitive: any, context: ValidationContext): ValidationViolation[]
}

export interface ValidationContext {
  allPrimitives: Map<string, any>
  serviceRegistry: any
  chartInstance: any
}

/**
 * Validates architectural compliance of primitives
 */
export class PrimitiveArchitectureValidator {
  private static instance: PrimitiveArchitectureValidator
  private rules: ArchitecturalRule[] = []

  static getInstance(): PrimitiveArchitectureValidator {
    if (!this.instance) {
      this.instance = new PrimitiveArchitectureValidator()
      this.instance.registerDefaultRules()
    }
    return this.instance
  }

  /**
   * Register a new architectural rule
   */
  registerRule(rule: ArchitecturalRule): void {
    this.rules.push(rule)
  }

  /**
   * Validate a single primitive
   */
  validatePrimitive(primitive: any, context: ValidationContext): ValidationResult {
    const violations: ValidationViolation[] = []

    for (const rule of this.rules) {
      try {
        const ruleViolations = rule.validate(primitive, context)
        violations.push(...ruleViolations)
      } catch (error) {
        violations.push({
          rule: rule.name,
          severity: ValidationSeverity.ERROR,
          message: `Rule validation failed: ${error.message}`,
          context: { error: error.message }
        })
      }
    }

    return {
      valid: violations.length === 0,
      violations,
      score: this.calculateScore(violations)
    }
  }

  /**
   * Validate all primitives in a system
   */
  validateSystem(context: ValidationContext): ValidationResult {
    const allViolations: ValidationViolation[] = []

    for (const [id, primitive] of context.allPrimitives) {
      const result = this.validatePrimitive(primitive, context)
      allViolations.push(...result.violations)
    }

    return {
      valid: allViolations.length === 0,
      violations: allViolations,
      score: this.calculateScore(allViolations)
    }
  }

  /**
   * Calculate compliance score based on violations
   */
  private calculateScore(violations: ValidationViolation[]): number {
    if (violations.length === 0) return 100

    const severityWeights = {
      [ValidationSeverity.WARNING]: 1,
      [ValidationSeverity.ERROR]: 3,
      [ValidationSeverity.CRITICAL]: 5
    }

    const totalPenalty = violations.reduce((sum, v) => sum + severityWeights[v.severity], 0)
    const maxPenalty = violations.length * severityWeights[ValidationSeverity.CRITICAL]

    return Math.max(0, 100 - (totalPenalty / maxPenalty) * 100)
  }

  /**
   * Register default architectural rules
   */
  private registerDefaultRules(): void {
    // Rule 1: Single Responsibility - Primitives should have one clear purpose
    this.registerRule({
      name: 'single-responsibility',
      description: 'Primitives should have a single, well-defined responsibility',
      severity: ValidationSeverity.ERROR,
      validate: (primitive, context) => {
        const violations: ValidationViolation[] = []

        // Check if primitive has too many public methods (indication of multiple responsibilities)
        const publicMethods = Object.getOwnPropertyNames(Object.getPrototypeOf(primitive))
          .filter(name => !name.startsWith('_') && typeof primitive[name] === 'function')

        if (publicMethods.length > 15) {
          violations.push({
            rule: 'single-responsibility',
            severity: ValidationSeverity.WARNING,
            message: `Primitive has ${publicMethods.length} public methods, consider decomposition`,
            context: { methodCount: publicMethods.length, methods: publicMethods }
          })
        }

        return violations
      }
    })

    // Rule 2: Configuration Consistency
    this.registerRule({
      name: 'configuration-consistency',
      description: 'Primitives should use standardized configuration patterns',
      severity: ValidationSeverity.ERROR,
      validate: (primitive, context) => {
        const violations: ValidationViolation[] = []

        // Check if primitive has proper configuration interface
        if (!primitive.config || typeof primitive.config !== 'object') {
          violations.push({
            rule: 'configuration-consistency',
            severity: ValidationSeverity.CRITICAL,
            message: 'Primitive missing configuration object',
            context: { primitiveId: primitive.id },
            suggestion: 'Implement proper configuration interface extending BasePrimitiveConfig'
          })
        }

        // Check if configuration uses standardized constants
        if (primitive.config?.style) {
          const style = primitive.config.style
          const hardcodedValues = this.findHardcodedValues(style)

          if (hardcodedValues.length > 0) {
            violations.push({
              rule: 'configuration-consistency',
              severity: ValidationSeverity.WARNING,
              message: 'Configuration contains hardcoded values',
              context: { hardcodedValues },
              suggestion: 'Replace hardcoded values with constants from PrimitiveDefaults'
            })
          }
        }

        return violations
      }
    })

    // Rule 3: Memory Management
    this.registerRule({
      name: 'memory-management',
      description: 'Primitives should properly manage memory and cleanup resources',
      severity: ValidationSeverity.CRITICAL,
      validate: (primitive, context) => {
        const violations: ValidationViolation[] = []

        // Check for cleanup methods
        if (typeof primitive.detached !== 'function') {
          violations.push({
            rule: 'memory-management',
            severity: ValidationSeverity.CRITICAL,
            message: 'Primitive missing detached() cleanup method',
            context: { primitiveId: primitive.id },
            suggestion: 'Implement detached() method to cleanup resources'
          })
        }

        // Check for event cleanup
        if (primitive.eventSubscriptions && Array.isArray(primitive.eventSubscriptions)) {
          if (primitive.eventSubscriptions.length > 0 && typeof primitive.cleanupEventSubscriptions !== 'function') {
            violations.push({
              rule: 'memory-management',
              severity: ValidationSeverity.ERROR,
              message: 'Primitive has event subscriptions but no cleanup method',
              context: { subscriptionCount: primitive.eventSubscriptions.length },
              suggestion: 'Implement cleanupEventSubscriptions() method'
            })
          }
        }

        return violations
      }
    })

    // Rule 4: Service Dependency Management
    this.registerRule({
      name: 'service-dependencies',
      description: 'Primitives should properly manage service dependencies',
      severity: ValidationSeverity.ERROR,
      validate: (primitive, context) => {
        const violations: ValidationViolation[] = []

        // Check service initialization order
        const services = ['layoutManager', 'coordinateService', 'templateEngine', 'eventManager']
        const initializeServices = primitive.initializeServices

        if (typeof initializeServices === 'function') {
          // Services should be initialized in dependency order
          // This is a simplified check - in practice you'd analyze the actual dependencies
        }

        return violations
      }
    })

    // Rule 5: Error Handling Coverage
    this.registerRule({
      name: 'error-handling',
      description: 'Primitives should have comprehensive error handling',
      severity: ValidationSeverity.WARNING,
      validate: (primitive, context) => {
        const violations: ValidationViolation[] = []

        // Check critical methods have error handling
        const criticalMethods = ['attached', 'detached', 'renderContent', 'updateConfig']

        for (const methodName of criticalMethods) {
          if (typeof primitive[methodName] === 'function') {
            const methodSource = primitive[methodName].toString()

            // Simple check for try-catch blocks
            if (!methodSource.includes('try') || !methodSource.includes('catch')) {
              violations.push({
                rule: 'error-handling',
                severity: ValidationSeverity.WARNING,
                message: `Critical method ${methodName} lacks error handling`,
                context: { method: methodName },
                suggestion: 'Add try-catch blocks or use @withErrorBoundary decorator'
              })
            }
          }
        }

        return violations
      }
    })

    // Rule 6: Styling Standardization
    this.registerRule({
      name: 'styling-standardization',
      description: 'Primitives should use standardized styling utilities',
      severity: ValidationSeverity.ERROR,
      validate: (primitive, context) => {
        const violations: ValidationViolation[] = []

        // Check if primitive uses PrimitiveStylingUtils
        const className = primitive.constructor.name
        const source = primitive.constructor.toString()

        if (!source.includes('PrimitiveStylingUtils')) {
          violations.push({
            rule: 'styling-standardization',
            severity: ValidationSeverity.ERROR,
            message: 'Primitive not using standardized styling utilities',
            context: { className },
            suggestion: 'Migrate to use PrimitiveStylingUtils for consistent styling'
          })
        }

        return violations
      }
    })
  }

  /**
   * Find hardcoded values in configuration
   */
  private findHardcodedValues(obj: any, path: string = ''): string[] {
    const hardcoded: string[] = []

    if (typeof obj === 'string') {
      // Check for common hardcoded patterns
      const patterns = [
        /^\d+px$/,           // "12px"
        /^rgba?\([^)]+\)$/,  // "rgb(255,0,0)"
        /^#[0-9a-f]{3,6}$/i, // "#fff" or "#ffffff"
        /^(none|auto|pointer|default)$/ // Common CSS values
      ]

      for (const pattern of patterns) {
        if (pattern.test(obj)) {
          hardcoded.push(`${path}: "${obj}"`)
          break
        }
      }
    } else if (typeof obj === 'object' && obj !== null) {
      for (const [key, value] of Object.entries(obj)) {
        const newPath = path ? `${path}.${key}` : key
        hardcoded.push(...this.findHardcodedValues(value, newPath))
      }
    }

    return hardcoded
  }
}

/**
 * Validation utilities
 */
export const ArchitectureValidator = {
  /**
   * Quick validation of a primitive
   */
  validate: (primitive: any, context?: Partial<ValidationContext>): ValidationResult => {
    const validator = PrimitiveArchitectureValidator.getInstance()
    const fullContext: ValidationContext = {
      allPrimitives: new Map([[primitive.id, primitive]]),
      serviceRegistry: null,
      chartInstance: null,
      ...context
    }
    return validator.validatePrimitive(primitive, fullContext)
  },

  /**
   * Generate compliance report
   */
  generateReport: (result: ValidationResult): string => {
    const { valid, violations, score } = result

    let report = `Architecture Compliance Report\n`
    report += `Score: ${score}/100\n`
    report += `Status: ${valid ? 'PASS' : 'FAIL'}\n\n`

    if (violations.length > 0) {
      report += `Violations (${violations.length}):\n`
      violations.forEach((v, i) => {
        report += `${i + 1}. [${v.severity.toUpperCase()}] ${v.rule}: ${v.message}\n`
        if (v.suggestion) {
          report += `   Suggestion: ${v.suggestion}\n`
        }
      })
    } else {
      report += `No violations found. Excellent architectural compliance!\n`
    }

    return report
  }
}

/**
 * Error Boundary System for Primitives
 *
 * Provides centralized error handling, recovery mechanisms,
 * and error reporting for the primitive system.
 */

export enum ErrorSeverity {
  LOW = 'low',
  MEDIUM = 'medium',
  HIGH = 'high',
  CRITICAL = 'critical'
}

export enum ErrorCategory {
  INITIALIZATION = 'initialization',
  RENDERING = 'rendering',
  EVENT_HANDLING = 'event_handling',
  SERVICE_COMMUNICATION = 'service_communication',
  CONFIGURATION = 'configuration',
  LIFECYCLE = 'lifecycle'
}

export interface PrimitiveError {
  id: string
  primitiveId: string
  primitiveType: string
  category: ErrorCategory
  severity: ErrorSeverity
  message: string
  stack?: string
  context?: Record<string, any>
  timestamp: number
  recoverable: boolean
}

export interface ErrorRecoveryStrategy {
  canRecover(error: PrimitiveError): boolean
  recover(error: PrimitiveError, primitive: any): Promise<boolean>
}

/**
 * Centralized error boundary for primitive operations
 */
export class PrimitiveErrorBoundary {
  private static instance: PrimitiveErrorBoundary
  private errors: Map<string, PrimitiveError> = new Map()
  private recoveryStrategies: ErrorRecoveryStrategy[] = []
  private errorListeners: ((error: PrimitiveError) => void)[] = []

  static getInstance(): PrimitiveErrorBoundary {
    if (!this.instance) {
      this.instance = new PrimitiveErrorBoundary()
      this.instance.registerDefaultRecoveryStrategies()
    }
    return this.instance
  }

  /**
   * Register an error recovery strategy
   */
  registerRecoveryStrategy(strategy: ErrorRecoveryStrategy): void {
    this.recoveryStrategies.push(strategy)
  }

  /**
   * Listen for errors
   */
  onError(listener: (error: PrimitiveError) => void): () => void {
    this.errorListeners.push(listener)
    return () => {
      const index = this.errorListeners.indexOf(listener)
      if (index > -1) {
        this.errorListeners.splice(index, 1)
      }
    }
  }

  /**
   * Safely execute an operation with error boundary
   */
  async safeExecute<T>(
    primitiveId: string,
    primitiveType: string,
    operation: () => T | Promise<T>,
    category: ErrorCategory,
    context?: Record<string, any>
  ): Promise<T | null> {
    try {
      const result = await operation()
      return result
    } catch (error) {
      const primitiveError = this.createError(
        primitiveId,
        primitiveType,
        category,
        error,
        context
      )

      return this.handleError(primitiveError, operation)
    }
  }

  /**
   * Handle an error with recovery attempts
   */
  private async handleError(
    error: PrimitiveError,
    operation?: any
  ): Promise<any> {
    // Store error
    this.errors.set(error.id, error)

    // Notify listeners
    this.errorListeners.forEach(listener => listener(error))

    // Log error based on severity
    this.logError(error)

    // Attempt recovery if possible
    if (error.recoverable) {
      for (const strategy of this.recoveryStrategies) {
        if (strategy.canRecover(error)) {
          const recovered = await strategy.recover(error, operation)
          if (recovered) {
            // Mark error as recovered
            error.context = { ...error.context, recovered: true }
            return null // Recovery successful, return null to indicate handled
          }
        }
      }
    }

    // If critical and not recoverable, re-throw
    if (error.severity === ErrorSeverity.CRITICAL && !error.recoverable) {
      throw new Error(`Critical primitive error: ${error.message}`)
    }

    return null
  }

  /**
   * Create a primitive error object
   */
  private createError(
    primitiveId: string,
    primitiveType: string,
    category: ErrorCategory,
    originalError: any,
    context?: Record<string, any>
  ): PrimitiveError {
    const severity = this.determineSeverity(category, originalError)
    const recoverable = this.isRecoverable(category, severity)

    return {
      id: `${primitiveId}_${Date.now()}_${Math.random().toString(36).slice(2)}`,
      primitiveId,
      primitiveType,
      category,
      severity,
      message: originalError?.message || String(originalError),
      stack: originalError?.stack,
      context: context || {},
      timestamp: Date.now(),
      recoverable
    }
  }

  /**
   * Determine error severity based on category and error type
   */
  private determineSeverity(category: ErrorCategory, error: any): ErrorSeverity {
    // Critical errors that break functionality
    if (category === ErrorCategory.INITIALIZATION) {
      return ErrorSeverity.CRITICAL
    }

    // High priority errors
    if (category === ErrorCategory.RENDERING || category === ErrorCategory.LIFECYCLE) {
      return ErrorSeverity.HIGH
    }

    // Medium priority errors
    if (category === ErrorCategory.SERVICE_COMMUNICATION) {
      return ErrorSeverity.MEDIUM
    }

    // Low priority errors (can be ignored)
    return ErrorSeverity.LOW
  }

  /**
   * Determine if an error is recoverable
   */
  private isRecoverable(category: ErrorCategory, severity: ErrorSeverity): boolean {
    // Initialization errors are typically not recoverable
    if (category === ErrorCategory.INITIALIZATION) {
      return false
    }

    // Critical errors are typically not recoverable
    if (severity === ErrorSeverity.CRITICAL) {
      return false
    }

    // Most other errors can be recovered from
    return true
  }

  /**
   * Log error with appropriate level
   */
  private logError(error: PrimitiveError): void {
    const logMessage = `[${error.severity.toUpperCase()}] ${error.primitiveType}(${error.primitiveId}): ${error.message}`

    switch (error.severity) {
      case ErrorSeverity.CRITICAL:
        console.error(logMessage, error)
        break
      case ErrorSeverity.HIGH:
        console.error(logMessage, error.context)
        break
      case ErrorSeverity.MEDIUM:
        console.warn(logMessage, error.context)
        break
      case ErrorSeverity.LOW:
        console.info(logMessage)
        break
    }
  }

  /**
   * Register default recovery strategies
   */
  private registerDefaultRecoveryStrategies(): void {
    // DOM recreation strategy
    this.registerRecoveryStrategy({
      canRecover: (error) => error.category === ErrorCategory.RENDERING,
      recover: async (error, primitive) => {
        try {
          // Attempt to recreate container element
          if (primitive && typeof primitive.recreateContainer === 'function') {
            await primitive.recreateContainer()
            return true
          }
        } catch {
          return false
        }
        return false
      }
    })

    // Event handler reset strategy
    this.registerRecoveryStrategy({
      canRecover: (error) => error.category === ErrorCategory.EVENT_HANDLING,
      recover: async (error, primitive) => {
        try {
          // Reset event handlers
          if (primitive && typeof primitive.resetEventHandlers === 'function') {
            await primitive.resetEventHandlers()
            return true
          }
        } catch {
          return false
        }
        return false
      }
    })

    // Service reconnection strategy
    this.registerRecoveryStrategy({
      canRecover: (error) => error.category === ErrorCategory.SERVICE_COMMUNICATION,
      recover: async (error, primitive) => {
        try {
          // Attempt service reconnection
          if (primitive && typeof primitive.reconnectServices === 'function') {
            await primitive.reconnectServices()
            return true
          }
        } catch {
          return false
        }
        return false
      }
    })
  }

  /**
   * Get error statistics
   */
  getErrorStats(): {
    total: number
    bySeverity: Record<ErrorSeverity, number>
    byCategory: Record<ErrorCategory, number>
    recovered: number
  } {
    const errors = Array.from(this.errors.values())

    return {
      total: errors.length,
      bySeverity: Object.values(ErrorSeverity).reduce((acc, severity) => {
        acc[severity] = errors.filter(e => e.severity === severity).length
        return acc
      }, {} as Record<ErrorSeverity, number>),
      byCategory: Object.values(ErrorCategory).reduce((acc, category) => {
        acc[category] = errors.filter(e => e.category === category).length
        return acc
      }, {} as Record<ErrorCategory, number>),
      recovered: errors.filter(e => e.context?.recovered).length
    }
  }
}

/**
 * Decorator for automatic error boundary wrapping
 */
export function withErrorBoundary(
  category: ErrorCategory,
  context?: Record<string, any>
) {
  return function (target: any, propertyKey: string, descriptor: PropertyDescriptor) {
    const originalMethod = descriptor.value

    descriptor.value = async function (...args: any[]) {
      const boundary = PrimitiveErrorBoundary.getInstance()
      const primitiveId = this.id || 'unknown'
      const primitiveType = this.constructor.name

      return boundary.safeExecute(
        primitiveId,
        primitiveType,
        () => originalMethod.apply(this, args),
        category,
        context
      )
    }

    return descriptor
  }
}

import { BasePanePrimitive, BasePrimitiveConfig, PrimitivePriority } from './BasePanePrimitive'
import { Time } from 'lightweight-charts'
import {
  TimeRangeSeconds,
  DefaultRangeSwitcherConfig,
  ButtonColors,
  ButtonDimensions,
  ButtonEffects,
  ButtonSpacing,
  CommonValues
} from './PrimitiveDefaults'
import { PrimitiveStylingUtils, BaseStyleConfig } from './PrimitiveStylingUtils'

/**
 * Predefined time range values for easy configuration
 */
export enum TimeRange {
  FIVE_MINUTES = 'FIVE_MINUTES',
  FIFTEEN_MINUTES = 'FIFTEEN_MINUTES',
  THIRTY_MINUTES = 'THIRTY_MINUTES',
  ONE_HOUR = 'ONE_HOUR',
  FOUR_HOURS = 'FOUR_HOURS',
  ONE_DAY = 'ONE_DAY',
  ONE_WEEK = 'ONE_WEEK',
  TWO_WEEKS = 'TWO_WEEKS',
  ONE_MONTH = 'ONE_MONTH',
  THREE_MONTHS = 'THREE_MONTHS',
  SIX_MONTHS = 'SIX_MONTHS',
  ONE_YEAR = 'ONE_YEAR',
  TWO_YEARS = 'TWO_YEARS',
  FIVE_YEARS = 'FIVE_YEARS',
  ALL = 'ALL'
}

/**
 * Range configuration for time switching
 * Supports both enum values and custom seconds for flexibility
 */
export interface RangeConfig {
  /**
   * Display text for the range
   */
  text: string

  /**
   * Time range - can be enum value or custom seconds
   * Use TimeRange enum for predefined ranges, or number for custom seconds
   * Use null or TimeRange.ALL for "All" range
   */
  range: TimeRange | number | null

  /**
   * @deprecated Use 'range' instead. This is kept for backwards compatibility.
   */
  seconds?: number | null

}

/**
 * Get the range value from a RangeConfig, supporting both new and legacy formats
 */
export function getRangeValue(rangeConfig: RangeConfig): TimeRange | number | null {
  // Support new 'range' property first
  if (rangeConfig.range !== undefined) {
    return rangeConfig.range
  }
  // Fall back to legacy 'seconds' property for backwards compatibility
  return rangeConfig.seconds || null
}

/**
 * Check if a range represents "All" (show all data)
 */
export function isAllRange(rangeConfig: RangeConfig): boolean {
  const range = getRangeValue(rangeConfig)
  return range === null || range === TimeRange.ALL
}

/**
 * Convert TimeRange enum or value to seconds
 */
export function getSecondsFromRange(range: TimeRange | number | null): number | null {
  if (range === null || range === TimeRange.ALL) {
    return null
  }

  if (typeof range === 'number') {
    return range
  }

  // Map enum values to seconds using the existing TimeRangeSeconds constants
  switch (range) {
    case TimeRange.FIVE_MINUTES:
      return TimeRangeSeconds.FIVE_MINUTES
    case TimeRange.FIFTEEN_MINUTES:
      return TimeRangeSeconds.FIFTEEN_MINUTES
    case TimeRange.THIRTY_MINUTES:
      return 1800 // 30 minutes
    case TimeRange.ONE_HOUR:
      return TimeRangeSeconds.ONE_HOUR
    case TimeRange.FOUR_HOURS:
      return TimeRangeSeconds.FOUR_HOURS
    case TimeRange.ONE_DAY:
      return TimeRangeSeconds.ONE_DAY
    case TimeRange.ONE_WEEK:
      return TimeRangeSeconds.ONE_WEEK
    case TimeRange.TWO_WEEKS:
      return TimeRangeSeconds.ONE_WEEK * 2
    case TimeRange.ONE_MONTH:
      return TimeRangeSeconds.ONE_MONTH
    case TimeRange.THREE_MONTHS:
      return TimeRangeSeconds.THREE_MONTHS
    case TimeRange.SIX_MONTHS:
      return TimeRangeSeconds.SIX_MONTHS
    case TimeRange.ONE_YEAR:
      return TimeRangeSeconds.ONE_YEAR
    case TimeRange.TWO_YEARS:
      return TimeRangeSeconds.ONE_YEAR * 2
    case TimeRange.FIVE_YEARS:
      return TimeRangeSeconds.FIVE_YEARS
    default:
      return null
  }
}

/**
 * Configuration for RangeSwitcherPrimitive
 */
export interface RangeSwitcherPrimitiveConfig extends BasePrimitiveConfig {
  /**
   * Available time ranges
   */
  ranges: RangeConfig[]

  /**
   * Callback when range changes
   */
  onRangeChange?: (range: RangeConfig, index: number) => void

  /**
   * Range switcher styling
   */
  style?: BasePrimitiveConfig['style'] & {
    /**
     * Button styling
     */
    button?: {
      backgroundColor?: string
      color?: string
      hoverBackgroundColor?: string
      hoverColor?: string
      border?: string
      borderRadius?: number
      padding?: string
      margin?: string
      fontSize?: number
      fontWeight?: string | number
      minWidth?: number
    }

    /**
     * Container styling
     */
    container?: {
      display?: 'flex' | 'block'
      flexDirection?: 'row' | 'column'
      gap?: number
      alignItems?: string
      justifyContent?: string
    }
  }
}

/**
 * RangeSwitcherPrimitive - A lightweight-charts pane primitive for time range switching
 *
 * This primitive provides:
 * - Interactive time range buttons (1D, 7D, 1M, 3M, 1Y, All)
 * - Chart-level positioning (typically top-right corner)
 * - Automatic chart time scale updates
 * - Configurable styling and ranges
 * - Event integration for range changes
 *
 * Example usage:
 * ```typescript
 * const rangeSwitcher = new RangeSwitcherPrimitive('range-switcher', {
 *   corner: 'top-right',
 *   priority: PrimitivePriority.RANGE_SWITCHER,
 *   ranges: [
 *     { text: '1D', seconds: 86400 },
 *     { text: '7D', seconds: 604800 },
 *     { text: '1M', seconds: 2592000 },
 *     { text: 'All', seconds: null }
 *   ],
 *   onRangeChange: (range) => {
 *     // Range changed to: range.text
 *   }
 * })
 *
 * // Add to chart (chart-level, not pane-specific)
 * chart.attachPrimitive(rangeSwitcher)
 * ```
 */
export class RangeSwitcherPrimitive extends BasePanePrimitive<RangeSwitcherPrimitiveConfig> {

  private buttonElements: HTMLElement[] = []
  private buttonEventCleanupFunctions: (() => void)[] = []
  private dataTimespan: number | null = null // Cached data timespan in seconds
  private initialVisibilitySetupComplete: boolean = false // Track if initial setup is done
  private dataChangeIntervalId: NodeJS.Timeout | null = null // Store interval ID for cleanup

  constructor(id: string, config: RangeSwitcherPrimitiveConfig) {
    // Set default priority and configuration for range switchers
    const configWithDefaults: RangeSwitcherPrimitiveConfig = {
      priority: PrimitivePriority.RANGE_SWITCHER,
      visible: true,
      style: {
        backgroundColor: 'transparent',
        padding: DefaultRangeSwitcherConfig.layout.CONTAINER_PADDING,
        container: {
          display: 'flex',
          flexDirection: DefaultRangeSwitcherConfig.layout.FLEX_DIRECTION,
          gap: DefaultRangeSwitcherConfig.layout.CONTAINER_GAP,
          alignItems: DefaultRangeSwitcherConfig.layout.ALIGN_ITEMS,
          justifyContent: DefaultRangeSwitcherConfig.layout.JUSTIFY_CONTENT
        },
        button: {
          backgroundColor: ButtonColors.DEFAULT_BACKGROUND,
          color: ButtonColors.DEFAULT_COLOR,
          hoverBackgroundColor: ButtonColors.HOVER_BACKGROUND,
          hoverColor: ButtonColors.HOVER_COLOR,
          border: ButtonEffects.DEFAULT_BORDER,
          borderRadius: ButtonDimensions.BORDER_RADIUS,
          padding: ButtonSpacing.RANGE_BUTTON_PADDING,
          margin: '0 2px',
          fontSize: ButtonDimensions.RANGE_FONT_SIZE,
          fontWeight: 500,
          minWidth: ButtonDimensions.MIN_WIDTH_RANGE
        },
        ...config.style
      },
      ...config
    }

    super(id, configWithDefaults)
  }

  // ===== BasePanePrimitive Implementation =====

  /**
   * Get the template string (not used for interactive elements)
   */
  protected getTemplate(): string {
    return '' // Range switcher is fully interactive, no template needed
  }

  /**
   * Render the range switcher buttons
   */
  protected renderContent(): void {
    if (!this.containerElement) return

    // If buttons are already rendered, don't recreate them
    if (this.buttonElements.length > 0) {
      return
    }

    // Clean up existing event listeners
    this.cleanupButtonEventListeners()

    // Clear existing content
    this.containerElement.innerHTML = ''
    this.buttonElements = []

    // Create container for buttons
    const buttonContainer = document.createElement('div')
    buttonContainer.className = 'range-switcher-container'
    this.applyContainerStyling(buttonContainer)

    // Create buttons for each range (filtering is now done server-side)
    this.config.ranges.forEach((range, index) => {
      const button = this.createRangeButton(range, index)
      buttonContainer.appendChild(button)
      this.buttonElements.push(button)
    })

    this.containerElement.appendChild(buttonContainer)

    // Trigger layout recalculation after content is rendered to ensure proper positioning
    // Use setTimeout to allow DOM to update dimensions first
    setTimeout(() => {
      if (this.layoutManager) {
        this.layoutManager.recalculateAllLayouts()
      }
    }, 0)
  }

  /**
   * Create a single range button
   */
  private createRangeButton(range: RangeConfig, index: number): HTMLElement {
    const button = this.createButtonElement(range, index)
    this.applyButtonStyling(button, false)
    this.attachButtonEventHandlers(button, index)
    return button
  }

  /**
   * Create the basic button element with attributes
   */
  private createButtonElement(range: RangeConfig, index: number): HTMLElement {
    const button = document.createElement('button')
    button.className = 'range-button'
    button.textContent = range.text
    button.setAttribute('data-range-index', index.toString())
    button.setAttribute('aria-label', `Switch to ${range.text} time range`)

    // Add data attributes for debugging and testing
    const rangeValue = getRangeValue(range)
    const seconds = getSecondsFromRange(rangeValue)
    if (seconds !== null) {
      button.setAttribute('data-range-seconds', seconds.toString())
    }

    return button
  }

  /**
   * Attach event handlers to range button
   */
  private attachButtonEventHandlers(button: HTMLElement, index: number): void {
    const eventHandlers = this.createButtonEventHandlers(button, index)

    // Add event listeners
    button.addEventListener('click', eventHandlers.click)
    button.addEventListener('mouseenter', eventHandlers.mouseEnter)
    button.addEventListener('mouseleave', eventHandlers.mouseLeave)

    // Store cleanup function
    const cleanup = () => {
      button.removeEventListener('click', eventHandlers.click)
      button.removeEventListener('mouseenter', eventHandlers.mouseEnter)
      button.removeEventListener('mouseleave', eventHandlers.mouseLeave)
    }
    this.buttonEventCleanupFunctions.push(cleanup)
  }

  /**
   * Create event handler functions for range button
   */
  private createButtonEventHandlers(button: HTMLElement, index: number): {
    click: (e: Event) => void;
    mouseEnter: () => void;
    mouseLeave: () => void;
  } {
    return {
      click: (e: Event) => {
        e.preventDefault()
        e.stopPropagation()
        this.handleRangeClick(index)
      },
      mouseEnter: () => {
        this.applyButtonStyling(button, false, true)
      },
      mouseLeave: () => {
        this.applyButtonStyling(button, false, false)
      }
    }
  }

  /**
   * Clean up button event listeners
   */
  private cleanupButtonEventListeners(): void {
    this.buttonEventCleanupFunctions.forEach(cleanup => cleanup())
    this.buttonEventCleanupFunctions = []
  }

  /**
   * Apply container styling
   */
  private applyContainerStyling(container: HTMLElement): void {
    const style = container.style
    const containerConfig = this.config.style?.container

    if (containerConfig) {
      if (containerConfig.display) style.display = containerConfig.display
      if (containerConfig.flexDirection) style.flexDirection = containerConfig.flexDirection
      if (containerConfig.gap) style.gap = `${containerConfig.gap}px`
      if (containerConfig.alignItems) style.alignItems = containerConfig.alignItems
      if (containerConfig.justifyContent) style.justifyContent = containerConfig.justifyContent
    }

    // Ensure interactive elements can receive events
    style.pointerEvents = 'auto'
  }

  /**
   * Apply button styling using standardized utilities
   */
  private applyButtonStyling(button: HTMLElement, isActive: boolean, isHover: boolean = false): void {
    const buttonConfig = this.config.style?.button

    if (buttonConfig) {
      // Prepare base styles with compact, professional appearance
      const baseStyles: BaseStyleConfig = {
        border: buttonConfig.border || ButtonEffects.RANGE_BORDER,
        borderRadius: buttonConfig.borderRadius || 4, // Rounded corners for modern look
        padding: buttonConfig.padding || ButtonSpacing.RANGE_BUTTON_PADDING,
        margin: buttonConfig.margin || ButtonSpacing.RANGE_BUTTON_MARGIN,
        fontSize: buttonConfig.fontSize || 11, // Slightly smaller font for compactness
        fontWeight: buttonConfig.fontWeight || CommonValues.FONT_WEIGHT_MEDIUM,
        backgroundColor: buttonConfig.backgroundColor || 'rgba(255, 255, 255, 0.9)',
        color: buttonConfig.color || '#666',
        cursor: CommonValues.POINTER,
        transition: ButtonEffects.DEFAULT_TRANSITION,
        boxShadow: '0 1px 2px rgba(0, 0, 0, 0.05)' // Subtle shadow for depth
      }

      // Prepare state-specific styles
      const stateStyles: BaseStyleConfig = {}

      if (isHover) {
        stateStyles.backgroundColor = buttonConfig.hoverBackgroundColor || 'rgba(255, 255, 255, 1)'
        stateStyles.color = buttonConfig.hoverColor || '#333'
        stateStyles.boxShadow = ButtonEffects.RANGE_HOVER_BOX_SHADOW
        stateStyles.transform = 'translateY(-1px)' // Subtle lift effect
      }

      // Determine state for styling utils
      const state = isHover ? 'hover' : 'default'

      // Apply styles using standardized utilities
      PrimitiveStylingUtils.applyInteractionState(button, baseStyles, stateStyles, state)

      // Set minimum width if specified
      if (buttonConfig.minWidth) {
        button.style.minWidth = `${buttonConfig.minWidth}px`
      }
    }
  }

  /**
   * Handle range button click
   */
  private handleRangeClick(index: number): void {

    // Apply range to chart
    this.applyRangeToChart(this.config.ranges[index])

    // Emit range change event
    if (this.config.onRangeChange) {
      this.config.onRangeChange(this.config.ranges[index], index)
    }

    // Emit custom event through event manager
    if (this.eventManager) {
      this.eventManager.emitCustomEvent('rangeChange', {
        range: this.config.ranges[index],
        index: index
      })
    }
  }


  /**
   * Apply range to chart time scale
   */
  private applyRangeToChart(range: RangeConfig): void {
    if (!this.chart) return

    try {
      const timeScale = this.chart.timeScale()

      const rangeValue = getRangeValue(range)
      const seconds = getSecondsFromRange(rangeValue)

      if (seconds === null) {
        // "All" range - fit all content
        timeScale.fitContent()
      } else {
        // Specific time range - use current visible range or current time
        const currentRange = timeScale.getVisibleRange()
        let endTime: number

        if (currentRange && currentRange.to) {
          // Use the current visible end time as reference
          endTime = currentRange.to as number
        } else {
          // Fallback to current time
          endTime = Date.now() / 1000
        }

        const fromTime = endTime - seconds

        timeScale.setVisibleRange({
          from: fromTime as Time,
          to: endTime as Time
        })
      }
    } catch (error) {
      // Silently handle chart range application errors
    }
  }

  /**
   * Get the timespan of available data in seconds
   */
  private getDataTimespan(): number | null {
    if (!this.chart) return null

    try {
      const timeScale = this.chart.timeScale()

      // Store current visible range to restore it later
      const currentRange = timeScale.getVisibleRange()

      // Always use fitContent to get the full data range for button visibility decisions
      // This ensures buttons are hidden based on total data availability, not current zoom level
      timeScale.fitContent()
      const fullRange = timeScale.getVisibleRange()

      // Restore the original range immediately to avoid interfering with user view
      if (currentRange) {
        timeScale.setVisibleRange(currentRange)
      }

      if (!fullRange || !fullRange.from || !fullRange.to) return null

      // Calculate timespan in seconds
      const timespanSeconds = (fullRange.to as number) - (fullRange.from as number)

      // Cache the result for performance
      this.dataTimespan = timespanSeconds

      return timespanSeconds
    } catch (error) {
      // Return null if we can't determine data timespan
      return null
    }
  }

  /**
   * Check if a range is valid for the current data
   */
  private isRangeValidForData(range: RangeConfig): boolean {
    const rangeValue = getRangeValue(range)

    // "All" range is always valid
    if (isAllRange(range)) {
      return true
    }

    const rangeSeconds = getSecondsFromRange(rangeValue)
    if (rangeSeconds === null) {
      return true // Unknown ranges are considered valid
    }

    const dataTimespan = this.getDataTimespan()
    if (dataTimespan === null) {
      return true // If we can't determine data timespan, show all ranges
    }

    // Hide ranges that are significantly larger than available data
    // Add a 10% buffer to account for minor timing differences
    const bufferMultiplier = 1.1
    return rangeSeconds <= (dataTimespan * bufferMultiplier)
  }

  /**
   * Get visible ranges based on data availability
   */
  private getVisibleRanges(): Array<{ range: RangeConfig; originalIndex: number }> {
    return this.config.ranges
      .map((range, index) => ({ range, originalIndex: index }))
      .filter(({ range }) => this.isRangeValidForData(range))
  }

  /**
   * Get CSS class name for the container
   */
  protected getContainerClassName(): string {
    return 'range-switcher-primitive'
  }

  /**
   * Override pane ID - range switcher is chart-level (pane 0)
   */
  protected getPaneId(): number {
    return 0 // Always chart-level
  }

  // ===== Lifecycle Hooks =====

  /**
   * Override detached to ensure proper cleanup
   */
  public detached(): void {
    this.cleanupButtonEventListeners()
    super.detached()
  }

  /**
   * Setup custom event subscriptions
   */
  protected setupCustomEventSubscriptions(): void {
    if (!this.eventManager) return

    // REMOVED: timeScale subscription that was hiding buttons on every zoom
    // const timeScaleSub = this.eventManager.subscribe('timeScaleChange', (event) => {
    //   this.handleTimeScaleChange(event)
    // })
    // this.eventSubscriptions.push(timeScaleSub)

    // Subscribe to data updates to refresh range visibility (but not timeScale changes)
    const dataUpdateSub = this.eventManager.subscribe('dataUpdate', () => {
      this.handleDataUpdate()
    })
    this.eventSubscriptions.push(dataUpdateSub)
  }

  /**
   * Handle external time scale changes
   */
  private handleTimeScaleChange(event: { from: any; to: any }): void {
    // Optionally sync active range when time scale changes externally
    // This could be used to highlight which range matches the current view
  }

  /**
   * Handle data updates that might affect range visibility
   * Only processes during initial setup, not after user interactions
   */
  private handleDataUpdate(): void {
    if (this.mounted && !this.initialVisibilitySetupComplete) {
      this.invalidateDataTimespan()
      // Update button visibility without full re-render
      this.updateRangeButtonVisibility()
    }
  }

  /**
   * Update range button visibility based on current data
   * Only hides buttons during initial setup, not after user interactions
   */
  private updateRangeButtonVisibility(): void {
    // Only hide buttons during initial setup, not after user interactions
    if (this.initialVisibilitySetupComplete) {
      return
    }

    // Check each range and update button visibility
    this.config.ranges.forEach((range, index) => {
      const button = this.buttonElements[index]
      if (button) {
        if (this.isRangeValidForData(range)) {
          button.style.display = '' // Show the button
          button.removeAttribute('data-hidden-reason')
        } else {
          button.style.display = 'none' // Hide the button
          button.setAttribute('data-hidden-reason', 'exceeds-data-range')
        }
      }
    })
  }

  /**
   * Called when container is created
   */
  protected onContainerCreated(container: HTMLElement): void {
    // Ensure container allows pointer events for buttons
    container.style.pointerEvents = 'auto'

    // Set up mutation observer to detect data changes
    this.setupDataChangeObserver()
  }

  /**
   * Set up observer to detect chart data changes
   */
  private setupDataChangeObserver(): void {
    if (!this.chart) return

    // Use a timeout to periodically check for data changes during initial setup only
    // This is more reliable than trying to intercept all possible data update events
    const checkDataChanges = () => {
      if (this.mounted && !this.initialVisibilitySetupComplete) {
        const currentTimespan = this.getDataTimespan()
        if (currentTimespan !== this.dataTimespan) {
          this.updateRangeButtonVisibility()
        }
      }
    }

    // Check every 1 second for data changes (only during initial setup)
    this.dataChangeIntervalId = setInterval(checkDataChanges, 1000)
  }

  // ===== Public API =====

  /**
   * Add a new range
   */
  public addRange(range: RangeConfig): void {
    this.config.ranges.push(range)
    if (this.mounted) {
      this.invalidateDataTimespan() // Clear cache when ranges change
      this.renderContent() // Full re-render needed for new buttons
    }
  }

  /**
   * Remove a range by index
   */
  public removeRange(index: number): void {
    if (index < 0 || index >= this.config.ranges.length) return

    this.config.ranges.splice(index, 1)


    if (this.mounted) {
      this.renderContent()
    }
  }

  /**
   * Update ranges
   */
  public updateRanges(ranges: RangeConfig[]): void {
    this.config.ranges = ranges

    if (this.mounted) {
      this.invalidateDataTimespan() // Clear cache when ranges change
      this.renderContent() // Full re-render needed for new button set
    }
  }


  /**
   * Invalidate cached data timespan (call when data changes)
   */
  public invalidateDataTimespan(): void {
    this.dataTimespan = null
  }

  /**
   * Get the current data timespan in seconds
   */
  public getDataTimespanSeconds(): number | null {
    return this.getDataTimespan()
  }

  /**
   * Force update of range button visibility
   * Useful when called externally after data changes
   */
  public updateButtonVisibility(): void {
    if (this.mounted && !this.initialVisibilitySetupComplete) {
      this.invalidateDataTimespan()
      this.updateRangeButtonVisibility()
    }
  }

  /**
   * Get information about hidden ranges
   */
  public getHiddenRanges(): Array<{ range: RangeConfig; index: number; reason: string }> {
    const hiddenRanges: Array<{ range: RangeConfig; index: number; reason: string }> = []

    this.config.ranges.forEach((range, index) => {
      if (!this.isRangeValidForData(range)) {
        hiddenRanges.push({
          range,
          index,
          reason: 'exceeds-data-range'
        })
      }
    })

    return hiddenRanges
  }

  /**
   * Get information about visible ranges
   */
  public getVisibleRangeInfo(): Array<{ range: RangeConfig; index: number; dataTimespan: number | null }> {
    const dataTimespan = this.getDataTimespan()

    return this.config.ranges
      .map((range, index) => ({ range, index, dataTimespan }))
      .filter(({ range }) => this.isRangeValidForData(range))
  }


  /**
   * Programmatically trigger range change
   */
  public triggerRangeChange(index: number): void {
    this.handleRangeClick(index)
  }
}

