/**
 * Centralized service for managing chart coordinate calculations
 * Provides consistent positioning across all chart features
 */

import {IChartApi, ISeriesApi, Time} from 'lightweight-charts'
import {
  ChartCoordinates,
  PaneCoordinates,
  LegendCoordinates,
  ElementPosition,
  CoordinateOptions,
  CoordinateCacheEntry,
  BoundingBox,
  ScaleDimensions,
  ContainerDimensions,
  Margins
} from '../types/coordinates'
import {
  validateChartCoordinates,
  sanitizeCoordinates,
  createBoundingBox,
  areCoordinatesStale,
  logValidationResult
} from '../utils/coordinateValidation'
import {
  DIMENSIONS,
  TIMING,
  Z_INDEX,
  getFallback,
  getMargins
} from '../config/positioningConfig'
import {UniversalSpacing} from '../primitives/PrimitiveDefaults'

/**
 * Configuration for chart dimensions validation
 */
export interface ChartDimensionsOptions {
  minWidth?: number
  minHeight?: number
  maxAttempts?: number
  baseDelay?: number
}

/**
 * Configuration for pane dimensions options
 */
export interface PaneDimensionsOptions {
  includeMargins?: boolean
  includeScales?: boolean
  validateDimensions?: boolean
}

/**
 * Configuration for positioning calculations (from PositioningEngine)
 */
export interface PositioningConfig {
  margins?: Partial<Margins>
  dimensions?: {width?: number; height?: number}
  zIndex?: number
  alignment?: 'start' | 'center' | 'end'
  offset?: {x?: number; y?: number}
}

/**
 * Tooltip positioning configuration (from PositioningEngine)
 */
export interface TooltipPosition {
  x: number
  y: number
  anchor: 'top' | 'bottom' | 'left' | 'right'
  offset: {x: number; y: number}
}

/**
 * Singleton service for chart coordinate management
 */
export class ChartCoordinateService {
  private static instance: ChartCoordinateService
  private coordinateCache = new Map<string, CoordinateCacheEntry>()
  private paneDimensionsCache = new Map<
    string,
    {
      dimensions: {[paneId: number]: {width: number; height: number}}
      expiresAt: number
    }
  >()
  private chartRegistry = new Map<string, IChartApi>()
  private updateCallbacks = new Map<string, Set<() => void>>()

  /**
   * Get singleton instance
   */
  static getInstance(): ChartCoordinateService {
    if (!this.instance) {
      this.instance = new ChartCoordinateService()
    }
    return this.instance
  }

  private constructor() {
    // Private constructor for singleton
    this.startCacheCleanup()
  }

  /**
   * Register a chart for coordinate tracking
   */
  registerChart(chartId: string, chart: IChartApi): void {
    this.chartRegistry.set(chartId, chart)
    this.invalidateCache(chartId)
  }

  /**
   * Unregister a chart
   */
  unregisterChart(chartId: string): void {
    this.chartRegistry.delete(chartId)
    this.coordinateCache.delete(chartId)
    this.updateCallbacks.delete(chartId)
  }

  /**
   * Get coordinates for a chart with caching and validation
   */
  async getCoordinates(
    chart: IChartApi,
    container: HTMLElement,
    options: CoordinateOptions = {}
  ): Promise<ChartCoordinates> {
    const {
      includeMargins = true,
      useCache = true,
      validateResult = true,
      fallbackOnError = true
    } = options

    // Generate cache key
    const cacheKey = this.generateCacheKey(chart, container)

    // Check cache if enabled
    if (useCache) {
      const cached = this.coordinateCache.get(cacheKey)
      if (cached && !areCoordinatesStale(cached, TIMING.cacheExpiration)) {
        return cached
      }
    }

    try {
      // Calculate coordinates
      const coordinates = await this.calculateCoordinates(chart, container, includeMargins)

      // Validate if requested
      if (validateResult) {
        const validation = validateChartCoordinates(coordinates)
        logValidationResult(validation, 'ChartCoordinateService')

        if (!validation.isValid && fallbackOnError) {
          return sanitizeCoordinates(coordinates)
        }
      }

      // Cache the result
      const cacheEntry: CoordinateCacheEntry = {
        ...coordinates,
        cacheKey,
        expiresAt: Date.now() + TIMING.cacheExpiration
      }
      this.coordinateCache.set(cacheKey, cacheEntry)

      // Notify listeners
      this.notifyUpdateCallbacks(cacheKey)

      return coordinates
    } catch (error) {
      if (fallbackOnError) {
        return sanitizeCoordinates({})
      }

      throw error
    }
  }

  /**
   * Get full pane bounds including price scale areas (for collapse buttons)
   */
  getFullPaneBounds(chart: IChartApi, paneId: number): any | null {
    try {
      // Validate inputs
      if (!chart || typeof paneId !== 'number' || paneId < 0) {
        return null
      }

      // Get pane size from chart with error handling
      let paneSize: any = null
      try {
        paneSize = chart.paneSize(paneId)
      } catch (error) {
        return null
      }

      if (!paneSize || typeof paneSize.height !== 'number' || typeof paneSize.width !== 'number') {
        return null
      }

      // Calculate cumulative offset for this pane
      let offsetY = 0
      for (let i = 0; i < paneId; i++) {
        try {
          const size = chart.paneSize(i)
          if (size && typeof size.height === 'number') {
            offsetY += size.height
          }
        } catch (error) {
          // Continue with other panes even if one fails
        }
      }

      // Return full pane bounds including all price scale areas
      const paneWidth = paneSize.width || getFallback('paneWidth')
      return createBoundingBox(
        0, // Full pane starts at 0
        offsetY,
        paneWidth, // Full pane width including price scales
        paneSize.height
      )
    } catch (error) {
      return null
    }
  }

  /**
   * Get coordinates for a specific pane
   */
  getPaneCoordinates(chart: IChartApi, paneId: number): PaneCoordinates | null {
    try {
      // Validate inputs
      if (!chart || typeof paneId !== 'number' || paneId < 0) {
        return null
      }

      // Get pane size from chart with error handling
      let paneSize: any = null
      try {
        paneSize = chart.paneSize(paneId)
      } catch (error) {
        return null
      }

      if (!paneSize || typeof paneSize.height !== 'number' || typeof paneSize.width !== 'number') {
        return null
      }

      // Calculate cumulative offset for this pane
      let offsetY = 0
      for (let i = 0; i < paneId; i++) {
        try {
          const size = chart.paneSize(i)
          if (size && typeof size.height === 'number') {
            offsetY += size.height
          }
        } catch (error) {
          // Continue with other panes even if one fails
        }
      }


      // Get chart element for price scale width
      // Get chart element (used for price scale width calculation)
      const timeScaleHeight = this.getTimeScaleHeight(chart)

      // Get both left and right price scale widths for proper legend positioning
      const axisDimensions = this.getAxisDimensions(chart)

      // For legend positioning, we need coordinates relative to the chart element itself
      // The legend is appended to the chart element, so coordinates should be relative to it
      const legendOffsetX = 0 // Legend is positioned relative to chart element
      const legendOffsetY = offsetY // Y offset for multi-pane charts

      // Calculate bounds relative to chart element (for pane primitive positioning)
      // Pane primitives should get the full pane area without price scale adjustments
      const paneWidth = paneSize.width || getFallback('paneWidth')

      const bounds = createBoundingBox(
        legendOffsetX,
        legendOffsetY,
        paneWidth,  // Use full pane width - no price scale adjustment
        paneSize.height
      )


      // Calculate content area (excluding scales) relative to chart element
      // This is where the actual chart content starts (after price scale)
      // The left Y-axis (price scale) takes up priceScaleWidth pixels from the left
      const contentArea = createBoundingBox(
        axisDimensions.leftPriceScaleWidth, // Start after the left Y-axis (price scale)
        legendOffsetY,
        paneWidth - axisDimensions.leftPriceScaleWidth - axisDimensions.rightPriceScaleWidth, // Width excluding both price scales
        paneSize.height - (paneId === 0 ? 0 : timeScaleHeight)
      )

      // Get margins
      const margins = getMargins('pane')

      // Debug logging for legend positioning
      if (paneId === 0) {
        // Main pane - coordinates calculated
      }

      return {
        id: paneId,
        index: paneId,
        isMainPane: paneId === 0,
        bounds,
        contentArea,
        margins
      }
    } catch (error) {
      return null
    }
  }

  /**
   * Get pane coordinates with enhanced fallback methods
   */
  async getPaneCoordinatesWithFallback(
    chart: IChartApi,
    paneId: number,
    container: HTMLElement,
    options: PaneDimensionsOptions & ChartDimensionsOptions = {}
  ): Promise<PaneCoordinates | null> {
    const {...paneOptions} = options

    // Method 1: Try chart API first
    let paneCoords = this.getPaneCoordinates(chart, paneId)
    if (paneCoords) {
      return paneCoords
    }

    // Method 2: Single immediate retry without delay for resize performance
    paneCoords = this.getPaneCoordinates(chart, paneId)
    if (paneCoords) {
      return paneCoords
    }

    // Method 3: DOM fallback
    return this.getPaneCoordinatesFromDOM(chart, container, paneId, paneOptions)
  }

  /**
   * Get pane coordinates using DOM measurements (fallback method)
   */
  private getPaneCoordinatesFromDOM(
    chart: IChartApi,
    container: HTMLElement,
    paneId: number,
    options: PaneDimensionsOptions = {}
  ): PaneCoordinates | null {
    try {
      // Find pane elements in DOM
      const chartElement = chart.chartElement()
      if (!chartElement) {
        return null
      }

      const paneElements = chartElement.querySelectorAll('.tv-lightweight-charts-pane')
      if (paneElements.length <= paneId) {
        return null
      }

      const paneElement = paneElements[paneId] as HTMLElement
      const paneRect = paneElement.getBoundingClientRect()
      const chartRect = chartElement.getBoundingClientRect()

      // Calculate relative position within the chart container
      const offsetY = paneRect.top - chartRect.top
      const width = paneRect.width
      const height = paneRect.height

      // Validate dimensions if requested
      if (options.validateDimensions && (width < 10 || height < 10)) {
        return null
      }

      // For legend positioning, coordinates should be relative to the chart element
      // The legend is appended to the chart element, so we use 0 for x offset
      const legendOffsetX = 0
      const legendOffsetY = offsetY

      // Calculate bounds relative to chart element (for legend positioning)
      const bounds = createBoundingBox(legendOffsetX, legendOffsetY, width, height)

      // Calculate content area (excluding scales) relative to chart element
      const priceScaleWidth = this.getPriceScaleWidth(chart)
      const timeScaleHeight = this.getTimeScaleHeight(chart)
      // The left Y-axis (price scale) takes up priceScaleWidth pixels from the left
      const contentArea = createBoundingBox(
        priceScaleWidth, // Start after the left Y-axis (price scale)
        legendOffsetY,
        width - priceScaleWidth, // Width is the remaining area after price scale
        height - (paneId === 0 ? 0 : timeScaleHeight)
      )

      // Get margins
      const margins = getMargins('pane')

      return {
        id: paneId,
        index: paneId,
        isMainPane: paneId === 0,
        bounds,
        contentArea,
        margins
      }
    } catch (error) {
      return null
    }
  }

  /**
   * Check if a point is within a pane
   */
  isPointInPane(point: {x: number; y: number}, paneCoords: PaneCoordinates): boolean {
    const {bounds} = paneCoords
    return (
      point.x >= bounds.left &&
      point.x <= bounds.right &&
      point.y >= bounds.top &&
      point.y <= bounds.bottom
    )
  }

  /**
   * Check if chart dimensions are valid
   */
  areChartDimensionsValid(
    dimensions: ChartCoordinates,
    minWidth: number = 200,
    minHeight: number = 200
  ): boolean {
    try {
      const {container} = dimensions
      return container.width >= minWidth && container.height >= minHeight
    } catch (error) {
      return false
    }
  }

  /**
   * Check if chart dimensions object is valid
   */
  areChartDimensionsObjectValid(
    dimensions: {container: {width: number; height: number}},
    minWidth: number = 200,
    minHeight: number = 200
  ): boolean {
    try {
      const {container} = dimensions
      return container.width >= minWidth && container.height >= minHeight
    } catch (error) {
      return false
    }
  }

  /**
   * Get validated chart coordinates
   */
  async getValidatedCoordinates(
    chart: IChartApi,
    container: HTMLElement,
    options: ChartDimensionsOptions = {}
  ): Promise<ChartCoordinates | null> {
    try {
      const coordinates = await this.getCoordinates(chart, container, {
        validateResult: true
      })

      if (this.areChartDimensionsValid(coordinates, options.minWidth, options.minHeight)) {
        return coordinates
      } else {
        return null
      }
    } catch (error) {
      return null
    }
  }

  /**
   * Get chart dimensions with multiple fallback methods
   */
  async getChartDimensionsWithFallback(
    chart: IChartApi,
    container: HTMLElement,
    options: ChartDimensionsOptions = {}
  ): Promise<{
    container: {width: number; height: number}
    timeScale: {x: number; y: number; width: number; height: number}
    priceScale: {x: number; y: number; width: number; height: number}
  }> {
    const {minWidth = 200, minHeight = 200} = options

    // Method 1: Try chart API first
    try {
      const chartElement = chart.chartElement()
      if (chartElement) {
        const chartRect = chartElement.getBoundingClientRect()
        if (chartRect.width >= minWidth && chartRect.height >= minHeight) {
          return this.getChartDimensionsFromAPI(chart, {
            width: chartRect.width,
            height: chartRect.height
          })
        }
      }
    } catch (error) {
      // Chart API method failed, trying DOM fallback
    }

    // Method 2: DOM fallback
    try {
      const result = this.getChartDimensionsFromDOM(chart, container)
      if (result.container.width >= minWidth && result.container.height >= minHeight) {
        return result
      }
    } catch (error) {
      // DOM method failed, using defaults
    }

    // Method 3: Default values
    return this.getDefaultChartDimensions()
  }

  /**
   * Get chart dimensions using chart API (most accurate)
   */
  private getChartDimensionsFromAPI(
    chart: IChartApi,
    chartSize: {width: number; height: number}
  ): {
    container: {width: number; height: number}
    timeScale: {x: number; y: number; width: number; height: number}
    priceScale: {x: number; y: number; width: number; height: number}
  } {
    try {
      // Get time scale dimensions
      let timeScaleHeight = 35
      let timeScaleWidth = chartSize.width

      try {
        const timeScale = chart.timeScale()
        timeScaleHeight = timeScale.height() || 35
        timeScaleWidth = timeScale.width() || chartSize.width
      } catch (error) {
        // Time scale API failed, using defaults
      }

      // Get price scale width
      let priceScaleWidth = 70

      try {
        const priceScale = chart.priceScale('left')
        priceScaleWidth = priceScale.width() || 70
      } catch (error) {
        // Price scale API failed, using defaults
      }

      return {
        timeScale: {
          x: 0,
          y: chartSize.height - timeScaleHeight,
          height: timeScaleHeight,
          width: timeScaleWidth
        },
        priceScale: {
          x: 0,
          y: 0,
          height: chartSize.height - timeScaleHeight,
          width: priceScaleWidth
        },
        container: chartSize
      }
    } catch (error) {
      throw error
    }
  }

  /**
   * Get chart dimensions using DOM measurements (fallback method)
   */
  private getChartDimensionsFromDOM(
    chart: IChartApi,
    container: HTMLElement
  ): {
    container: {width: number; height: number}
    timeScale: {x: number; y: number; width: number; height: number}
    priceScale: {x: number; y: number; width: number; height: number}
  } {
    try {
      // Get container dimensions with multiple fallback methods
      let width = 0
      let height = 0

      // Method 1: getBoundingClientRect
      try {
        const rect = container.getBoundingClientRect()
        width = rect.width
        height = rect.height
      } catch (error) {
      }

      // Method 2: offset dimensions
      if (!width || !height) {
        width = container.offsetWidth
        height = container.offsetHeight
      }

      // Method 3: client dimensions
      if (!width || !height) {
        width = container.clientWidth
        height = container.clientHeight
      }

      // Method 4: scroll dimensions
      if (!width || !height) {
        width = container.scrollWidth
        height = container.scrollHeight
      }

      // Ensure minimum dimensions
      width = Math.max(width || 800, 200)
      height = Math.max(height || 600, 200)

      // Get time scale dimensions
      let timeScaleHeight = 35
      let timeScaleWidth = width

      try {
        const timeScale = chart.timeScale()
        timeScaleHeight = timeScale.height() || 35
        timeScaleWidth = timeScale.width() || width
      } catch (error) {
      }

      // Get price scale width
      let priceScaleWidth = 70

      try {
        const priceScale = chart.priceScale('left')
        priceScaleWidth = priceScale.width() || 70
      } catch (error) {
      }

      return {
        timeScale: {
          x: 0,
          y: height - timeScaleHeight,
          height: timeScaleHeight,
          width: timeScaleWidth
        },
        priceScale: {
          x: 0,
          y: 0,
          height: height - timeScaleHeight,
          width: priceScaleWidth
        },
        container: {width, height}
      }
    } catch (error) {
      throw error
    }
  }

  /**
   * Get default chart dimensions (last resort)
   */
  private getDefaultChartDimensions(): {
    container: {width: number; height: number}
    timeScale: {x: number; y: number; width: number; height: number}
    priceScale: {x: number; y: number; width: number; height: number}
  } {
    return {
      timeScale: {
        x: 0,
        y: 565, // 600 - 35
        height: 35,
        width: 800
      },
      priceScale: {
        x: 0,
        y: 0,
        height: 565, // 600 - 35
        width: 70
      },
      container: {
        width: 800,
        height: 600
      }
    }
  }

  /**
   * Get validated chart dimensions
   */
  async getValidatedChartDimensions(
    chart: IChartApi,
    container: HTMLElement,
    options: ChartDimensionsOptions = {}
  ): Promise<{
    container: {width: number; height: number}
    timeScale: {x: number; y: number; width: number; height: number}
    priceScale: {x: number; y: number; width: number; height: number}
  } | null> {
    try {
      const dimensions = await this.getChartDimensionsWithFallback(chart, container, options)

      if (this.areChartDimensionsObjectValid(dimensions, options.minWidth, options.minHeight)) {
        return dimensions
      } else {
        return null
      }
    } catch (error) {

      return null
    }
  }

  /**
   * Calculate range switcher position for the entire chart
   */
  getRangeSwitcherPosition(
    chart: IChartApi,
    position: ElementPosition,
    containerDimensions?: { width: number; height: number }
  ): LegendCoordinates | null {
    try {
      // Get main pane coordinates (pane 0) for reference
      const paneCoords = this.getPaneCoordinates(chart, 0)
      if (!paneCoords) return null

      // Get container dimensions
      const chartElement = chart.chartElement()
      const container = containerDimensions || {
        width: chartElement.clientWidth || chartElement.offsetWidth || 800,
        height: chartElement.clientHeight || chartElement.offsetHeight || 600
      }

      // Get chart layout to account for price scale and time scale dimensions
      try {
        chart.chartElement().querySelector('.tv-lightweight-charts')?.getBoundingClientRect()
      } catch (e) {
        // Layout check failed
      }

      // Get actual chart dimensions using lightweight-charts API
      let actualTimeScaleHeight = 35 // Fallback
      let actualPriceScaleWidth = 70 // Fallback

      try {
        // Get actual time scale height from chart API
        actualTimeScaleHeight = chart.timeScale().height()

        // Get actual price scale width - try right scale first, then left scale
        const rightPriceScale = chart.priceScale('right')
        if (rightPriceScale) {
          actualPriceScaleWidth = rightPriceScale.width()
        } else {
          const leftPriceScale = chart.priceScale('left')
          if (leftPriceScale) {
            actualPriceScaleWidth = leftPriceScale.width()
          }
        }
      } catch (e) {
        // Use fallback values if API calls fail
      }

      const priceScaleLabelHeight = 20 // Estimated height for price scale labels (e.g., "161.75")

      // Helper function to count total number of panes
      const getTotalPaneCount = (): number => {
        let paneCount = 0
        try {
          // Keep trying to get pane sizes until we find a non-existent pane
          while (true) {
            const paneSize = chart.paneSize(paneCount)
            if (!paneSize || typeof paneSize.height !== 'number') {
              break
            }
            paneCount++
          }
        } catch (error) {
          // When paneSize() throws an error, we've reached the end
        }
        return paneCount
      }

      const totalPanes = getTotalPaneCount()

      // Calculate position-specific margins
      const getMarginForPosition = (pos: string) => {
        const baseMargin = UniversalSpacing.EDGE_PADDING
        const margins = {
          top: baseMargin + priceScaleLabelHeight, // Add space for price scale labels at top
          right: baseMargin + actualPriceScaleWidth, // Add space for price scale width
          bottom: baseMargin, // Base margin for bottom
          left: baseMargin
        }

        // Only add time scale height margin for bottom positions when:
        // 1. It's a bottom position AND
        // 2. There's only one pane (single-pane chart where X-axis is at bottom of pane 0)
        // In multi-pane charts, the X-axis is only at the very bottom of the last pane, not pane 0
        if (pos.includes('bottom') && totalPanes === 1) {
          margins.bottom += actualTimeScaleHeight // X-axis height only for single-pane charts
        }

        return margins
      }

      const margins = getMarginForPosition(position)
      const rangeSwitcherDimensions = { width: 200, height: 40 } // Estimated dimensions

      let top = 0
      let left: number | undefined = 0
      let right: number | undefined
      let bottom: number | undefined

      // Calculate position based on alignment
      // Range switcher only supports corner positions
      // For bottom positions, position relative to pane 0 (main price chart)
      // For top positions, position relative to entire chart container
      switch (position) {
        case 'top-left':
          top = margins.top
          left = margins.left
          right = undefined
          break

        case 'top-right':
          top = margins.top
          left = undefined
          right = margins.right
          break

        case 'bottom-left':
          // Position at bottom of pane 0, not entire chart
          // This ensures range switcher is positioned at the bottom of the main price chart pane
          top = paneCoords.bounds.bottom - margins.bottom - rangeSwitcherDimensions.height
          left = margins.left
          right = undefined
          bottom = undefined
          break

        case 'bottom-right':
          // Position at bottom of pane 0, not entire chart
          // This ensures range switcher is positioned at the bottom of the main price chart pane
          top = paneCoords.bounds.bottom - margins.bottom - rangeSwitcherDimensions.height
          left = undefined
          right = margins.right
          bottom = undefined
          break

        default:
          // Default to bottom-right for range switcher
          top = paneCoords.bounds.bottom - margins.bottom - rangeSwitcherDimensions.height
          left = undefined
          right = margins.right
          break
      }

      // Convert bottom to top if needed
      if (bottom !== undefined && top === 0) {
        top = container.height - bottom - rangeSwitcherDimensions.height
        bottom = undefined
      }

      return {
        top,
        left,
        right,
        bottom,
        width: rangeSwitcherDimensions.width,
        height: rangeSwitcherDimensions.height,
        zIndex: 1000
      }
    } catch (error) {
      return null
    }
  }

  /**
   * Calculate legend position within a pane
   */
  getLegendPosition(
    chart: IChartApi,
    paneId: number,
    position: ElementPosition
  ): LegendCoordinates | null {
    const paneCoords = this.getPaneCoordinates(chart, paneId)
    if (!paneCoords) return null

    const margins = getMargins('legend')
    const legendDimensions = DIMENSIONS.legend

    let top = 0
    let left = 0
    let right: number | undefined
    let bottom: number | undefined

    // Calculate position based on alignment
    switch (position) {
      case 'top-left':
        top = paneCoords.contentArea.top + margins.top
        left = paneCoords.contentArea.left + margins.left
        break

      case 'top-right':
        top = paneCoords.contentArea.top + margins.top
        right = margins.right
        break

      case 'top-center':
        top = paneCoords.contentArea.top + margins.top
        left = paneCoords.contentArea.left + (paneCoords.contentArea.width - legendDimensions.defaultWidth) / 2
        break

      case 'bottom-left':
        bottom = margins.bottom
        left = paneCoords.contentArea.left + margins.left
        break

      case 'bottom-right':
        bottom = margins.bottom
        right = margins.right
        break

      case 'bottom-center':
        bottom = margins.bottom
        left = paneCoords.contentArea.left + (paneCoords.contentArea.width - legendDimensions.defaultWidth) / 2
        break

      case 'center':
        top =
          paneCoords.contentArea.top +
          (paneCoords.contentArea.height - legendDimensions.defaultHeight) / 2
        left =
          paneCoords.contentArea.left +
          (paneCoords.contentArea.width - legendDimensions.defaultWidth) / 2
        break
    }

    // Convert bottom to top if needed
    if (bottom !== undefined && top === 0) {
      top = bottom
      bottom = undefined
    }

    return {
      top,
      left,
      right,
      bottom,
      width: legendDimensions.defaultWidth,
      height: legendDimensions.defaultHeight,
      zIndex: Z_INDEX.legend
    }
  }

  /**
   * Subscribe to coordinate updates
   */
  onCoordinateUpdate(chartId: string, callback: () => void): () => void {
    if (!this.updateCallbacks.has(chartId)) {
      this.updateCallbacks.set(chartId, new Set())
    }

    this.updateCallbacks.get(chartId)!.add(callback)

    // Return unsubscribe function
    return () => {
      const callbacks = this.updateCallbacks.get(chartId)
      if (callbacks) {
        callbacks.delete(callback)
      }
    }
  }

  /**
   * Invalidate cache for a specific chart
   */
  invalidateCache(chartId?: string): void {
    if (chartId) {
      // Remove specific chart entries
      const keysToDelete: string[] = []
      this.coordinateCache.forEach((entry, key) => {
        if (key.includes(chartId)) {
          keysToDelete.push(key)
        }
      })
      keysToDelete.forEach(key => this.coordinateCache.delete(key))
    } else {
      // Clear all cache
      this.coordinateCache.clear()
    }
  }

  /**
   * Calculate coordinates for a chart
   */
  private async calculateCoordinates(
    chart: IChartApi,
    container: HTMLElement,
    includeMargins: boolean
  ): Promise<ChartCoordinates> {
    return new Promise(resolve => {
      // Use requestAnimationFrame for better performance
      requestAnimationFrame(() => {
        try {
          // Get container dimensions
          const containerDimensions = this.getContainerDimensions(container)

          // Get scale dimensions
          const timeScale = this.getTimeScaleDimensions(chart, containerDimensions)
          const priceScaleLeft = this.getPriceScaleDimensions(chart, 'left', containerDimensions)
          const priceScaleRight = this.getPriceScaleDimensions(chart, 'right', containerDimensions)

          // Get all panes
          const panes = this.getAllPaneCoordinates(chart)

          // Calculate content area
          const contentArea = this.calculateContentArea(
            containerDimensions,
            timeScale,
            priceScaleLeft,
            includeMargins
          )

          const coordinates: ChartCoordinates = {
            container: containerDimensions,
            timeScale,
            priceScaleLeft,
            priceScaleRight,
            panes,
            contentArea,
            timestamp: Date.now(),
            isValid: true
          }

          resolve(coordinates)
        } catch (error) {
          resolve(sanitizeCoordinates({}))
        }
      })
    })
  }

  /**
   * Get container dimensions
   */
  private getContainerDimensions(container: HTMLElement): ContainerDimensions {
    const rect = container.getBoundingClientRect()
    return {
      width: rect.width || container.offsetWidth || getFallback('containerWidth'),
      height: rect.height || container.offsetHeight || getFallback('containerHeight'),
      offsetTop: container.offsetTop || 0,
      offsetLeft: container.offsetLeft || 0
    }
  }

  /**
   * Get time scale dimensions
   */
  private getTimeScaleDimensions(
    chart: IChartApi,
    container: ContainerDimensions
  ): ScaleDimensions {
    try {
      const timeScale = chart.timeScale()
      const height = timeScale.height() || getFallback('timeScaleHeight')
      const width = timeScale.width() || container.width

      return {
        x: 0,
        y: container.height - height,
        width,
        height
      }
    } catch {
      return {
        x: 0,
        y: container.height - getFallback('timeScaleHeight'),
        width: container.width,
        height: getFallback('timeScaleHeight')
      }
    }
  }

  /**
   * Get price scale dimensions
   */
  private getPriceScaleDimensions(
    chart: IChartApi,
    side: 'left' | 'right',
    container: ContainerDimensions
  ): ScaleDimensions {
    try {
      const priceScale = chart.priceScale(side)
      const width = priceScale.width() || (side === 'left' ? getFallback('priceScaleWidth') : 0)

      return {
        x: side === 'left' ? 0 : container.width - width,
        y: 0,
        width,
        height: container.height - getFallback('timeScaleHeight')
      }
    } catch {
      const defaultWidth = side === 'left' ? getFallback('priceScaleWidth') : 0
      return {
        x: side === 'left' ? 0 : container.width - defaultWidth,
        y: 0,
        width: defaultWidth,
        height: container.height - getFallback('timeScaleHeight')
      }
    }
  }

  /**
   * Get all pane coordinates
   */
  private getAllPaneCoordinates(chart: IChartApi): PaneCoordinates[] {
    const panes: PaneCoordinates[] = []
    let paneIndex = 0
    // Track total height for future use (currently disabled)

    // Try to get panes until we hit an invalid one
    while (paneIndex < 10) {
      // Safety limit
      try {
        const paneSize = chart.paneSize(paneIndex)
        if (!paneSize) break

        const paneCoords = this.getPaneCoordinates(chart, paneIndex)
        if (paneCoords) {
          panes.push(paneCoords)
        }

        // Track total height for future use
        paneIndex++
      } catch {
        break
      }
    }

    // Ensure we have at least one pane
    if (panes.length === 0) {
      panes.push({
        id: 0,
        index: 0,
        isMainPane: true,
        bounds: createBoundingBox(0, 0, getFallback('paneWidth'), getFallback('paneHeight')),
        contentArea: createBoundingBox(
          getFallback('priceScaleWidth'),
          0,
          getFallback('paneWidth') - getFallback('priceScaleWidth'),
          getFallback('paneHeight') - getFallback('timeScaleHeight')
        ),
        margins: getMargins('pane')
      })
    }

    return panes
  }

  /**
   * Calculate content area
   */
  private calculateContentArea(
    container: ContainerDimensions,
    timeScale: ScaleDimensions,
    priceScaleLeft: ScaleDimensions,
    includeMargins: boolean
  ): BoundingBox {
    const margins = includeMargins ? getMargins('content') : {top: 0, right: 0, bottom: 0, left: 0}

    const x = priceScaleLeft.width + margins.left
    const y = margins.top
    const width = container.width - priceScaleLeft.width - margins.left - margins.right
    const height = container.height - timeScale.height - margins.top - margins.bottom

    return createBoundingBox(x, y, width, height)
  }

  /**
   * Get price scale width helper
   */
  private getPriceScaleWidth(chart: IChartApi, side: 'left' | 'right' = 'left'): number {
    try {
      const priceScale = chart.priceScale(side)
      const width = priceScale.width()

      // If width is 0 or undefined, the price scale is not visible
      if (!width || width === 0) {
        return 0
      }

      return width
    } catch {
      // If we can't access the price scale, assume it's not visible
      return 0
    }
  }

  /**
   * Get time scale height helper
   */
  private getTimeScaleHeight(chart: IChartApi): number {
    try {
      const timeScale = chart.timeScale()
      return timeScale.height() || getFallback('timeScaleHeight')
    } catch {
      return getFallback('timeScaleHeight')
    }
  }

  /**
   * Generate cache key
   */
  private generateCacheKey(chart: IChartApi, container: HTMLElement): string {
    const chartId = chart?.chartElement?.()?.id || 'unknown'
    const containerId = container?.id || 'unknown'
    return `${chartId}-${containerId}`
  }

  /**
   * Notify update callbacks
   */
  private notifyUpdateCallbacks(cacheKey: string): void {
    const chartId = cacheKey.split('-')[0]
    const callbacks = this.updateCallbacks.get(chartId)

    if (callbacks) {
      callbacks.forEach(callback => {
        try {
          callback()
        } catch (error) {
        }
      })
    }
  }

  /**
   * Start cache cleanup timer
   */
  private startCacheCleanup(): void {
    setInterval(() => {
      const now = Date.now()
      const keysToDelete: string[] = []
      this.coordinateCache.forEach((entry, key) => {
        if (entry.expiresAt < now) {
          keysToDelete.push(key)
        }
      })
      keysToDelete.forEach(key => this.coordinateCache.delete(key))
    }, TIMING.cacheExpiration)
  }

  /**
   * Get current pane dimensions for comparison
   */
  getCurrentPaneDimensions(chart: IChartApi): {
    [paneId: number]: {width: number; height: number}
  } {
    const dimensions: {[paneId: number]: {width: number; height: number}} = {}
    let paneIndex = 0

    while (paneIndex < 10) {
      // Safety limit
      try {
        const paneSize = chart.paneSize(paneIndex)
        if (!paneSize) break

        dimensions[paneIndex] = {
          width: paneSize.width || 0,
          height: paneSize.height || 0
        }

        paneIndex++
      } catch {
        break
      }
    }

    return dimensions
  }

  /**
   * Check if pane dimensions have changed and notify listeners
   */
  checkPaneSizeChanges(chart: IChartApi, chartId: string): boolean {
    const currentDimensions = this.getCurrentPaneDimensions(chart)
    const cacheKey = this.generateCacheKey(chart, chart.chartElement())

    // Check if we have cached pane dimensions
    const cachedPaneDimensions = this.paneDimensionsCache.get(cacheKey)

    if (!cachedPaneDimensions) {
      // First time checking, store current dimensions
      this.paneDimensionsCache.set(cacheKey, {
        dimensions: currentDimensions,
        expiresAt: Date.now() + TIMING.cacheExpiration
      })
      return false
    }

    // Compare with cached dimensions
    const hasChanges = this.hasPaneSizeChanges(cachedPaneDimensions.dimensions, currentDimensions)

    if (hasChanges) {
      // Update cached dimensions
      cachedPaneDimensions.dimensions = currentDimensions
      cachedPaneDimensions.expiresAt = Date.now() + TIMING.cacheExpiration
      // Invalidate the coordinate cache to force recalculation
      this.invalidateCache(chartId)
      // Notify listeners about the change
      this.notifyUpdateCallbacks(cacheKey)
      return true
    }

    return false
  }

  /**
   * Enhanced pane size change detection with better performance
   */
  checkPaneSizeChangesOptimized(chart: IChartApi, chartId: string): boolean {
    const currentDimensions = this.getCurrentPaneDimensions(chart)
    const cacheKey = this.generateCacheKey(chart, chart.chartElement())

    // Check if we have cached pane dimensions
    const cachedPaneDimensions = this.paneDimensionsCache.get(cacheKey)

    if (!cachedPaneDimensions) {
      // First time checking, store current dimensions
      this.paneDimensionsCache.set(cacheKey, {
        dimensions: currentDimensions,
        expiresAt: Date.now() + TIMING.cacheExpiration
      })
      return false
    }

    // Check if dimensions have changed
    const hasChanges = this.hasPaneSizeChanges(cachedPaneDimensions.dimensions, currentDimensions)

    if (hasChanges) {
      // Update cached pane dimensions
      cachedPaneDimensions.dimensions = currentDimensions
      cachedPaneDimensions.expiresAt = Date.now() + TIMING.cacheExpiration

      // Invalidate coordinate cache for this chart
      this.invalidateCache(chartId)

      // Notify listeners
      this.notifyUpdateCallbacks(cacheKey)

      return true
    }

    return false
  }

  /**
   * Force refresh of coordinates for a specific chart
   * Useful when external changes affect chart layout
   */
  forceRefreshCoordinates(chartId: string): void {
    // Clear all cache entries for this chart
    const keysToDelete: string[] = []
    this.coordinateCache.forEach((entry, key) => {
      if (key.includes(chartId)) {
        keysToDelete.push(key)
      }
    })
    keysToDelete.forEach(key => this.coordinateCache.delete(key))

    // Also clear pane dimensions cache
    const paneKeysToDelete: string[] = []
    this.paneDimensionsCache.forEach((entry, key) => {
      if (key.includes(chartId)) {
        paneKeysToDelete.push(key)
      }
    })
    paneKeysToDelete.forEach(key => this.paneDimensionsCache.delete(key))

    // Notify all listeners for this chart
    this.updateCallbacks.forEach((callbacks, key) => {
      if (key.includes(chartId)) {
        callbacks.forEach(callback => {
          try {
            callback()
          } catch (error) {
          }
        })
      }
    })
  }

  /**
   * Check if pane dimensions have changed
   */
  private hasPaneSizeChanges(
    oldDimensions: {[paneId: number]: {width: number; height: number}},
    newDimensions: {[paneId: number]: {width: number; height: number}}
  ): boolean {
    const oldKeys = Object.keys(oldDimensions)
    const newKeys = Object.keys(newDimensions)

    if (oldKeys.length !== newKeys.length) {
      return true
    }

    for (const paneId of oldKeys) {
      const oldDim = oldDimensions[parseInt(paneId)]
      const newDim = newDimensions[parseInt(paneId)]

      if (!oldDim || !newDim) {
        return true
      }

      if (oldDim.width !== newDim.width || oldDim.height !== newDim.height) {
        return true
      }
    }

    return false
  }

  /**
   * Integration with CornerLayoutManager
   * Get chart dimensions for layout manager
   */
  getChartDimensionsForLayout(chart: IChartApi): { width: number; height: number } | null {
    try {
      const chartElement = chart.chartElement()
      if (!chartElement) return null

      const rect = chartElement.getBoundingClientRect()
      return {
        width: rect.width || chartElement.offsetWidth || 800,
        height: rect.height || chartElement.offsetHeight || 600
      }
    } catch (error) {
      return null
    }
  }

  /**
   * Get chart layout dimensions including axis information for layout manager
   */
  getChartLayoutDimensionsForManager(chart: IChartApi): any {
    try {
      const chartElement = chart.chartElement()
      if (!chartElement) return null

      const rect = chartElement.getBoundingClientRect()
      const container = {
        width: rect.width || chartElement.offsetWidth || 800,
        height: rect.height || chartElement.offsetHeight || 600
      }

      // Get axis dimensions
      let leftPriceScaleWidth = 0
      let rightPriceScaleWidth = 0
      let timeScaleHeight = 0

      try {
        // Get time scale height
        const timeScale = chart.timeScale()
        timeScaleHeight = timeScale.height() || 35

        // Get left price scale width
        const leftPriceScale = chart.priceScale('left')
        if (leftPriceScale) {
          leftPriceScaleWidth = leftPriceScale.width() || 0
        }

        // Get right price scale width
        const rightPriceScale = chart.priceScale('right')
        if (rightPriceScale) {
          rightPriceScaleWidth = rightPriceScale.width() || 0
        }

        // If no price scales are visible, default to right scale
        if (leftPriceScaleWidth === 0 && rightPriceScaleWidth === 0) {
          rightPriceScaleWidth = 70 // Default right price scale width
        }
      } catch (scaleError) {
        // Fallback values
        rightPriceScaleWidth = 70
        timeScaleHeight = 35
      }

      return {
        container,
        axis: {
          priceScale: {
            left: {
              width: leftPriceScaleWidth,
              height: container.height - timeScaleHeight
            },
            right: {
              width: rightPriceScaleWidth,
              height: container.height - timeScaleHeight
            }
          },
          timeScale: {
            width: container.width,
            height: timeScaleHeight
          }
        }
      }
    } catch (error) {
      console.warn('Error getting chart layout dimensions:', error)
      return {
        container: { width: 800, height: 600 },
        axis: {
          priceScale: {
            left: { width: 0, height: 565 },
            right: { width: 70, height: 565 }
          },
          timeScale: { width: 800, height: 35 }
        }
      }
    }
  }

  /**
   * Convert ElementPosition to Corner for layout manager
   */
  positionToCorner(position: ElementPosition): string {
    // Map supported positions to corners, with fallbacks for unsupported positions
    switch (position) {
      case 'top-left':
        return 'top-left'
      case 'top-right':
        return 'top-right'
      case 'bottom-left':
        return 'bottom-left'
      case 'bottom-right':
        return 'bottom-right'
      case 'top-center':
        return 'top-right' // Fallback to top-right
      case 'bottom-center':
        return 'bottom-right' // Fallback to bottom-right
      case 'center':
        return 'top-right' // Fallback to top-right
      default:
        return 'top-right'
    }
  }

  /**
   * ================================
   * POSITIONING ENGINE FUNCTIONALITY
   * Absorbed from PositioningEngine to ensure single source of truth
   * ================================
   */

  /**
   * Calculate legend position with consistent logic
   */
  calculateLegendPosition(
    chart: IChartApi,
    paneId: number,
    position: ElementPosition,
    config?: PositioningConfig
  ): LegendCoordinates | null {
    // Use existing getPaneCoordinates method
    const paneCoords = this.getPaneCoordinates(chart, paneId)
    if (!paneCoords) return null

    // Merge configuration with defaults
    const margins = {...getMargins('legend'), ...(config?.margins || {})}

    // For initial positioning, use default dimensions
    // The actual dimensions will be calculated when the element is rendered
    const dimensions = {
      width: config?.dimensions?.width || DIMENSIONS.legend.defaultWidth,
      height: config?.dimensions?.height || DIMENSIONS.legend.defaultHeight
    }

    const zIndex = config?.zIndex || Z_INDEX.legend
    const offset = config?.offset || {x: 0, y: 0}

    // Calculate position based on alignment
    // Use full pane bounds for legends to avoid time axis clipping
    const coords = this.calculateElementPosition(
      paneCoords.bounds,
      dimensions,
      position,
      margins,
      offset
    )

    return {
      ...coords,
      width: dimensions.width,
      height: dimensions.height,
      zIndex
    }
  }

  /**
   * Recalculate legend position with actual element dimensions
   */
  recalculateLegendPosition(
    chart: IChartApi,
    paneId: number,
    position: ElementPosition,
    legendElement: HTMLElement,
    config?: PositioningConfig
  ): LegendCoordinates | null {
    // Use existing getPaneCoordinates method
    const paneCoords = this.getPaneCoordinates(chart, paneId)
    if (!paneCoords) return null

    // Get actual element dimensions with fallbacks
    let actualDimensions = {
      width: legendElement.offsetWidth || legendElement.scrollWidth || 0,
      height: legendElement.offsetHeight || legendElement.scrollHeight || 0
    }

    // If dimensions are still 0, try to get them from computed styles
    if (actualDimensions.width === 0 || actualDimensions.height === 0) {
      const computedStyle = window.getComputedStyle(legendElement)
      actualDimensions = {
        width:
          parseInt(computedStyle.width) ||
          legendElement.clientWidth ||
          DIMENSIONS.legend.defaultWidth,
        height:
          parseInt(computedStyle.height) ||
          legendElement.clientHeight ||
          DIMENSIONS.legend.defaultHeight
      }
    }

    // Ensure minimum dimensions
    actualDimensions.width = Math.max(actualDimensions.width, DIMENSIONS.legend.minWidth)
    actualDimensions.height = Math.max(actualDimensions.height, DIMENSIONS.legend.minHeight)

    // Merge configuration with defaults
    const margins = {...getMargins('legend'), ...(config?.margins || {})}
    const zIndex = config?.zIndex || Z_INDEX.legend
    const offset = config?.offset || {x: 0, y: 0}

    // Calculate position based on actual dimensions
    // Use full pane bounds for legends to avoid time axis clipping
    const coords = this.calculateElementPosition(
      paneCoords.bounds,
      actualDimensions,
      position,
      margins,
      offset
    )

    return {
      ...coords,
      width: actualDimensions.width,
      height: actualDimensions.height,
      zIndex
    }
  }

  /**
   * Calculate tooltip position relative to cursor
   */
  calculateTooltipPosition(
    cursorX: number,
    cursorY: number,
    tooltipWidth: number,
    tooltipHeight: number,
    containerBounds: BoundingBox,
    preferredAnchor: 'top' | 'bottom' | 'left' | 'right' = 'top'
  ): TooltipPosition {
    const margins = getMargins('tooltip')
    const offset = {x: 10, y: 10}

    let x = cursorX
    let y = cursorY
    let anchor = preferredAnchor

    // Calculate position based on preferred anchor
    switch (preferredAnchor) {
      case 'top':
        x = cursorX - tooltipWidth / 2
        y = cursorY - tooltipHeight - offset.y
        break
      case 'bottom':
        x = cursorX - tooltipWidth / 2
        y = cursorY + offset.y
        break
      case 'left':
        x = cursorX - tooltipWidth - offset.x
        y = cursorY - tooltipHeight / 2
        break
      case 'right':
        x = cursorX + offset.x
        y = cursorY - tooltipHeight / 2
        break
    }

    // Adjust if tooltip goes outside container bounds
    if (x < containerBounds.left + margins.left) {
      x = containerBounds.left + margins.left
      if (anchor === 'left') anchor = 'right'
    }
    if (x + tooltipWidth > containerBounds.right - margins.right) {
      x = containerBounds.right - tooltipWidth - margins.right
      if (anchor === 'right') anchor = 'left'
    }
    if (y < containerBounds.top + margins.top) {
      y = containerBounds.top + margins.top
      if (anchor === 'top') anchor = 'bottom'
    }
    if (y + tooltipHeight > containerBounds.bottom - margins.bottom) {
      y = containerBounds.bottom - tooltipHeight - margins.bottom
      if (anchor === 'bottom') anchor = 'top'
    }

    return {x, y, anchor, offset}
  }

  /**
   * Calculate overlay position (for rectangles, annotations, etc.)
   * Note: This requires a series to convert prices to coordinates
   */
  calculateOverlayPosition(
    startTime: Time,
    endTime: Time,
    startPrice: number,
    endPrice: number,
    chart: IChartApi,
    series?: ISeriesApi<any>,
    paneId: number = 0
  ): BoundingBox | null {
    try {
      const timeScale = chart.timeScale()

      // Convert time to x coordinates
      const x1 = timeScale.timeToCoordinate(startTime)
      const x2 = timeScale.timeToCoordinate(endTime)

      // Convert price to y coordinates (requires series)
      let y1: number | null = null
      let y2: number | null = null

      if (series) {
        y1 = series.priceToCoordinate(startPrice)
        y2 = series.priceToCoordinate(endPrice)
      } else {
        // Fallback: estimate based on chart height
        const chartElement = chart.chartElement()
        if (chartElement) {
          const height = chartElement.clientHeight
          // Simple linear mapping (this is a rough approximation)
          y1 = height * 0.3 // Default positions
          y2 = height * 0.7
        }
      }

      if (x1 === null || x2 === null || y1 === null || y2 === null) {
        return null
      }

      // Calculate bounding box
      const x = Math.min(x1, x2)
      const y = Math.min(y1, y2)
      const width = Math.abs(x2 - x1)
      const height = Math.abs(y2 - y1)

      return createBoundingBox(x, y, width, height)
    } catch (error) {
      return null
    }
  }

  /**
   * Calculate multi-pane layout positions
   */
  calculateMultiPaneLayout(
    totalHeight: number,
    paneHeights: number[] | 'equal' | {[key: number]: number}
  ): {[paneId: number]: BoundingBox} {
    const layout: {[paneId: number]: BoundingBox} = {}

    if (paneHeights === 'equal') {
      // Equal height distribution
      const paneCount = Object.keys(layout).length || 1
      const heightPerPane = totalHeight / paneCount

      for (let i = 0; i < paneCount; i++) {
        layout[i] = createBoundingBox(
          0,
          i * heightPerPane,
          0, // Width will be set by chart
          heightPerPane
        )
      }
    } else if (Array.isArray(paneHeights)) {
      // Specific heights for each pane
      let currentY = 0
      paneHeights.forEach((height, index) => {
        layout[index] = createBoundingBox(
          0,
          currentY,
          0, // Width will be set by chart
          height
        )
        currentY += height
      })
    } else {
      // Object with pane ID to height mapping
      let currentY = 0
      for (const [paneId, height] of Object.entries(paneHeights)) {
        layout[Number(paneId)] = createBoundingBox(
          0,
          currentY,
          0, // Width will be set by chart
          height
        )
        currentY += height
      }
    }

    return layout
  }

  /**
   * Calculate crosshair label position
   */
  calculateCrosshairLabelPosition(
    crosshairX: number,
    crosshairY: number,
    labelWidth: number,
    labelHeight: number,
    containerBounds: BoundingBox,
    axis: 'x' | 'y'
  ): {x: number; y: number} {
    const margins = getMargins('content')

    if (axis === 'x') {
      // Time axis label
      return {
        x: Math.max(
          containerBounds.left + margins.left,
          Math.min(crosshairX - labelWidth / 2, containerBounds.right - labelWidth - margins.right)
        ),
        y: containerBounds.bottom - labelHeight - margins.bottom
      }
    } else {
      // Price axis label
      return {
        x: containerBounds.right - labelWidth - margins.right,
        y: Math.max(
          containerBounds.top + margins.top,
          Math.min(
            crosshairY - labelHeight / 2,
            containerBounds.bottom - labelHeight - margins.bottom
          )
        )
      }
    }
  }

  /**
   * Calculate element position within bounds
   */
  private calculateElementPosition(
    bounds: BoundingBox,
    dimensions: {width: number; height: number},
    position: ElementPosition,
    margins: Margins,
    offset: {x?: number; y?: number}
  ): {top: number; left: number; right?: number; bottom?: number} {
    const offsetX = offset.x || 0
    const offsetY = offset.y || 0

    switch (position) {
      case 'top-left':
        return {
          top: bounds.top + margins.top + offsetY,
          left: bounds.left + margins.left + offsetX
        }

      case 'top-right':
        return {
          top: bounds.top + margins.top + offsetY,
          left: bounds.right - dimensions.width - margins.right - offsetX,
          right: margins.right + offsetX
        }

      case 'bottom-left':
        return {
          top: bounds.bottom - dimensions.height - margins.bottom - offsetY,
          left: bounds.left + margins.left + offsetX,
          bottom: margins.bottom + offsetY
        }

      case 'bottom-right':
        return {
          top: bounds.bottom - dimensions.height - margins.bottom - offsetY,
          left: bounds.right - dimensions.width - margins.right - offsetX,
          right: margins.right + offsetX,
          bottom: margins.bottom + offsetY
        }

      case 'center':
        return {
          top: bounds.top + (bounds.height - dimensions.height) / 2 + offsetY,
          left: bounds.left + (bounds.width - dimensions.width) / 2 + offsetX
        }

      default:
        return {
          top: bounds.top + margins.top + offsetY,
          left: bounds.left + margins.left + offsetX
        }
    }
  }

  /**
   * Validate positioning constraints
   */
  validatePositioning(
    element: BoundingBox,
    container: BoundingBox
  ): {isValid: boolean; adjustments: {x?: number; y?: number}} {
    const adjustments: {x?: number; y?: number} = {}
    let isValid = true

    // Check if element fits within container
    if (element.left < container.left) {
      adjustments.x = container.left - element.left
      isValid = false
    } else if (element.right > container.right) {
      adjustments.x = container.right - element.right
      isValid = false
    }

    if (element.top < container.top) {
      adjustments.y = container.top - element.top
      isValid = false
    } else if (element.bottom > container.bottom) {
      adjustments.y = container.bottom - element.bottom
      isValid = false
    }

    return {isValid, adjustments}
  }

  /**
   * Apply positioning to DOM element
   */
  applyPositionToElement(
    element: HTMLElement,
    coordinates: LegendCoordinates | {top: number; left: number; right?: number; bottom?: number}
  ): void {
    // Reset all position properties
    element.style.top = 'auto'
    element.style.left = 'auto'
    element.style.right = 'auto'
    element.style.bottom = 'auto'

    // Apply new position
    if (coordinates.top !== undefined) {
      element.style.top = `${coordinates.top}px`
    }
    if (coordinates.left !== undefined) {
      element.style.left = `${coordinates.left}px`
    }
    if (coordinates.right !== undefined) {
      element.style.right = `${coordinates.right}px`
    }
    if (coordinates.bottom !== undefined) {
      element.style.bottom = `${coordinates.bottom}px`
    }

    // Apply z-index if available
    if ('zIndex' in coordinates && coordinates.zIndex !== undefined) {
      element.style.zIndex = String(coordinates.zIndex)
    }

    // Ensure position is absolute
    if (!element.style.position || element.style.position === 'static') {
      element.style.position = 'absolute'
    }
  }

  /**
   * Calculate responsive scaling factor
   */
  calculateScalingFactor(
    currentWidth: number,
    currentHeight: number,
    baseWidth: number = DIMENSIONS.chart.defaultWidth,
    baseHeight: number = DIMENSIONS.chart.defaultHeight
  ): {x: number; y: number; uniform: number} {
    const scaleX = currentWidth / baseWidth
    const scaleY = currentHeight / baseHeight
    const uniform = Math.min(scaleX, scaleY)

    return {x: scaleX, y: scaleY, uniform}
  }

  /**
   * Calculate widget stack position for layout manager support
   */
  calculateWidgetStackPosition(
    chart: IChartApi,
    paneId: number,
    corner: string,
    widgets: any[],
    index: number
  ): any {
    // Get pane coordinates
    const paneCoords = this.getPaneCoordinates(chart, paneId)
    if (!paneCoords) return null

    const isTopCorner = corner.startsWith('top')
    const isRightCorner = corner.endsWith('right')

    // Get actual axis dimensions from lightweight-charts APIs
    const axisDimensions = this.getAxisDimensions(chart)

    // Calculate cumulative offset from previous widgets
    let cumulativeHeight = 0
    for (let i = 0; i < index; i++) {
      const prevWidget = widgets[i]
      if (prevWidget && prevWidget.visible) {
        const dims = prevWidget.getDimensions()

        // If dimensions are 0, use a reasonable fallback for legends/buttons
        let height = dims.height
        if (height === 0) {
          // Estimate height based on widget type
          if (prevWidget.getContainerClassName && prevWidget.getContainerClassName().includes('legend')) {
            height = 24 // Default legend height
          } else if (prevWidget.getContainerClassName && prevWidget.getContainerClassName().includes('button')) {
            height = 16 // Default button height
          } else {
            height = 20 // Generic fallback
          }
        }

        cumulativeHeight += height + UniversalSpacing.WIDGET_GAP // Widget gap between stacked legends

      }
    }


    const position: any = {
      zIndex: 1000 + index
    }

    const edgePadding = UniversalSpacing.EDGE_PADDING

    // Set horizontal position using actual Y-axis widths from price scale APIs
    if (isRightCorner) {
      // For right corners, account for right price scale width
      position.right = edgePadding + axisDimensions.rightPriceScaleWidth
    } else {
      // For left corners, account for left price scale width
      position.left = edgePadding + axisDimensions.leftPriceScaleWidth
    }

    // Set vertical position using actual X-axis height from time scale API
    if (isTopCorner) {
      position.top = paneCoords.bounds.top + edgePadding + cumulativeHeight
    } else {
      // For bottom positioning, account for X-axis height on the last pane
      let bottomOffset = edgePadding + cumulativeHeight

      const isLastPane = this.isLastPane(chart, paneId)
      if (isLastPane) {
        // Add actual X-axis height from time scale API
        bottomOffset += axisDimensions.timeScaleHeight
      }

      position.bottom = bottomOffset
    }

    return position
  }

  /**
   * Get actual axis dimensions from lightweight-charts APIs
   */
  private getAxisDimensions(chart: IChartApi): {
    timeScaleHeight: number;
    leftPriceScaleWidth: number;
    rightPriceScaleWidth: number;
  } {
    let timeScaleHeight = 35 // Default fallback
    let leftPriceScaleWidth = 0 // Default: no left scale
    let rightPriceScaleWidth = 70 // Default fallback for right scale

    try {
      // Get X-axis (time scale) height using ITimeScaleApi
      timeScaleHeight = chart.timeScale().height()
    } catch (error) {
      // Use fallback value
    }

    try {
      // Get left Y-axis (price scale) width using IPriceScaleApi
      const leftPriceScale = chart.priceScale('left')
      if (leftPriceScale) {
        leftPriceScaleWidth = leftPriceScale.width()
      }
    } catch (error) {
      // Left scale doesn't exist or failed - keep default 0
    }

    try {
      // Get right Y-axis (price scale) width using IPriceScaleApi
      const rightPriceScale = chart.priceScale('right')
      if (rightPriceScale) {
        rightPriceScaleWidth = rightPriceScale.width()
      }
    } catch (error) {
      // Use fallback value
    }

    return {
      timeScaleHeight,
      leftPriceScaleWidth,
      rightPriceScaleWidth
    }
  }

  /**
   * Check if the given pane is the last pane in the chart
   */
  private isLastPane(chart: IChartApi, paneId: number): boolean {
    try {
      // Try to get the next pane - if it fails, this is the last pane
      chart.paneSize(paneId + 1)
      return false // If we get here, there's a next pane
    } catch (error) {
      return true // Error means no next pane exists
    }
  }

  /**
   * Calculate cumulative offset for widget stacking
   */
  calculateCumulativeOffset(widgets: any[], index: number, gap: number = 8): number {
    let cumulativeHeight = 0
    for (let i = 0; i < index; i++) {
      const prevWidget = widgets[i]
      if (prevWidget && prevWidget.visible) {
        const dims = prevWidget.getDimensions()
        cumulativeHeight += dims.height + gap
      }
    }
    return cumulativeHeight
  }

  /**
   * Validate stacking bounds for overflow detection
   */
  validateStackingBounds(
    corner: string,
    widgets: any[],
    containerBounds: BoundingBox
  ): {isValid: boolean; overflowingWidgets: any[]} {
    const overflowing: any[] = []
    const isTopCorner = corner.startsWith('top')
    let cumulativeHeight = UniversalSpacing.EDGE_PADDING // Edge padding

    for (const widget of widgets) {
      if (!widget.visible) continue

      const dims = widget.getDimensions()
      const totalHeightRequired = cumulativeHeight + dims.height + UniversalSpacing.EDGE_PADDING // Edge padding

      if (isTopCorner) {
        if (totalHeightRequired > containerBounds.height) {
          overflowing.push(widget)
        }
      } else {
        if (totalHeightRequired > containerBounds.height) {
          overflowing.push(widget)
        }
      }

      cumulativeHeight += dims.height + UniversalSpacing.WIDGET_GAP // Widget gap
    }

    return {
      isValid: overflowing.length === 0,
      overflowingWidgets: overflowing
    }
  }

  /**
   * Setup automatic layout manager updates when chart dimensions change
   */
  setupLayoutManagerIntegration(chart: IChartApi, layoutManager: any): void {
    const updateLayoutManager = () => {
      const layoutDimensions = this.getChartLayoutDimensionsForManager(chart)
      if (layoutDimensions) {
        layoutManager.updateChartLayout(layoutDimensions)
      }
    }

    // Fast synchronous update for resize events
    const fastUpdateLayoutManager = () => {
      // Use immediate dimension update for fast resize response
      if (layoutManager.updateChartDimensionsFromElement) {
        layoutManager.updateChartDimensionsFromElement()
      } else {
        // Fallback to async method
        updateLayoutManager()
      }
    }

    // Immediate setup for fast resize performance
    updateLayoutManager()

    // Single follow-up using requestAnimationFrame for smooth updates
    requestAnimationFrame(() => {
      updateLayoutManager()
    })

    // Watch for chart element resize and pane changes
    try {
      const chartElement = chart.chartElement()
      if (chartElement && typeof ResizeObserver !== 'undefined') {
        let lastLayoutUpdate = 0
        const layoutThrottleDelay = 16 // ~60fps max to prevent X-axis lag
        const resizeObserver = new ResizeObserver(() => {
          // Throttle layout updates to prevent performance issues during pan/zoom
          const now = Date.now()
          if (now - lastLayoutUpdate >= layoutThrottleDelay) {
            lastLayoutUpdate = now
            fastUpdateLayoutManager()
          }
        })
        resizeObserver.observe(chartElement)
      }

      // Watch for pane size changes using periodic checks
      // This is necessary because LightweightCharts doesn't provide pane resize events
      let lastPaneSizes: Array<{ width: number; height: number }> = []

      const checkPaneChanges = () => {
        try {
          const currentPaneSizes: Array<{ width: number; height: number }> = []

          // Get current pane sizes
          for (let i = 0; i < 10; i++) { // Check up to 10 panes
            try {
              const paneSize = chart.paneSize(i)
              if (paneSize) {
                currentPaneSizes[i] = { width: paneSize.width, height: paneSize.height }
              }
            } catch {
              break // No more panes
            }
          }

          // Compare with last known sizes
          let changed = currentPaneSizes.length !== lastPaneSizes.length
          if (!changed) {
            for (let i = 0; i < currentPaneSizes.length; i++) {
              const current = currentPaneSizes[i]
              const last = lastPaneSizes[i]
              if (!last || current.width !== last.width || current.height !== last.height) {
                changed = true
                break
              }
            }
          }

          if (changed) {
            lastPaneSizes = currentPaneSizes
            updateLayoutManager()
          }
        } catch (error) {
          // Ignore errors in pane change detection
        }
      }

      // Check for pane changes every 250ms for responsive UI (reduced frequency to prevent X-axis lag)
      const paneCheckInterval = setInterval(checkPaneChanges, 250)

      // Clean up interval after a reasonable time
      setTimeout(() => {
        clearInterval(paneCheckInterval)
      }, 300000) // 5 minutes

    } catch (error) {
      // Fallback to periodic checks if ResizeObserver not available
      const intervalId = setInterval(updateLayoutManager, 1000)

      // Clean up interval on chart destruction
      setTimeout(() => {
        clearInterval(intervalId)
      }, 60000) // Clean up after 1 minute as fallback
    }
  }
}

import { IChartApi, ISeriesApi } from 'lightweight-charts'
import { LegendPrimitive } from '../primitives/LegendPrimitive'
import { RangeSwitcherPrimitive, DefaultRangeConfigs } from '../primitives/RangeSwitcherPrimitive'
import { PrimitiveEventManager } from './PrimitiveEventManager'
import { CornerLayoutManager } from './CornerLayoutManager'
import { LegendConfig, RangeSwitcherConfig, PaneCollapseConfig } from '../types'
import { ExtendedSeriesApi, CrosshairEventData } from '../types/ChartInterfaces'
import { PrimitivePriority } from '../primitives/BasePanePrimitive'
import { PaneButtonPanelPlugin, createPaneButtonPanelPlugin } from '../plugins/chart/paneButtonPanelPlugin'

/**
 * ChartPrimitiveManager - Centralized management for all chart primitives
 *
 * This service provides unified API for adding/removing primitives and replaces
 * the old ChartWidgetManager system with pure primitive-only approach.
 */
export class ChartPrimitiveManager {
  private static instances: Map<string, ChartPrimitiveManager> = new Map()

  private chart: IChartApi
  private chartId: string
  private eventManager: PrimitiveEventManager
  private primitives: Map<string, LegendPrimitive | RangeSwitcherPrimitive | PaneButtonPanelPlugin> = new Map()
  private legendCounter: number = 0

  private constructor(chart: IChartApi, chartId: string) {
    this.chart = chart
    this.chartId = chartId
    this.eventManager = PrimitiveEventManager.getInstance(chartId)
    this.eventManager.initialize(chart)
  }

  /**
   * Get or create primitive manager for a chart
   */
  public static getInstance(chart: IChartApi, chartId: string): ChartPrimitiveManager {
    if (!ChartPrimitiveManager.instances.has(chartId)) {
      ChartPrimitiveManager.instances.set(chartId, new ChartPrimitiveManager(chart, chartId))
    }
    return ChartPrimitiveManager.instances.get(chartId)!
  }

  /**
   * Clean up primitive manager for a chart
   */
  public static cleanup(chartId: string): void {
    const instance = ChartPrimitiveManager.instances.get(chartId)
    if (instance) {
      instance.destroy()
      ChartPrimitiveManager.instances.delete(chartId)
    }

    // Also cleanup the layout manager and event manager for this chart
    CornerLayoutManager.cleanup(chartId)
    PrimitiveEventManager.cleanup(chartId)
  }

  /**
   * Add range switcher primitive
   */
  public addRangeSwitcher(config: RangeSwitcherConfig): { destroy: () => void } {
    const primitiveId = `range-switcher-${this.chartId}`

    try {
      const rangeSwitcher = new RangeSwitcherPrimitive(primitiveId, {
        corner: config.position || 'top-right',
        priority: PrimitivePriority.RANGE_SWITCHER,
        ranges: config.ranges || [...DefaultRangeConfigs.trading],
      })

      // Attach to first pane (chart-level primitives go to pane 0)
      const panes = this.chart.panes()
      if (panes.length > 0) {
        panes[0].attachPrimitive(rangeSwitcher)
      }
      this.primitives.set(primitiveId, rangeSwitcher)

      return {
        destroy: () => this.destroyPrimitive(primitiveId)
      }
    } catch (error) {
      return { destroy: () => {} }
    }
  }

  /**
   * Add legend primitive
   */
  public addLegend(
    config: LegendConfig,
    isPanePrimitive: boolean = false,
    paneId: number = 0,
    seriesReference?: ExtendedSeriesApi
  ): { destroy: () => void } {
    const primitiveId = `legend-${this.chartId}-${++this.legendCounter}`

    try {
      const legend = new LegendPrimitive(primitiveId, {
        corner: config.position || 'top-left',
        priority: PrimitivePriority.LEGEND,
        text: config.text || '$$value$$',
        valueFormat: config.valueFormat || '.2f',
        isPanePrimitive,
        paneId,
        style: {
          backgroundColor: config.backgroundColor || 'rgba(0, 0, 0, 0.8)',
          color: config.textColor || 'white'
        }
      })

      // Attach to series if we have a series reference (preferred for legends)
      if (seriesReference) {
        try {
          seriesReference.attachPrimitive(legend)
        } catch (error) {
          // Fallback to pane attachment if series attachment fails
          this.attachToPaneAsFallback(legend, isPanePrimitive, paneId)
        }
      } else {
        // Attach to appropriate level (chart or pane) when no series reference
        this.attachToPaneAsFallback(legend, isPanePrimitive, paneId)
      }

      this.primitives.set(primitiveId, legend)

      return {
        destroy: () => this.destroyPrimitive(primitiveId)
      }
    } catch (error) {
      return { destroy: () => {} }
    }
  }


  /**
   * Add button panel (gear + collapse buttons) primitive
   */
  public addButtonPanel(
    paneId: number,
    config: PaneCollapseConfig = {}
  ): { destroy: () => void; plugin: PaneButtonPanelPlugin } {
    const primitiveId = `button-panel-${this.chartId}-${paneId}`

    try {
      const buttonPanel = createPaneButtonPanelPlugin(paneId, config, this.chartId)

      // Attach to appropriate pane
      const panes = this.chart.panes()
      if (panes.length > paneId) {
        panes[paneId].attachPrimitive(buttonPanel)
      } else {
        // Fallback to first pane if pane doesn't exist
        const fallbackPanes = this.chart.panes()
        if (fallbackPanes.length > 0) {
          fallbackPanes[0].attachPrimitive(buttonPanel)
        }
      }

      this.primitives.set(primitiveId, buttonPanel)

      return {
        destroy: () => this.destroyPrimitive(primitiveId),
        plugin: buttonPanel
      }
    } catch (error) {
      return {
        destroy: () => {},
        plugin: createPaneButtonPanelPlugin(paneId, config, this.chartId)
      }
    }
  }

  /**
   * Update legend values with crosshair data
   */
  public updateLegendValues(crosshairData: CrosshairEventData): void {
    // The legend primitives automatically handle crosshair updates through the event system
    // This method is kept for backward compatibility but functionality is now handled
    // by the primitive event system and crosshair subscriptions in BasePanePrimitive
  }

  /**
   * Destroy a specific primitive
   */
  private destroyPrimitive(primitiveId: string): void {
    const primitive = this.primitives.get(primitiveId)
    if (primitive) {
      try {
        // Detach from all panes (does nothing if not attached to that pane)
        const panes = this.chart.panes()
        panes.forEach(pane => {
          pane.detachPrimitive(primitive)
        })
        this.primitives.delete(primitiveId)
      } catch (error) {
      }
    }
  }

  /**
   * Get primitive by ID
   */
  public getPrimitive(primitiveId: string): LegendPrimitive | RangeSwitcherPrimitive | PaneButtonPanelPlugin | undefined {
    return this.primitives.get(primitiveId)
  }

  /**
   * Get all primitives
   */
  public getAllPrimitives(): Map<string, LegendPrimitive | RangeSwitcherPrimitive | PaneButtonPanelPlugin> {
    return new Map(this.primitives)
  }


  /**
   * Destroy all primitives for this chart
   */
  public destroy(): void {
    // Destroy all primitives
    for (const [primitiveId, primitive] of this.primitives) {
      try {
        // Detach from all panes (does nothing if not attached to that pane)
        const panes = this.chart.panes()
        panes.forEach(pane => {
          pane.detachPrimitive(primitive)
        })
      } catch (error) {
      }
    }

    // Clear references
    this.primitives.clear()
  }

  /**
   * Get event manager instance (for advanced usage)
   */
  public getEventManager(): PrimitiveEventManager {
    return this.eventManager
  }

  /**
   * Get chart ID
   */
  public getChartId(): string {
    return this.chartId
  }

  /**
   * Helper method to attach primitive to pane as fallback
   */
  private attachToPaneAsFallback(primitive: LegendPrimitive | RangeSwitcherPrimitive | PaneButtonPanelPlugin, isPanePrimitive: boolean, paneId: number): void {
    if (isPanePrimitive && paneId >= 0) {
      // Get pane and attach to it
      const panes = this.chart.panes()
      if (panes.length > paneId) {
        panes[paneId].attachPrimitive(primitive)
      } else {
        // Fallback to first pane if pane doesn't exist
        const fallbackPanes = this.chart.panes()
        if (fallbackPanes.length > 0) {
          fallbackPanes[0].attachPrimitive(primitive)
        }
      }
    } else {
      // Attach to first pane (chart-level)
      const panes = this.chart.panes()
      if (panes.length > 0) {
        panes[0].attachPrimitive(primitive)
      }
    }
  }
}

/**
 * Cache manager for coordinate calculations
 * Extracted from ChartCoordinateService for better separation of concerns
 */

import { IChartApi } from 'lightweight-charts'
import { CoordinateCacheEntry } from '../types/coordinates'
import { areCoordinatesStale } from '../utils/coordinateValidation'
import { TIMING } from '../config/positioningConfig'

/**
 * Manages coordinate caching with automatic cleanup
 */
export class CoordinateCacheManager {
  private static instance: CoordinateCacheManager
  private coordinateCache = new Map<string, CoordinateCacheEntry>()
  private updateCallbacks = new Map<string, Set<() => void>>()
  private cleanupInterval: NodeJS.Timeout | null = null

  static getInstance(): CoordinateCacheManager {
    if (!this.instance) {
      this.instance = new CoordinateCacheManager()
      this.instance.startCacheCleanup()
    }
    return this.instance
  }

  /**
   * Generate a cache key for chart and container combination
   */
  generateCacheKey(chart: IChartApi, container: HTMLElement): string {
    const chartElement = chart.chartElement()
    const chartId = chartElement?.id || 'unnamed-chart'
    const containerId = container.id || 'unnamed-container'
    const timestamp = Math.floor(Date.now() / 1000) // Round to seconds for cache stability

    return `${chartId}-${containerId}-${timestamp}`
  }

  /**
   * Get cached coordinates if valid
   */
  getCachedCoordinates(cacheKey: string): CoordinateCacheEntry | null {
    const cached = this.coordinateCache.get(cacheKey)
    if (cached && !areCoordinatesStale(cached, TIMING.cacheExpiration)) {
      return cached
    }

    // Remove stale cache entry
    if (cached) {
      this.coordinateCache.delete(cacheKey)
    }

    return null
  }

  /**
   * Cache coordinates with timestamp
   */
  setCachedCoordinates(cacheKey: string, coordinates: unknown): void {
    const cacheEntry: CoordinateCacheEntry = {
      coordinates,
      timestamp: Date.now(),
      chartId: this.extractChartIdFromKey(cacheKey),
      containerId: this.extractContainerIdFromKey(cacheKey)
    }

    this.coordinateCache.set(cacheKey, cacheEntry)
  }

  /**
   * Check if coordinates exist in cache and are valid
   */
  hasCachedCoordinates(cacheKey: string): boolean {
    return this.getCachedCoordinates(cacheKey) !== null
  }

  /**
   * Invalidate cache for specific key
   */
  invalidateCache(cacheKey: string): void {
    this.coordinateCache.delete(cacheKey)
    this.notifyUpdateCallbacks(cacheKey)
  }

  /**
   * Invalidate all cache entries for a chart
   */
  invalidateChartCache(chartId: string): void {
    const keysToDelete: string[] = []

    for (const [key, entry] of this.coordinateCache.entries()) {
      if (entry.chartId === chartId) {
        keysToDelete.push(key)
      }
    }

    keysToDelete.forEach(key => {
      this.coordinateCache.delete(key)
      this.notifyUpdateCallbacks(key)
    })
  }

  /**
   * Register callback for cache updates
   */
  onCacheUpdate(cacheKey: string, callback: () => void): () => void {
    if (!this.updateCallbacks.has(cacheKey)) {
      this.updateCallbacks.set(cacheKey, new Set())
    }

    this.updateCallbacks.get(cacheKey)!.add(callback)

    // Return unsubscribe function
    return () => {
      const callbacks = this.updateCallbacks.get(cacheKey)
      if (callbacks) {
        callbacks.delete(callback)
        if (callbacks.size === 0) {
          this.updateCallbacks.delete(cacheKey)
        }
      }
    }
  }

  /**
   * Notify all registered callbacks for cache key
   */
  private notifyUpdateCallbacks(cacheKey: string): void {
    const callbacks = this.updateCallbacks.get(cacheKey)
    if (callbacks) {
      callbacks.forEach(callback => {
        try {
          callback()
        } catch (error) {
          // Ignore callback errors to prevent cache system failure
        }
      })
    }
  }

  /**
   * Start automatic cache cleanup
   */
  private startCacheCleanup(): void {
    if (this.cleanupInterval) {
      return
    }

    this.cleanupInterval = setInterval(() => {
      this.cleanupExpiredEntries()
    }, TIMING.cacheCleanupInterval)
  }

  /**
   * Stop automatic cache cleanup
   */
  stopCacheCleanup(): void {
    if (this.cleanupInterval) {
      clearInterval(this.cleanupInterval)
      this.cleanupInterval = null
    }
  }

  /**
   * Clean up expired cache entries
   */
  private cleanupExpiredEntries(): void {
    const now = Date.now()
    const keysToDelete: string[] = []

    for (const [key, entry] of this.coordinateCache.entries()) {
      if (areCoordinatesStale(entry, TIMING.cacheExpiration)) {
        keysToDelete.push(key)
      }
    }

    keysToDelete.forEach(key => {
      this.coordinateCache.delete(key)
    })

    // Clean up empty callback sets
    for (const [key, callbacks] of this.updateCallbacks.entries()) {
      if (callbacks.size === 0) {
        this.updateCallbacks.delete(key)
      }
    }
  }

  /**
   * Extract chart ID from cache key
   */
  private extractChartIdFromKey(cacheKey: string): string {
    return cacheKey.split('-')[0] || 'unknown'
  }

  /**
   * Extract container ID from cache key
   */
  private extractContainerIdFromKey(cacheKey: string): string {
    const parts = cacheKey.split('-')
    return parts[1] || 'unknown'
  }

  /**
   * Get cache statistics for debugging
   */
  getCacheStats(): {
    totalEntries: number
    staleEntries: number
    activeCallbacks: number
    memoryUsage: number
  } {
    let staleCount = 0
    let totalCallbacks = 0

    for (const entry of this.coordinateCache.values()) {
      if (areCoordinatesStale(entry, TIMING.cacheExpiration)) {
        staleCount++
      }
    }

    for (const callbacks of this.updateCallbacks.values()) {
      totalCallbacks += callbacks.size
    }

    return {
      totalEntries: this.coordinateCache.size,
      staleEntries: staleCount,
      activeCallbacks: totalCallbacks,
      memoryUsage: this.estimateMemoryUsage()
    }
  }

  /**
   * Estimate memory usage of cache (rough calculation)
   */
  private estimateMemoryUsage(): number {
    let totalSize = 0

    for (const [key, entry] of this.coordinateCache.entries()) {
      // Rough estimation: key size + JSON serialized entry size
      totalSize += key.length * 2 // UTF-16 chars
      totalSize += JSON.stringify(entry).length * 2
    }

    return totalSize
  }

  /**
   * Clear all cache data (for testing or memory management)
   */
  clearAll(): void {
    this.coordinateCache.clear()
    this.updateCallbacks.clear()
  }

  /**
   * Cleanup on service destruction
   */
  destroy(): void {
    this.stopCacheCleanup()
    this.clearAll()
  }
}

/**
 * Service for managing pane-specific coordinate calculations
 * Extracted from ChartCoordinateService for better separation of concerns
 */

import { IChartApi } from 'lightweight-charts'
import { PaneCoordinates, BoundingBox, ElementPosition } from '../types/coordinates'
import { ChartDimensionsService, PaneDimensionsOptions } from './ChartDimensionsService'
import { createBoundingBox } from '../utils/coordinateValidation'
import { DIMENSIONS, getMargins } from '../config/positioningConfig'
import { UniversalSpacing } from '../primitives/PrimitiveDefaults'

/**
 * Manages pane-specific coordinate calculations and positioning
 */
export class PaneCoordinatesService {
  private static instance: PaneCoordinatesService
  private dimensionsService: ChartDimensionsService

  constructor() {
    this.dimensionsService = ChartDimensionsService.getInstance()
  }

  static getInstance(): PaneCoordinatesService {
    if (!this.instance) {
      this.instance = new PaneCoordinatesService()
    }
    return this.instance
  }

  /**
   * Get comprehensive pane coordinates
   */
  getPaneCoordinates(chart: IChartApi, paneId: number): PaneCoordinates | null {
    try {
      if (!chart || typeof paneId !== 'number' || paneId < 0) {
        return null
      }

      const panes = chart.panes()
      if (!panes || paneId >= panes.length) {
        return null
      }

      const paneSize = panes[paneId].getSize()
      if (!paneSize || typeof paneSize.height !== 'number' || typeof paneSize.width !== 'number') {
        return null
      }

      // Calculate cumulative top offset from previous panes
      let cumulativeTop = 0
      for (let i = 0; i < paneId; i++) {
        const size = panes[i]?.getSize()
        if (size && typeof size.height === 'number') {
          cumulativeTop += size.height
        }
      }

      // Add time scale height for main pane
      if (paneId === 0) {
        const timeScaleDimensions = this.dimensionsService.getTimeScaleDimensions(chart)
        cumulativeTop += timeScaleDimensions.height
      }

      // Get chart container for absolute positioning
      const chartElement = chart.chartElement()
      const containerRect = chartElement ? chartElement.getBoundingClientRect() : null

      const margins = getMargins()
      const contentArea = this.calculateContentArea(chart)

      return {
        paneId,
        x: contentArea?.left || margins.left,
        y: cumulativeTop + (contentArea?.top || margins.top),
        width: paneSize.width,
        height: paneSize.height,
        absoluteX: (containerRect?.left || 0) + (contentArea?.left || margins.left),
        absoluteY: (containerRect?.top || 0) + cumulativeTop + (contentArea?.top || margins.top),
        contentArea: contentArea || {
          top: margins.top,
          left: margins.left,
          width: paneSize.width,
          height: paneSize.height
        },
        margins,
        isMainPane: paneId === 0,
        isLastPane: this.isLastPane(chart, paneId)
      }
    } catch (error) {
      return null
    }
  }

  /**
   * Get full pane bounds including all elements
   */
  getFullPaneBounds(chart: IChartApi, paneId: number): BoundingBox | null {
    try {
      if (!chart || typeof paneId !== 'number' || paneId < 0) {
        return null
      }

      const panes = chart.panes()
      if (!panes || paneId >= panes.length) {
        return null
      }

      const paneSize = panes[paneId].getSize()
      if (!paneSize || typeof paneSize.height !== 'number' || typeof paneSize.width !== 'number') {
        return null
      }

      // Calculate cumulative top offset from previous panes
      let cumulativeTop = 0
      for (let i = 0; i < paneId; i++) {
        const size = panes[i]?.getSize()
        if (size && typeof size.height === 'number') {
          cumulativeTop += size.height
        }
      }

      const margins = getMargins()
      const priceScaleWidth = this.dimensionsService.getPriceScaleDimensions(chart).width

      return createBoundingBox({
        top: cumulativeTop,
        left: priceScaleWidth,
        width: paneSize.width,
        height: paneSize.height
      })
    } catch (error) {
      return null
    }
  }

  /**
   * Get coordinates for all panes in the chart
   */
  getAllPaneCoordinates(chart: IChartApi): PaneCoordinates[] {
    const coordinates: PaneCoordinates[] = []

    try {
      const panes = chart.panes()
      if (!panes) return coordinates

      for (let i = 0; i < panes.length; i++) {
        const paneCoords = this.getPaneCoordinates(chart, i)
        if (paneCoords) {
          coordinates.push(paneCoords)
        }
      }
    } catch (error) {
      // Return empty array on error
    }

    return coordinates
  }

  /**
   * Get pane coordinates from DOM elements as fallback
   */
  getPaneCoordinatesFromDOM(
    container: HTMLElement,
    paneId: number,
    options: PaneDimensionsOptions = {}
  ): PaneCoordinates | null {
    try {
      const {
        includeMargins = true,
        includeScales = true,
        validateDimensions = true
      } = options

      // Find pane elements in the DOM
      const paneElements = container.querySelectorAll('[data-name="pane"]')
      if (!paneElements || paneId >= paneElements.length) {
        return null
      }

      const paneElement = paneElements[paneId] as HTMLElement
      if (!paneElement) {
        return null
      }

      const rect = paneElement.getBoundingClientRect()
      const containerRect = container.getBoundingClientRect()

      const relativeX = rect.left - containerRect.left
      const relativeY = rect.top - containerRect.top

      const margins = includeMargins ? getMargins() : { top: 0, left: 0, right: 0, bottom: 0 }

      const coordinates: PaneCoordinates = {
        paneId,
        x: Math.floor(relativeX),
        y: Math.floor(relativeY),
        width: Math.floor(rect.width),
        height: Math.floor(rect.height),
        absoluteX: Math.floor(rect.left),
        absoluteY: Math.floor(rect.top),
        contentArea: {
          top: Math.floor(relativeY + margins.top),
          left: Math.floor(relativeX + margins.left),
          width: Math.floor(rect.width - margins.left - margins.right),
          height: Math.floor(rect.height - margins.top - margins.bottom)
        },
        margins,
        isMainPane: paneId === 0,
        isLastPane: paneId === paneElements.length - 1
      }

      // Validate dimensions if requested
      if (validateDimensions) {
        if (coordinates.width < DIMENSIONS.minPaneWidth ||
            coordinates.height < DIMENSIONS.minPaneHeight) {
          return null
        }
      }

      return coordinates
    } catch (error) {
      return null
    }
  }

  /**
   * Calculate element position within a pane
   */
  calculateElementPosition(
    paneCoords: PaneCoordinates,
    elementWidth: number,
    elementHeight: number,
    corner: 'top-left' | 'top-right' | 'bottom-left' | 'bottom-right' = 'top-right',
    offset: { x: number; y: number } = { x: 0, y: 0 }
  ): ElementPosition {
    const baseX = paneCoords.contentArea.left
    const baseY = paneCoords.contentArea.top
    const contentWidth = paneCoords.contentArea.width
    const contentHeight = paneCoords.contentArea.height

    let x: number
    let y: number

    switch (corner) {
      case 'top-left':
        x = baseX + offset.x
        y = baseY + offset.y
        break
      case 'top-right':
        x = baseX + contentWidth - elementWidth - offset.x
        y = baseY + offset.y
        break
      case 'bottom-left':
        x = baseX + offset.x
        y = baseY + contentHeight - elementHeight - offset.y
        break
      case 'bottom-right':
        x = baseX + contentWidth - elementWidth - offset.x
        y = baseY + contentHeight - elementHeight - offset.y
        break
      default:
        x = baseX + offset.x
        y = baseY + offset.y
    }

    return {
      x: Math.floor(x),
      y: Math.floor(y),
      width: elementWidth,
      height: elementHeight,
      corner,
      offset
    }
  }

  /**
   * Check if pane size has changed significantly
   */
  hasPaneSizeChanges(
    previousCoords: PaneCoordinates,
    currentCoords: PaneCoordinates,
    threshold: number = 5
  ): boolean {
    const widthDiff = Math.abs(previousCoords.width - currentCoords.width)
    const heightDiff = Math.abs(previousCoords.height - currentCoords.height)

    return widthDiff > threshold || heightDiff > threshold
  }

  /**
   * Calculate the content area excluding scales and margins
   */
  private calculateContentArea(chart: IChartApi): {
    top: number
    left: number
    width: number
    height: number
  } | null {
    try {
      const priceScaleWidth = this.dimensionsService.getPriceScaleDimensions(chart).width
      const timeScaleHeight = this.dimensionsService.getTimeScaleDimensions(chart).height
      const chartElement = chart.chartElement()

      if (!chartElement) {
        return null
      }

      const rect = chartElement.getBoundingClientRect()
      const margins = getMargins()

      return {
        top: margins.top,
        left: priceScaleWidth + margins.left,
        width: rect.width - priceScaleWidth - margins.left - margins.right,
        height: rect.height - timeScaleHeight - margins.top - margins.bottom
      }
    } catch (error) {
      return null
    }
  }

  /**
   * Check if this is the last pane in the chart
   */
  private isLastPane(chart: IChartApi, paneId: number): boolean {
    try {
      const panes = chart.panes()
      return panes ? paneId === panes.length - 1 : true
    } catch (error) {
      return true
    }
  }

  /**
   * Get axis dimensions for coordinate calculations
   */
  getAxisDimensions(chart: IChartApi): {
    timeScale: { width: number; height: number }
    priceScale: { width: number; height: number }
  } {
    return {
      timeScale: this.dimensionsService.getTimeScaleDimensions(chart),
      priceScale: this.dimensionsService.getPriceScaleDimensions(chart)
    }
  }
}

import { IChartApi, ISeriesApi } from 'lightweight-charts'

/**
 * Event types for primitive interactions
 */
export interface PrimitiveEventTypes {
  /**
   * Crosshair position changed
   */
  crosshairMove: {
    time: any
    point: { x: number; y: number } | null
    seriesData: Map<ISeriesApi<any>, any>
  }

  /**
   * Chart data updated
   */
  dataUpdate: {
    series: ISeriesApi<any>
    data: any[]
  }

  /**
   * Chart resize event
   */
  resize: {
    width: number
    height: number
  }

  /**
   * Primitive visibility changed
   */
  visibilityChange: {
    primitiveId: string
    visible: boolean
  }

  /**
   * Primitive configuration changed
   */
  configChange: {
    primitiveId: string
    config: any
  }

  /**
   * Chart time scale visible range changed
   */
  timeScaleChange: {
    from: any
    to: any
  }

  /**
   * Chart click event
   */
  click: {
    time: any
    point: { x: number; y: number }
    seriesData: Map<ISeriesApi<any>, any>
  }

  /**
   * Chart hover event
   */
  hover: {
    time: any
    point: { x: number; y: number }
    seriesData: Map<ISeriesApi<any>, any>
  }

  /**
   * Custom primitive events
   */
  custom: {
    eventType: string
    data: any
  }
}

/**
 * Event listener type
 */
export type PrimitiveEventListener<K extends keyof PrimitiveEventTypes> = (
  event: PrimitiveEventTypes[K]
) => void

/**
 * Event subscription interface
 */
export interface EventSubscription {
  unsubscribe(): void
}

/**
 * PrimitiveEventManager - Centralized event management for primitives
 *
 * Provides unified event handling for chart interactions and primitive lifecycle.
 * Integrates with lightweight-charts event system and provides abstracted events
 * for primitive implementations.
 *
 * Following DRY principles - single source of truth for event management
 */
export class PrimitiveEventManager {
  private static instances: Map<string, PrimitiveEventManager> = new Map()

  private chart: IChartApi | null = null
  private chartId: string
  private eventListeners: Map<string, Set<Function>> = new Map()
  private chartEventCleanup: Array<() => void> = []
  private _isDestroyed: boolean = false

  // Crosshair tracking
  private lastCrosshairPosition: { time: any; point: { x: number; y: number } | null } | null = null

  private constructor(chartId: string) {
    this.chartId = chartId
  }

  /**
   * Get or create event manager for a chart
   */
  public static getInstance(chartId: string): PrimitiveEventManager {
    if (!PrimitiveEventManager.instances.has(chartId)) {
      PrimitiveEventManager.instances.set(chartId, new PrimitiveEventManager(chartId))
    }
    return PrimitiveEventManager.instances.get(chartId)!
  }

  /**
   * Clean up event manager for a chart
   */
  public static cleanup(chartId: string): void {
    const instance = PrimitiveEventManager.instances.get(chartId)
    if (instance) {
      instance.destroy()
      PrimitiveEventManager.instances.delete(chartId)
    }
  }

  /**
   * Initialize with chart API
   */
  public initialize(chart: IChartApi): void {
    if (this._isDestroyed) {
      throw new Error('Cannot initialize destroyed PrimitiveEventManager')
    }

    this.chart = chart
    this.setupChartEventListeners()
  }

  /**
   * Subscribe to primitive event
   */
  public subscribe<K extends keyof PrimitiveEventTypes>(
    eventType: K,
    listener: PrimitiveEventListener<K>
  ): EventSubscription {
    if (this._isDestroyed) {
      throw new Error('Cannot subscribe to destroyed PrimitiveEventManager')
    }

    const eventKey = eventType as string
    if (!this.eventListeners.has(eventKey)) {
      this.eventListeners.set(eventKey, new Set())
    }

    this.eventListeners.get(eventKey)!.add(listener)

    return {
      unsubscribe: () => {
        const listeners = this.eventListeners.get(eventKey)
        if (listeners) {
          listeners.delete(listener)
          if (listeners.size === 0) {
            this.eventListeners.delete(eventKey)
          }
        }
      }
    }
  }

  /**
   * Emit event to subscribers
   */
  public emit<K extends keyof PrimitiveEventTypes>(
    eventType: K,
    event: PrimitiveEventTypes[K]
  ): void {
    if (this._isDestroyed) {
      return
    }

    const eventKey = eventType as string
    const listeners = this.eventListeners.get(eventKey)

    if (listeners) {
      listeners.forEach(listener => {
        try {
          listener(event)
        } catch (error) {
          console.error(`Error in primitive event listener for ${eventType}:`, error)
        }
      })
    }
  }

  /**
   * Setup chart event listeners
   */
  private setupChartEventListeners(): void {
    if (!this.chart) return

    // Crosshair move events with throttling to prevent performance issues during pan/zoom
    let lastCrosshairUpdate = 0
    const crosshairThrottleDelay = 16 // ~60fps max update rate
    const crosshairMoveHandler = (param: any) => {
      const now = Date.now()
      if (now - lastCrosshairUpdate >= crosshairThrottleDelay) {
        lastCrosshairUpdate = now
        this.handleCrosshairMove(param)
      }
    }
    this.chart.subscribeCrosshairMove(crosshairMoveHandler)
    this.chartEventCleanup.push(() => this.chart!.unsubscribeCrosshairMove(crosshairMoveHandler))

    // Chart click events
    const clickHandler = (param: any) => {
      this.handleChartClick(param)
    }
    this.chart.subscribeClick(clickHandler)
    this.chartEventCleanup.push(() => this.chart!.unsubscribeClick(clickHandler))

    // Time scale visible range changes with throttling to prevent X-axis lag
    let lastTimeScaleUpdate = 0
    const timeScaleThrottleDelay = 16 // ~60fps max update rate
    const timeScaleHandler = () => {
      const now = Date.now()
      if (now - lastTimeScaleUpdate >= timeScaleThrottleDelay) {
        lastTimeScaleUpdate = now
        this.handleTimeScaleChange()
      }
    }
    this.chart.timeScale().subscribeVisibleTimeRangeChange(timeScaleHandler)
    this.chartEventCleanup.push(() => {
      this.chart!.timeScale().unsubscribeVisibleTimeRangeChange(timeScaleHandler)
    })

    // Chart resize events
    this.setupResizeObserver()
  }

  /**
   * Handle crosshair move events
   */
  private handleCrosshairMove(param: any): void {
    const time = param.time
    const point = param.point

    // Collect series data
    const seriesData = new Map<ISeriesApi<any>, any>()
    if (param.seriesData) {
      param.seriesData.forEach((data: any, series: ISeriesApi<any>) => {
        seriesData.set(series, data)
      })
    }

    // Update last position
    this.lastCrosshairPosition = { time, point }

    // Emit crosshair move event
    this.emit('crosshairMove', {
      time,
      point,
      seriesData
    })

    // Emit hover event if point is valid
    if (point && time) {
      this.emit('hover', {
        time,
        point,
        seriesData
      })
    }
  }

  /**
   * Handle chart click events
   */
  private handleChartClick(param: any): void {
    const time = param.time
    const point = param.point

    if (!point || !time) return

    // Collect series data at click point
    const seriesData = new Map<ISeriesApi<any>, any>()
    if (param.seriesData) {
      param.seriesData.forEach((data: any, series: ISeriesApi<any>) => {
        seriesData.set(series, data)
      })
    }

    this.emit('click', {
      time,
      point,
      seriesData
    })
  }

  /**
   * Handle time scale changes
   */
  private handleTimeScaleChange(): void {
    if (!this.chart) return

    const visibleRange = this.chart.timeScale().getVisibleRange()
    if (visibleRange) {
      this.emit('timeScaleChange', {
        from: visibleRange.from,
        to: visibleRange.to
      })
    }
  }

  /**
   * Setup resize observer for chart container
   */
  private setupResizeObserver(): void {
    if (!this.chart) return

    const chartElement = this.chart.chartElement()
    if (!chartElement || !window.ResizeObserver) return

    const resizeObserver = new ResizeObserver(entries => {
      for (const entry of entries) {
        const { width, height } = entry.contentRect
        this.emit('resize', { width, height })
      }
    })

    resizeObserver.observe(chartElement)
    this.chartEventCleanup.push(() => resizeObserver.disconnect())
  }

  /**
   * Emit primitive visibility change event
   */
  public emitVisibilityChange(primitiveId: string, visible: boolean): void {
    this.emit('visibilityChange', { primitiveId, visible })
  }

  /**
   * Emit primitive configuration change event
   */
  public emitConfigChange(primitiveId: string, config: any): void {
    this.emit('configChange', { primitiveId, config })
  }

  /**
   * Emit custom primitive event
   */
  public emitCustomEvent(eventType: string, data: any): void {
    this.emit('custom', { eventType, data })
  }

  /**
   * Get current crosshair position
   */
  public getCurrentCrosshairPosition(): { time: any; point: { x: number; y: number } | null } | null {
    return this.lastCrosshairPosition
  }

  /**
   * Get chart API reference
   */
  public getChart(): IChartApi | null {
    return this.chart
  }

  /**
   * Get chart ID
   */
  public getChartId(): string {
    return this.chartId
  }

  /**
   * Check if event manager is destroyed
   */
  public isDestroyed(): boolean {
    return this._isDestroyed
  }

  /**
   * Get event listener count for debugging
   */
  public getEventListenerCount(): { [eventType: string]: number } {
    const counts: { [eventType: string]: number } = {}
    this.eventListeners.forEach((listeners, eventType) => {
      counts[eventType] = listeners.size
    })
    return counts
  }

  /**
   * Destroy event manager
   */
  public destroy(): void {
    if (this._isDestroyed) return

    // Clean up chart event listeners
    this.chartEventCleanup.forEach(cleanup => {
      try {
        cleanup()
      } catch (error) {
        console.error('Error cleaning up chart event listener:', error)
      }
    })
    this.chartEventCleanup = []

    // Clear all event listeners
    this.eventListeners.clear()

    // Clear references
    this.chart = null
    this.lastCrosshairPosition = null

    // Mark as destroyed
    this._isDestroyed = true
  }
}

/**
 * Event manager integration mixin for primitives
 */
export interface EventManagerIntegration {
  /**
   * Get event manager for this primitive
   */
  getEventManager(): PrimitiveEventManager | null

  /**
   * Subscribe to chart events
   */
  subscribeToEvents(): void

  /**
   * Unsubscribe from chart events
   */
  unsubscribeFromEvents(): void
}

/**
 * Helper function to create event manager integration
 */
export function createEventManagerIntegration(
  chartId: string,
  chart?: IChartApi
): EventManagerIntegration {
  let eventManager: PrimitiveEventManager | null = null
  let subscriptions: EventSubscription[] = []

  return {
    getEventManager(): PrimitiveEventManager | null {
      if (!eventManager) {
        eventManager = PrimitiveEventManager.getInstance(chartId)
        if (chart) {
          eventManager.initialize(chart)
        }
      }
      return eventManager
    },

    subscribeToEvents(): void {
      // Override in implementation to add specific event subscriptions
    },

    unsubscribeFromEvents(): void {
      subscriptions.forEach(sub => sub.unsubscribe())
      subscriptions = []
    }
  }
}

/**
 * Template processing engine for pane primitives
 *
 * Handles smart placeholder replacement with support for:
 * - Chart value placeholders: $$value$$, $$open$$, $$high$$, $$low$$, $$close$$
 * - Band/Ribbon placeholders: $$upper$$, $$middle$$, $$lower$$
 * - Volume placeholder: $$volume$$
 * - Time placeholder: $$time$$
 * - Custom placeholders: $$custom_key$$
 *
 * Following DRY principles - single source of truth for template processing
 */

import { UTCTimestamp } from 'lightweight-charts'
import { TemplateContext, TemplateFormatting } from '../types/ChartInterfaces'

/**
 * Interface for series data used in template processing
 */
export interface SeriesDataValue {
  time?: UTCTimestamp | string | number
  value?: number
  open?: number
  high?: number
  low?: number
  close?: number
  upper?: number
  middle?: number
  lower?: number
  volume?: number
  [key: string]: unknown
}

/**
 * Template processing options
 */
export interface TemplateOptions {
  /**
   * Whether to process placeholders (default: true)
   */
  processPlaceholders?: boolean

  /**
   * Whether to escape HTML (default: false)
   */
  escapeHtml?: boolean

  /**
   * Default value for missing placeholders
   */
  defaultValue?: string

  /**
   * Whether to throw on missing placeholder data (default: false)
   */
  strict?: boolean
}

/**
 * Template processing result
 */
export interface TemplateResult {
  /**
   * Processed template content
   */
  content: string

  /**
   * List of placeholders that were processed
   */
  processedPlaceholders: string[]

  /**
   * List of placeholders that had no data
   */
  missingPlaceholders: string[]

  /**
   * Whether errors occurred during processing
   */
  hasErrors: boolean

  /**
   * Error messages if errors occurred
   */
  errors: string[]
}

/**
 * TemplateEngine - Centralized template processing for primitives
 */
export class TemplateEngine {
  private static instance: TemplateEngine | null = null

  /**
   * Singleton instance
   */
  public static getInstance(): TemplateEngine {
    if (!TemplateEngine.instance) {
      TemplateEngine.instance = new TemplateEngine()
    }
    return TemplateEngine.instance
  }

  private constructor() {
    // Private constructor for singleton
  }

  /**
   * Process template with given context and options
   */
  public processTemplate(
    template: string,
    context: TemplateContext = {},
    options: TemplateOptions = {}
  ): TemplateResult {
    const result: TemplateResult = {
      content: template,
      processedPlaceholders: [],
      missingPlaceholders: [],
      hasErrors: false,
      errors: []
    }

    // Early return if no processing needed
    if (options.processPlaceholders === false) {
      return result
    }

    try {
      // Find all placeholders in template
      const placeholderRegex = /\$\$([a-zA-Z_][a-zA-Z0-9_]*)\$\$/g
      const placeholders = [...template.matchAll(placeholderRegex)]

      // Process each placeholder
      for (const match of placeholders) {
        const fullPlaceholder = match[0] // e.g., "$$value$$"
        const placeholderKey = match[1] // e.g., "value"

        try {
          const value = this.extractPlaceholderValue(placeholderKey, context)

          if (value !== null && value !== undefined) {
            // Format the value
            const formattedValue = this.formatValue(value, placeholderKey, context.formatting)

            // Replace in content
            result.content = result.content.replace(
              new RegExp(this.escapeRegex(fullPlaceholder), 'g'),
              options.escapeHtml ? this.escapeHtml(formattedValue) : formattedValue
            )

            result.processedPlaceholders.push(fullPlaceholder)
          } else {
            // Handle missing value
            const defaultValue = options.defaultValue || ''
            result.content = result.content.replace(
              new RegExp(this.escapeRegex(fullPlaceholder), 'g'),
              defaultValue
            )

            result.missingPlaceholders.push(fullPlaceholder)

            if (options.strict) {
              throw new Error(`Missing data for placeholder: ${fullPlaceholder}`)
            }
          }
        } catch (error) {
          result.hasErrors = true
          result.errors.push(`Error processing ${fullPlaceholder}: ${error}`)

          if (options.strict) {
            throw error
          }
        }
      }
    } catch (error) {
      result.hasErrors = true
      result.errors.push(`Template processing error: ${error}`)

      if (options.strict) {
        throw error
      }
    }

    return result
  }

  /**
   * Extract value for a specific placeholder key
   */
  private extractPlaceholderValue(key: string, context: TemplateContext): unknown {
    const { seriesData, customData } = context

    // Check custom data first
    if (customData && customData.hasOwnProperty(key)) {
      return customData[key]
    }

    // Check series data
    if (seriesData) {
      switch (key) {
        case 'value':
          return this.extractSmartValue(seriesData)
        case 'open':
          return seriesData.open
        case 'high':
          return seriesData.high
        case 'low':
          return seriesData.low
        case 'close':
          return seriesData.close
        case 'upper':
          return seriesData.upper
        case 'middle':
          return seriesData.middle
        case 'lower':
          return seriesData.lower
        case 'volume':
          return seriesData.volume
        case 'time':
          return seriesData.time
        default:
          // Check if key exists directly in series data
          if (seriesData.hasOwnProperty(key)) {
            return seriesData[key]
          }
      }
    }

    return null
  }

  /**
   * Smart value extraction for $$value$$ placeholder
   * Falls back through different value types based on series data structure
   */
  private extractSmartValue(seriesData: SeriesDataValue): number | null {
    // Priority order for value extraction:
    // 1. close (for candlestick/OHLC series)
    // 2. value (for line/area series)
    // 3. middle (for band series)
    // 4. average of upper/lower (for ribbon series)
    // 5. high (fallback)

    if (seriesData.close !== undefined) {
      return seriesData.close
    }

    if (seriesData.value !== undefined) {
      return seriesData.value
    }

    if (seriesData.middle !== undefined) {
      return seriesData.middle
    }

    if (seriesData.upper !== undefined && seriesData.lower !== undefined) {
      return (seriesData.upper + seriesData.lower) / 2
    }

    if (seriesData.high !== undefined) {
      return seriesData.high
    }

    return null
  }

  /**
   * Format value according to type and formatting options
   */
  private formatValue(value: unknown, key: string, formatting?: TemplateFormatting): string {
    if (value === null || value === undefined) {
      return ''
    }

    // Handle time formatting
    if (key === 'time') {
      return this.formatTime(value, formatting?.timeFormat)
    }

    // Handle numeric formatting
    if (typeof value === 'number') {
      return this.formatNumber(value, formatting?.valueFormat, formatting?.locale)
    }

    // Default to string conversion
    return value.toString()
  }

  /**
   * Format number according to format specification
   */
  private formatNumber(value: number, format?: string, locale?: string): string {
    if (!format) {
      return value.toFixed(2) // Default to 2 decimal places
    }

    // Parse format specification (e.g., '.2f', '.4f', etc.)
    const formatMatch = format.match(/\.(\d+)f/)
    if (formatMatch) {
      const decimals = parseInt(formatMatch[1])
      return value.toFixed(decimals)
    }

    // Handle locale-specific formatting if specified
    if (locale) {
      try {
        return value.toLocaleString(locale)
      } catch (error) {
        console.warn(`Invalid locale: ${locale}, falling back to default formatting`)
      }
    }

    return value.toFixed(2)
  }

  /**
   * Format time value
   */
  private formatTime(time: UTCTimestamp | string | number | unknown, format?: string): string {
    if (!time) return ''

    try {
      let date: Date

      // Convert time to Date object
      if (time instanceof Date) {
        date = time
      } else if (typeof time === 'number') {
        // Assume Unix timestamp (seconds or milliseconds)
        date = new Date(time > 1e10 ? time : time * 1000)
      } else if (typeof time === 'string') {
        date = new Date(time)
      } else {
        return time.toString()
      }

      // Apply format if specified
      if (format) {
        return this.formatDateWithCustomFormat(date, format)
      }

      // Default formatting
      return date.toLocaleString()
    } catch (error) {
      console.warn('Error formatting time:', error)
      return time.toString()
    }
  }

  /**
   * Format date with custom format string
   */
  private formatDateWithCustomFormat(date: Date, format: string): string {
    const formatMap: { [key: string]: string } = {
      'YYYY': date.getFullYear().toString(),
      'MM': (date.getMonth() + 1).toString().padStart(2, '0'),
      'DD': date.getDate().toString().padStart(2, '0'),
      'HH': date.getHours().toString().padStart(2, '0'),
      'mm': date.getMinutes().toString().padStart(2, '0'),
      'ss': date.getSeconds().toString().padStart(2, '0')
    }

    let result = format
    for (const [placeholder, value] of Object.entries(formatMap)) {
      result = result.replace(new RegExp(placeholder, 'g'), value)
    }

    return result
  }

  /**
   * Escape special regex characters
   */
  private escapeRegex(str: string): string {
    return str.replace(/[.*+?^${}()|[\]\\]/g, '\\$&')
  }

  /**
   * Escape HTML characters
   */
  private escapeHtml(str: string): string {
    const escapeMap: { [key: string]: string } = {
      '&': '&amp;',
      '<': '&lt;',
      '>': '&gt;',
      '"': '&quot;',
      "'": '&#39;'
    }

    return str.replace(/[&<>"']/g, (match) => escapeMap[match])
  }

  /**
   * Validate template syntax
   */
  public validateTemplate(template: string): { isValid: boolean; errors: string[] } {
    const errors: string[] = []

    try {
      // Check for malformed placeholders
      const placeholderRegex = /\$\$([a-zA-Z_][a-zA-Z0-9_]*)\$\$/g
      const invalidPlaceholderRegex = /\$\$[^$]*\$\$/g

      const validPlaceholders = [...template.matchAll(placeholderRegex)]
      const allDollarPairs = [...template.matchAll(invalidPlaceholderRegex)]

      if (allDollarPairs.length !== validPlaceholders.length) {
        errors.push('Template contains malformed placeholders')
      }

      // Check for unmatched $$ pairs
      const dollarCount = (template.match(/\$/g) || []).length
      if (dollarCount % 4 !== 0) {
        errors.push('Template contains unmatched $$ pairs')
      }

    } catch (error) {
      errors.push(`Template validation error: ${error}`)
    }

    return {
      isValid: errors.length === 0,
      errors
    }
  }

  /**
   * Get list of placeholders in template
   */
  public getPlaceholders(template: string): string[] {
    const placeholderRegex = /\$\$([a-zA-Z_][a-zA-Z0-9_]*)\$\$/g
    const matches = [...template.matchAll(placeholderRegex)]
    return matches.map(match => match[0])
  }

  /**
   * Create template context from series data
   */
  public createContextFromSeriesData(
    seriesData: SeriesDataValue,
    customData?: Record<string, unknown>,
    formatting?: TemplateContext['formatting']
  ): TemplateContext {
    return {
      seriesData,
      customData,
      formatting
    }
  }
}

import {Annotation, AnnotationLayer} from '../types'
import {UTCTimestamp, SeriesMarker, Time} from 'lightweight-charts'

export interface AnnotationVisualElements {
  markers: any[]
  shapes: any[]
  texts: any[]
}

export const createAnnotationVisualElements = (
  annotations: Annotation[]
): AnnotationVisualElements => {
  const markers: SeriesMarker<Time>[] = []
  const shapes: any[] = []
  const texts: any[] = []

  // Immediate return if annotations is null, undefined, or not an object
  if (!annotations || typeof annotations !== 'object') {
    return {markers, shapes, texts}
  }

  // Wrap the entire function in a try-catch to prevent any errors
  try {
    // Validate that annotations is an array
    if (!Array.isArray(annotations)) {

      return {markers, shapes, texts}
    }

    // Additional safety check - ensure annotations is actually an array
    try {
      if (typeof annotations.forEach !== 'function') {
        return {markers, shapes, texts}
      }
    } catch (error) {

      return {markers, shapes, texts}
    }

    // Convert to array if it's not already (defensive programming)
    let annotationsArray: Annotation[]
    try {
      annotationsArray = Array.from(annotations)
    } catch (error) {

      return {markers, shapes, texts}
    }

    // Final safety check
    if (!Array.isArray(annotationsArray) || typeof annotationsArray.forEach !== 'function') {
      return {markers, shapes, texts}
    }

    // Use try-catch around the entire forEach operation
    try {
      annotationsArray.forEach((annotation, index) => {
        try {
          // Validate annotation object
          if (!annotation || typeof annotation !== 'object') {

            return
          }

          // Create marker based on annotation type
          if (
            annotation.type === 'arrow' ||
            annotation.type === 'shape' ||
            annotation.type === 'circle'
          ) {
            const marker: SeriesMarker<Time> = {
              time: parseTime(annotation.time),
              position: annotation.position === 'above' ? 'aboveBar' : 'belowBar',
              color: annotation.color || '#2196F3',
              shape: annotation.type === 'arrow' ? 'arrowUp' : 'circle',
              text: annotation.text || '',
              size: annotation.fontSize || 1
            }
            markers.push(marker)
          }

          // Create shape if specified
          if (annotation.type === 'rectangle' || annotation.type === 'line') {
            const shape = {
              time: parseTime(annotation.time),
              price: annotation.price,
              type: annotation.type,
              color: annotation.color || '#2196F3',
              borderColor: annotation.borderColor || '#2196F3',
              borderWidth: annotation.borderWidth || 1,
              borderStyle: annotation.lineStyle || 'solid',
              size: annotation.fontSize || 1,
              text: annotation.text || ''
            }
            shapes.push(shape)
          }

          // Create text annotation if specified
          if (annotation.type === 'text') {
            const text = {
              time: parseTime(annotation.time),
              price: annotation.price,
              text: annotation.text,
              color: annotation.textColor || '#131722',
              backgroundColor: annotation.backgroundColor || 'rgba(255, 255, 255, 0.9)',
              fontSize: annotation.fontSize || 12,
              fontFamily: 'Arial',
              position: annotation.position === 'above' ? 'aboveBar' : 'belowBar'
            }
            texts.push(text)
          }
        } catch (error) {

        }
      })
    } catch (forEachError) {

    }
  } catch (outerError) {

  }

  return {markers, shapes, texts}
}

function parseTime(timeStr: string): UTCTimestamp {
  // Convert string time to UTC timestamp
  const date = new Date(timeStr)
  return Math.floor(date.getTime() / 1000) as UTCTimestamp
}

// Utility functions for annotation management
export function filterAnnotationsByTimeRange(
  annotations: Annotation[],
  startTime: string,
  endTime: string
): Annotation[] {
  const start = parseTime(startTime)
  const end = parseTime(endTime)

  return annotations.filter(annotation => {
    const time = parseTime(annotation.time)
    return time >= start && time <= end
  })
}

export function filterAnnotationsByPriceRange(
  annotations: Annotation[],
  minPrice: number,
  maxPrice: number
): Annotation[] {
  return annotations.filter(annotation => {
    return annotation.price >= minPrice && annotation.price <= maxPrice
  })
}

export function createAnnotationLayer(
  name: string,
  annotations: Annotation[] = []
): AnnotationLayer {
  return {
    name,
    annotations,
    visible: true,
    opacity: 1.0
  }
}

import {UTCTimestamp, SeriesMarker, Time} from 'lightweight-charts'
import {TradeConfig, TradeVisualizationOptions} from '../types'
import {UniversalSpacing} from '../primitives/PrimitiveDefaults'

// CRITICAL: Timezone-agnostic parsing functions
/**
 * Parse time value to UTC timestamp without timezone conversion
 * Handles both string dates and numeric timestamps
 */
function parseTime(time: string | number): UTCTimestamp | null {
  try {
    // If it's already a number (Unix timestamp), convert to seconds if needed
    if (typeof time === 'number') {
      // If timestamp is in milliseconds, convert to seconds
      if (time > 1000000000000) {
        return Math.floor(time / 1000) as UTCTimestamp
      }
      return Math.floor(time) as UTCTimestamp
    }

    // If it's a string, try to parse as date
    if (typeof time === 'string') {
      // First try to parse as Unix timestamp string
      const timestamp = parseInt(time, 10)
      if (!isNaN(timestamp)) {
        // It's a numeric string (Unix timestamp)
        if (timestamp > 1000000000000) {
          return Math.floor(timestamp / 1000) as UTCTimestamp
        }
        return Math.floor(timestamp) as UTCTimestamp
      }

      // Try to parse as ISO date string - CRITICAL: No timezone conversion
      if (time.includes('T') || time.includes('Z') || time.includes('+')) {
        // ISO format - parse directly to avoid local timezone conversion
        const date = new Date(time)
        if (isNaN(date.getTime())) {

          return null
        }
        // Use UTC timestamp directly - no timezone conversion
        return Math.floor(date.getTime() / 1000) as UTCTimestamp
      }

      // Regular date string parsing as fallback
      const date = new Date(time)
      if (isNaN(date.getTime())) {

        return null
      }
      return Math.floor(date.getTime() / 1000) as UTCTimestamp
    }

    return null
  } catch (error) {

    return null
  }
}

/**
 * Find nearest available timestamp in chart data
 */
function findNearestTime(targetTime: UTCTimestamp, chartData: any[]): UTCTimestamp | null {
  if (!chartData || chartData.length === 0) {
    return null
  }

  let nearestTime: UTCTimestamp | null = null
  let minDiff = Infinity

  for (const item of chartData) {
    if (!item.time) continue

    let itemTime: UTCTimestamp | null = null

    if (typeof item.time === 'number') {
      itemTime =
        item.time > 1000000000000
          ? (Math.floor(item.time / 1000) as UTCTimestamp)
          : (item.time as UTCTimestamp)
    } else if (typeof item.time === 'string') {
      itemTime = parseTime(item.time)
    }

    if (itemTime === null) continue

    const diff = Math.abs(itemTime - targetTime)
    if (diff < minDiff) {
      minDiff = diff
      nearestTime = itemTime
    }
  }

  return nearestTime
}

// Trade rectangle data interface (for data creation only)
export interface TradeRectangleData {
  time1: UTCTimestamp
  time2: UTCTimestamp
  price1: number
  price2: number
  fillColor: string
  borderColor: string
  borderWidth: number
  borderStyle: 'solid' | 'dashed' | 'dotted'
  opacity: number
  priceScaleId?: string
}

// Create trade rectangles from trade data
function createTradeRectangles(
  trades: TradeConfig[],
  options: TradeVisualizationOptions,
  chartData?: any[]
): TradeRectangleData[] {
  const rectangles: TradeRectangleData[] = []

  // Enhanced validation using coordinate service

  trades.forEach((trade, index) => {
    // Validate trade data - allow exitTime to be null for open trades
    if (
      !trade.entryTime ||
      typeof trade.entryPrice !== 'number' ||
      typeof trade.exitPrice !== 'number'
    ) {
      return
    }

    // Parse entry time
    const time1 = parseTime(trade.entryTime)
    if (time1 === null) {
      return
    }

    // Handle exit time - can be null for open trades
    let time2: UTCTimestamp | null = null
    if (trade.exitTime) {
      time2 = parseTime(trade.exitTime)
      if (time2 === null) {
        return
      }
    } else {
      // For open trades, use the last available time from chart data
      if (chartData && chartData.length > 0) {
        const lastTime = chartData[chartData.length - 1]?.time
        if (lastTime) {
          time2 = parseTime(lastTime)
        }
      }

      // If still no exit time, skip this trade
      if (time2 === null) {
        return
      }
    }

    // Find nearest available times in chart data if provided
    let adjustedTime1 = time1
    let adjustedTime2 = time2

    if (chartData && chartData.length > 0) {
      const nearestTime1 = findNearestTime(time1, chartData)
      const nearestTime2 = findNearestTime(time2, chartData)

      if (nearestTime1) adjustedTime1 = nearestTime1
      if (nearestTime2) adjustedTime2 = nearestTime2
    }

    // Validate prices
    if (trade.entryPrice <= 0 || trade.exitPrice <= 0) {
      return
    }

    const color = trade.isProfitable
      ? options.rectangleColorProfit || '#4CAF50'
      : options.rectangleColorLoss || '#F44336'

    const opacity = options.rectangleFillOpacity || 1.0

    const rectangle: TradeRectangleData = {
      time1: Math.min(adjustedTime1, adjustedTime2) as UTCTimestamp,
      price1: Math.min(trade.entryPrice, trade.exitPrice),
      time2: Math.max(adjustedTime1, adjustedTime2) as UTCTimestamp,
      price2: Math.max(trade.entryPrice, trade.exitPrice),
      fillColor: color,
      borderColor: color,
      borderWidth: options.rectangleBorderWidth || 3,
      borderStyle: 'solid' as const,
      opacity: opacity
    }

    rectangles.push(rectangle)
  })

  return rectangles
}

// Create trade markers
function createTradeMarkers(
  trades: TradeConfig[],
  options: TradeVisualizationOptions,
  chartData?: any[]
): SeriesMarker<Time>[] {
  const markers: SeriesMarker<Time>[] = []

  // Enhanced validation using coordinate service

  trades.forEach((trade, index) => {
    // Validate trade data - allow exitTime to be null for open trades
    if (
      !trade.entryTime ||
      typeof trade.entryPrice !== 'number' ||
      typeof trade.exitPrice !== 'number'
    ) {
      return
    }

    // Parse entry time
    const entryTime = parseTime(trade.entryTime)
    if (!entryTime) {
      return
    }

    // Handle exit time - can be null for open trades
    let exitTime: UTCTimestamp | null = null
    if (trade.exitTime) {
      exitTime = parseTime(trade.exitTime)
      if (!exitTime) {
        return
      }
    }

    // Find nearest available times in chart data if provided
    let adjustedEntryTime = entryTime
    let adjustedExitTime = exitTime

    if (chartData && chartData.length > 0) {
      const nearestEntryTime = findNearestTime(entryTime, chartData)
      if (nearestEntryTime) adjustedEntryTime = nearestEntryTime

      if (exitTime) {
        const nearestExitTime = findNearestTime(exitTime, chartData)
        if (nearestExitTime) adjustedExitTime = nearestExitTime
      }
    }

    // Entry marker
    const entryColor =
      trade.tradeType === 'long'
        ? options.entryMarkerColorLong || '#2196F3'
        : options.entryMarkerColorShort || '#FF9800'

    const entryMarker: SeriesMarker<Time> = {
      time: adjustedEntryTime,
      position: trade.tradeType === 'long' ? 'belowBar' : 'aboveBar',
      color: entryColor,
      shape: trade.tradeType === 'long' ? 'arrowUp' : 'arrowDown',
      text:
        options.showPnlInMarkers && trade.text
          ? trade.text
          : `Entry: $${trade.entryPrice.toFixed(2)}`
    }
    markers.push(entryMarker)

    // Exit marker - only create if trade has been closed
    if (adjustedExitTime) {
      const exitColor = trade.isProfitable
        ? options.exitMarkerColorProfit || '#4CAF50'
        : options.exitMarkerColorLoss || '#F44336'

      const exitMarker: SeriesMarker<Time> = {
        time: adjustedExitTime,
        position: trade.tradeType === 'long' ? 'aboveBar' : 'belowBar',
        color: exitColor,
        shape: trade.tradeType === 'long' ? 'arrowDown' : 'arrowUp',
        text:
          options.showPnlInMarkers && trade.text
            ? trade.text
            : `Exit: $${trade.exitPrice.toFixed(2)}`
      }
      markers.push(exitMarker)
    }
  })

  return markers
}

// Main function to create trade visual elements
export function createTradeVisualElements(
  trades: TradeConfig[],
  options: TradeVisualizationOptions,
  chartData?: any[],
  priceScaleId?: string
): {
  markers: SeriesMarker<Time>[]
  rectangles: TradeRectangleData[]
  annotations: any[]
} {
  const markers: SeriesMarker<Time>[] = []
  const rectangles: TradeRectangleData[] = []
  const annotations: any[] = []

  if (!trades || trades.length === 0) {
    return {markers, rectangles, annotations}
  }

  // Create markers if enabled
  if (options && (options.style === 'markers' || options.style === 'both')) {
    markers.push(...createTradeMarkers(trades, options, chartData))
  }

  // Create rectangles if enabled - these will be handled by RectanglePlugin
  if (options && (options.style === 'rectangles' || options.style === 'both')) {
    const newRectangles = createTradeRectangles(trades, options, chartData)
    rectangles.push(...newRectangles)
  }

  // Create annotations if enabled
  if (options.showAnnotations) {
    trades.forEach(trade => {
      const textParts: string[] = []

      if (options.showTradeId && trade.id) {
        textParts.push(`#${trade.id}`)
      }

      if (options.showTradeType) {
        textParts.push(trade.tradeType.toUpperCase())
      }

      if (options.showQuantity) {
        textParts.push(`Qty: ${trade.quantity}`)
      }

      if (trade.pnlPercentage !== undefined) {
        textParts.push(`P&L: ${trade.pnlPercentage.toFixed(1)}%`)
      }

      // Calculate midpoint for annotation position
      const entryTime = parseTime(trade.entryTime)
      const exitTime = parseTime(trade.exitTime)

      if (entryTime === null || exitTime === null) {
        return
      }

      const midTime = (entryTime + exitTime) / 2
      const midPrice = (trade.entryPrice + trade.exitPrice) / 2

      annotations.push({
        type: 'text',
        time: midTime,
        price: midPrice,
        text: textParts.join(' | '),
        fontSize: options.annotationFontSize || 12,
        backgroundColor: options.annotationBackground || 'rgba(255, 255, 255, 0.8)',
        color: '#000000',
        padding: UniversalSpacing.DEFAULT_PADDING
      })
    })
  }

  return {markers, rectangles, annotations}
}

/**
 * Convert trade rectangle data to RectanglePlugin format
 * This bridges the gap between trade data and the RectanglePlugin
 */
export function convertTradeRectanglesToPluginFormat(
  tradeRectangles: TradeRectangleData[],
  chart: any,
  series?: any
): any[] {
  if (!chart || !series) {
    return []
  }

  // Check if chart scales are ready
  const timeScale = chart.timeScale()
  const timeScaleWidth = timeScale.width()

  if (timeScaleWidth === 0) {
    return []
  }

  // Import ChartCoordinateService dynamically to avoid circular dependencies
  const {ChartCoordinateService} = require('../services/ChartCoordinateService')
  const coordinateService = ChartCoordinateService.getInstance()

  return tradeRectangles
    .map((rect, index) => {
      try {
        // Use ChartCoordinateService to calculate proper overlay position
        const boundingBox = coordinateService.calculateOverlayPosition(
          rect.time1,
          rect.time2,
          rect.price1,
          rect.price2,
          chart,
          series,
          0 // paneId
        )

        if (!boundingBox) {
          return null
        }

        const pluginRect = {
          id: `trade-rect-${index}`,
          x1: boundingBox.x,
          y1: boundingBox.y,
          x2: boundingBox.x + boundingBox.width,
          y2: boundingBox.y + boundingBox.height,
          color: rect.fillColor,
          borderColor: rect.borderColor,
          borderWidth: rect.borderWidth,
          fillOpacity: rect.opacity,
          borderOpacity: 1.0,
          label: `Trade ${index + 1}`,
          labelColor: '#000000',
          labelFontSize: 12,
          labelBackground: 'rgba(255, 255, 255, 0.8)',
          labelPadding: 4,
          zIndex: 10
        }

        return pluginRect
      } catch (error) {
        return null
      }
    })
    .filter(rect => rect !== null) // Remove null entries
}

