  crosshairMarkerBorderWidth?: number
  // Area-specific options
  topColor?: string
  bottomColor?: string
  invertFilledArea?: boolean
  // Histogram-specific options
  color?: string
  base?: number
}

/**
 * Chart layout configuration
 */
export interface ChartLayoutConfig {
  backgroundColor?: string
  textColor?: string
  fontSize?: number
  fontFamily?: string
  attributionLogo?: boolean
}

/**
 * Price scale configuration
 */
export interface PriceScaleConfig {
  position?: 'left' | 'right' | 'none'
  mode?: number
  autoScale?: boolean
  invertScale?: boolean
  alignLabels?: boolean
  borderVisible?: boolean
  borderColor?: string
  entireTextOnly?: boolean
  visible?: boolean
  ticksVisible?: boolean
  scaleMargins?: {
    top?: number
    bottom?: number
  }
}

/**
 * Time scale configuration
 */
export interface TimeScaleConfig {
  rightOffset?: number
  barSpacing?: number
  minBarSpacing?: number
  fixLeftEdge?: boolean
  fixRightEdge?: boolean
  lockVisibleTimeRangeOnResize?: boolean
  rightBarStaysOnScroll?: boolean
  borderVisible?: boolean
  borderColor?: string
  visible?: boolean
  timeVisible?: boolean
  secondsVisible?: boolean
  shiftVisibleRangeOnNewBar?: boolean
  tickMarkFormatter?: (time: UTCTimestamp, tickMarkType: number, locale: string) => string
}

// =============================================================================
// WINDOW EXTENSIONS
// =============================================================================

/**
 * Global window extensions for chart management
 */
declare global {
  interface Window {
    chartInstances?: Record<string, ExtendedChartApi>
    chartApiMap?: Record<string, ExtendedChartApi>
    chartGroupMap?: Record<string, number>
    seriesRefsMap?: Record<string, ExtendedSeriesApi[]>
    paneWrappers?: Record<string, Record<string, unknown>>
    paneButtonPanelWidgets?: Record<string, unknown[]>
    chartResizeObservers?: Record<string, ResizeObserver>
    paneLegendManagers?: Record<string, Record<number, unknown>>
    chartPlugins?: Map<string, unknown>
  }
}

// =============================================================================
// UTILITY TYPES
// =============================================================================

/**
 * Make all properties optional recursively
 */
export type DeepPartial<T> = {
  [P in keyof T]?: T[P] extends object ? DeepPartial<T[P]> : T[P]
}

/**
 * Extract function parameter types
 */
export type ParameterType<T> = T extends (...args: infer P) => any ? P : never

/**
 * Extract function return type
 */
export type ReturnType<T> = T extends (...args: any[]) => infer R ? R : never

/**
 * Non-nullable type utility
 */
export type NonNullable<T> = T extends null | undefined ? never : T

/**
 * Value of a record/object
 */
export type ValueOf<T> = T[keyof T]

/**
 * Layout and positioning types for the chart widget management system
 */

export type Corner = 'top-left' | 'top-right' | 'bottom-left' | 'bottom-right'

export interface Dimensions {
  width: number
  height: number
}

export interface AxisDimensions {
  priceScale: {
    left: {
      width: number // Left Y-axis width
      height: number
    }
    right: {
      width: number // Right Y-axis width
      height: number
    }
  }
  timeScale: {
    width: number
    height: number // X-axis height (bottom)
  }
}

export interface ChartLayoutDimensions {
  container: Dimensions
  axis: AxisDimensions
}

export interface Position {
  top?: number
  right?: number
  bottom?: number
  left?: number
  zIndex: number
}

export interface WidgetDimensions {
  width: number
  height: number
}

export interface IPositionableWidget {
  id: string
  corner: Corner
  priority: number
  visible: boolean
  getDimensions(): WidgetDimensions
  updatePosition(position: Position): void
}

export interface LayoutConfig {
  edgePadding: number // Distance from chart edges
  widgetGap: number   // Gap between stacked widgets
  baseZIndex: number  // Starting z-index for widgets
}

export interface CornerLayoutState {
  widgets: IPositionableWidget[]
  totalHeight: number
  totalWidth: number
}

export interface LayoutManagerEvents {
  onLayoutChanged: (corner: Corner, widgets: IPositionableWidget[]) => void
  onOverflow: (corner: Corner, overflowingWidgets: IPositionableWidget[]) => void
}

import {
  perfLogFn as perfLog,
  getCachedDOMElementForTesting as getCachedDOMElement,
  createOptimizedStyles
} from '../performance'

// Mock performance API
Object.defineProperty(window, 'performance', {
  value: {
    now: jest.fn(() => Date.now()),
    mark: jest.fn(),
    measure: jest.fn(),
    getEntriesByType: jest.fn(() => [])
  },
  writable: true
})

// Mock console methods
const originalConsole = {...console}
beforeEach(() => {



})

afterEach(() => {



})

describe('performance', () => {
  beforeEach(() => {
    jest.clearAllMocks()
  })

  describe('perfLog', () => {
    it('should log performance metrics when enabled', () => {
      const mockPerformance = {
        now: jest.fn(() => 1000),
        mark: jest.fn(),
        measure: jest.fn(),
        getEntriesByType: jest.fn(() => [])
      }
      Object.defineProperty(window, 'performance', {
        value: mockPerformance,
        writable: true
      })

      perfLog('test-operation', () => {
        // Simulate some work
        return 'result'
      })

      expect(mockPerformance.now).toHaveBeenCalled()

    })

    it('should handle errors gracefully', () => {
      const mockPerformance = {
        now: jest.fn(() => 1000),
        mark: jest.fn(),
        measure: jest.fn(),
        getEntriesByType: jest.fn(() => [])
      }
      Object.defineProperty(window, 'performance', {
        value: mockPerformance,
        writable: true
      })

      const errorFn = () => {
        throw new Error('Test error')
      }

      expect(() => {
        perfLog('error-operation', errorFn)
      }).toThrow('Test error')

      expect(mockPerformance.now).toHaveBeenCalled()
    })

    it('should work with async functions', async () => {
      const mockPerformance = {
        now: jest.fn(() => 1000),
        mark: jest.fn(),
        measure: jest.fn(),
        getEntriesByType: jest.fn(() => [])
      }
      Object.defineProperty(window, 'performance', {
        value: mockPerformance,
        writable: true
      })

      const asyncFn = async () => {
        await new Promise(resolve => setTimeout(resolve, 10))
        return 'async result'
      }

      const result = await perfLog('async-operation', asyncFn)

      expect(result).toBe('async result')
      expect(mockPerformance.now).toHaveBeenCalled()
    })

    it('should handle performance API not available', () => {
      Object.defineProperty(window, 'performance', {
        value: undefined,
        writable: true
      })

      const result = perfLog('no-performance', () => 'result')

      expect(result).toBe('result')

    })
  })

  describe('getCachedDOMElement', () => {
    it('should return cached element when available', () => {
      const mockElement = document.createElement('div')
      const cache = new Map()
      cache.set('test-id', mockElement)

      const result = getCachedDOMElement('test-id', cache, () => document.createElement('span'))

      expect(result).toBe(mockElement)
    })

    it('should create and cache new element when not available', () => {
      const cache = new Map()
      const createFn = jest.fn(() => document.createElement('div'))

      const result = getCachedDOMElement('new-id', cache, createFn)

      expect(result).toBeInstanceOf(HTMLDivElement)
      expect(createFn).toHaveBeenCalledWith('new-id')
      expect(cache.get('new-id')).toBe(result)
    })

    it('should handle null create function', () => {
      const cache = new Map()

      const result = getCachedDOMElement('test-id', cache, null)

      expect(result).toBeNull()
    })

    it('should handle create function returning null', () => {
      const cache = new Map()
      const createFn = jest.fn(() => null)

      const result = getCachedDOMElement('test-id', cache, createFn)

      expect(result).toBeNull()
      expect(createFn).toHaveBeenCalledWith('test-id')
    })

    it('should handle multiple calls with same ID', () => {
      const cache = new Map()
      const createFn = jest.fn(() => document.createElement('div'))

      const result1 = getCachedDOMElement('same-id', cache, createFn)
      const result2 = getCachedDOMElement('same-id', cache, createFn)

      expect(result1).toBe(result2)
      expect(createFn).toHaveBeenCalledTimes(1)
    })
  })

  describe('createOptimizedStyles', () => {
    it('should create optimized styles object', () => {
      const styles = createOptimizedStyles({
        width: '100%',
        height: '400px',
        backgroundColor: '#ffffff'
      })

      expect(styles).toEqual({
        width: '100%',
        height: '400px',
        backgroundColor: '#ffffff'
      })
    })

    it('should handle empty styles object', () => {
      const styles = createOptimizedStyles({})

      expect(styles).toEqual({})
    })

    it('should handle null styles', () => {
      const styles = createOptimizedStyles(null)

      expect(styles).toEqual({})
    })

    it('should handle undefined styles', () => {
      const styles = createOptimizedStyles(undefined)

      expect(styles).toEqual({})
    })

    it('should handle complex style objects', () => {
      const styles = createOptimizedStyles({
        width: '100%',
        height: '400px',
        backgroundColor: '#ffffff',
        border: '1px solid #ccc',
        borderRadius: '4px',
        padding: '10px',
        margin: '0',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center'
      })

      expect(styles).toEqual({
        width: '100%',
        height: '400px',
        backgroundColor: '#ffffff',
        border: '1px solid #ccc',
        borderRadius: '4px',
        padding: '10px',
        margin: '0',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center'
      })
    })

    it('should handle numeric values', () => {
      const styles = createOptimizedStyles({
        width: 800,
        height: 600,
        opacity: 0.8,
        zIndex: 1000
      })

      expect(styles).toEqual({
        width: 800,
        height: 600,
        opacity: 0.8,
        zIndex: 1000
      })
    })

    it('should handle mixed value types', () => {
      const styles = createOptimizedStyles({
        width: '100%',
        height: 400,
        opacity: 0.8,
        color: '#000000',
        fontSize: '14px',
        fontWeight: 500
      })

      expect(styles).toEqual({
        width: '100%',
        height: 400,
        opacity: 0.8,
        color: '#000000',
        fontSize: '14px',
        fontWeight: 500
      })
    })
  })
})

