/**
 * Preload services when user interaction is detected
 */
export function preloadOnInteraction(): void {
  const preloadAll = () => {
    ResourcePreloader.preload('coordinates', () =>
      import('../services/PaneCoordinatesService')
    )

    ResourcePreloader.preload('primitiveManager', () =>
      import('../services/ChartPrimitiveManager')
    )

    ResourcePreloader.preload('tooltipPlugin', () =>
      import('../plugins/chart/tooltipPlugin')
    )
  }

  // Preload on first user interaction
  const events = ['mousedown', 'touchstart', 'keydown']
  const handler = () => {
    preloadAll()
    events.forEach(event => {
      document.removeEventListener(event, handler, { capture: true })
    })
  }

  events.forEach(event => {
    document.addEventListener(event, handler, { capture: true, once: true })
  })
}

/**
 * Performance monitoring for lazy loading
 */
export const LazyLoadingMetrics = {
  loadTimes: new Map<string, number>(),

  startMeasure(key: string): void {
    this.loadTimes.set(`${key}_start`, performance.now())
  },

  endMeasure(key: string): number {
    const startTime = this.loadTimes.get(`${key}_start`)
    if (startTime) {
      const duration = performance.now() - startTime
      this.loadTimes.set(`${key}_duration`, duration)
      return duration
    }
    return 0
  },

  getMetrics(): Record<string, number> {
    const metrics: Record<string, number> = {}
    for (const [key, value] of this.loadTimes.entries()) {
      if (key.endsWith('_duration')) {
        metrics[key.replace('_duration', '')] = value
      }
    }
    return metrics
  }
}

/**
 * Centralized logging utility for the frontend application
 * Replaces direct console statements with structured logging
 */

export enum LogLevel {
  DEBUG = 0,
  INFO = 1,
  WARN = 2,
  ERROR = 3,
}

interface LogEntry {
  level: LogLevel;
  message: string;
  context?: string;
  data?: any;
  timestamp: Date;
}

class Logger {
  private isDevelopment: boolean;
  private logLevel: LogLevel;

  constructor() {
    this.isDevelopment = process.env.NODE_ENV === 'development';
    this.logLevel = this.isDevelopment ? LogLevel.DEBUG : LogLevel.WARN;
  }

  private shouldLog(level: LogLevel): boolean {
    return level >= this.logLevel;
  }

  private formatMessage(entry: LogEntry): string {
    const timestamp = entry.timestamp.toISOString();
    const levelName = LogLevel[entry.level];
    const context = entry.context ? `[${entry.context}] ` : '';
    return `${timestamp} ${levelName} ${context}${entry.message}`;
  }

  private log(level: LogLevel, message: string, context?: string, data?: any): void {
    if (!this.shouldLog(level)) return;

    const entry: LogEntry = {
      level,
      message,
      context,
      data,
      timestamp: new Date(),
    };

    const formattedMessage = this.formatMessage(entry);

    switch (level) {
      case LogLevel.DEBUG:
        console.debug(formattedMessage, data);
        break;
      case LogLevel.INFO:
        console.info(formattedMessage, data);
        break;
      case LogLevel.WARN:
        console.warn(formattedMessage, data);
        break;
      case LogLevel.ERROR:
        console.error(formattedMessage, data);
        break;
    }
  }

  debug(message: string, context?: string, data?: any): void {
    this.log(LogLevel.DEBUG, message, context, data);
  }

  info(message: string, context?: string, data?: any): void {
    this.log(LogLevel.INFO, message, context, data);
  }

  warn(message: string, context?: string, data?: any): void {
    this.log(LogLevel.WARN, message, context, data);
  }

  error(message: string, context?: string, data?: any): void {
    this.log(LogLevel.ERROR, message, context, data);
  }

  // Specialized methods for common contexts
  chartError(message: string, error?: Error): void {
    this.error(message, 'Chart', error);
  }

  primitiveError(message: string, primitiveId: string, error?: Error): void {
    this.error(message, `Primitive:${primitiveId}`, error);
  }

  performanceWarn(message: string, data?: any): void {
    this.warn(message, 'Performance', data);
  }

  renderDebug(message: string, componentName: string, data?: any): void {
    this.debug(message, `Render:${componentName}`, data);
  }
}

